css:
@-moz-document regexp(".*") {
html, body {
    color: rgb(216, 216, 216) !important;
    background: #272C2D !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
}

div, table {
    color: #efefef;
    background: #222 !important;
    /*
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    */
}

.content, .alt1, .alt2, a {
    color: #efefef;
    background: #222 !important;
}

b, a:link,
.vbmenu_option, .vbmenu_control,
.vbmenu_option a:link, .vbmenu_option vbmenu_option_alink,
.vbmenu_control a:link, .vbmenu_control_alink
{
    color: gray;
    background: #222 !important;
}

a:visited, body_avisited,
.vbmenu_option a:visited, .vbmenu_option vbmenu_option_avisited,
.vbmenu_control a:visited, .vbmenu_control_avisited {
    color: lightblue;
    background: #222 !important;
}

}
