css:
@-moz-document domain("vk.com") {
/* подсветка */
.nim-dialog_unread-out {
  background:#f5f7fa;
}

.im-page_classic .nim-dialog_unread-out {
  background:#f5f7fa;
  border-bottom:solid 1px #e7e8ec;
  margin-bottom:-1px;
}

.nim-dialog_unread {
  background:#f5f7fa;
}

.nim-dialog.nim-dialog_classic.nim-dialog_unread-out .nim-dialog--inner-text, 
.nim-dialog.nim-dialog_classic.nim-dialog_unread-out.nim-dialog_muted .nim-dialog--inner-text, 
.nim-dialog.nim-dialog_classic:not(.nim-dialog_deleted):hover.nim-dialog_unread-out .nim-dialog--inner-text,
.nim-dialog.nim-dialog_classic:not(.nim-dialog_deleted):hover.nim-dialog_unread-out.nim-dialog_muted .nim-dialog--inner-text {
    background: none;
}

/* квадратное */
.nim-peer .nim-peer--photo .im_grid>img,
.nim-peer .nim-peer--photo>img,
.nim-peer .nim-peer--photo-w,
.nim-dialog.nim-dialog_classic .nim-dialog--photo .nim-peer .nim-peer--photo .im_grid>img,
.nim-dialog.nim-dialog_classic .nim-dialog--photo .nim-peer .nim-peer--photo>img,
.module_body .people_cell_img,
.page_list_module .cell_img,
.page_list_module .thumb,
.post_img,
.post_field_user_image,
.wall_module .reply_img,
.chat_tab_img,
.top_profile_img,
.ow_ava.ow_ava_comm,
.like_tt_image,
.poll_tt_image,
.nim-dialog.nim-dialog_unread-out.nim-dialog_muted:not(.nim-dialog_failed) .nim-dialog--unread,
.nim-dialog.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread,
.nim-dialog.nim-dialog_unread.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread,
.friends_photo_img,
.right_list_img,
.friends_photo_wrap .ui_zoom_inner,
.group_row_photo, .group_row_img,
.im-prebody img {
  border-radius:2px;
}

/* реклама */
div.ads_ad_box, 
div#left_ads, 
div#ads_left, 
div.ads_ads_box, 
div.ads_ads_box2, 
div.ads_ads_box3, 
div#bFooter p {
    display: none !important;
}
}
