css:
@-moz-document url(https://donjon.bin.sh/) {

* {
	box-sizing: border-box!important;
}
body {
	background-color: #555;
	color: #ddd;
}
td.header {
	display: none;
}
#td-nav.nav {
	color: #ddd;
	background-color: #777;
	border: 1px solid #444;
	font-size: 1rem;
	width: 14vw;
	height: 100vh;
	padding: 0;
}
td.nav ul li {
	margin-bottom: .3rem;
	transition: .2s linear;
}
td.nav ul li a {
	color: #111;
}
td.nav ul li a:hover {
	color: #234;
}
td.nav ul li:hover {
	background-color: #888;
}
table.stats tr:hover td {
	background-color: transparent;
}
th {
	background-color: transparent;
}
table.stats tr:hover ol li:hover {
	background-color: rgba(0, 0, 0, .5);
}
textarea#list {
	background-color: #999;
	font-size: 1.1rem;
	line-height: 1.5rem;
	letter-spacing: .05rem;
	border: none;
}
#content.content {
	position: fixed;
	width: 86vw;
	height: 100vh;
	overflow-y: auto;
	color: #ddd;
	background-color: #444;
}
.footer::before {
	text-align: center;
	content: "donjon; donjon.bin.sh";
	display: inline-block;
	width: 100%;
	margin-top: 3px;
}
.footer {
	position: fixed;
	top: 0;
	right: 0;
	z-index: 1000;
	opacity: .4;
}
table table.center {
	width: 90%
}
#content.content a {
	color: #abc;
}
button,
input[type=submit],
input[type=button] {
	padding: 10px 20px;
	border: none;
	background-color: #ccc;
	transition: background-color .5s linear;
}
button,
input[type=submit],
input[type=button]:hover {
	background-color: #aaa;
}
  
}
