css:
@-moz-document domain(www.gp.se) {

.stampenCookieContainer {
    background: #00497c;
    color: #FFF;
    display: none;
}

.ad.panorama {
    max-width: 61.25rem;
    display: none;
}

.outsider .outsiderAds {
    display: none;
    position: absolute;
    right: -20px;
    z-index: 900;
}

.bottom-bar--sharing {
    width: 100%;
    border-top: 1px solid #e5e5e5;
    display: none;
}

.bottom-plate.slide-up .plate {
    visibility: visible;
    transition: transform ease-out .35s;
    display: none;
}

.ad * {
    vertical-align: bottom;
    display: none;
}

.strossle.container {
    max-width: 980px;
    display: none;
}

.main__footer__mediagroup {
    font-weight: 300;
    padding: 3.25rem 1.25rem 3rem 1.25rem;
    display: none;
}

.main__footer__buttons {
    padding: 2.5rem 1.25rem;
    display: none;
}

.main__footer {
    background: rgba(253,248,195,0);
    margin-top: 1rem;
}

.container .teaser-full.native {
    margin: 0 .625rem 1.25rem .625rem;
    padding: 0;
    display: none;
}

.ad.insider {
    float: left;
    margin-right: 1.5rem;
    margin-bottom: 1rem;
    margin-left: 0;
    display: none;
}
}
