css:
@-moz-document domain("disqus.com"), domain("webref.ru") {
/* COLOR */

main, body {
	background-color: #2d2d2d;
    color: #bcbcbc;
    font-family: Lato, PT Sans, Helvetica Neue, Arial !important;
}


/* DARK BACKGROUND */
pre,
.webrefExample-tabs {
	background: #1d1d1d;
}

.section-icon {
	background-color: #272727;
}

/* RESET BACKGROUND */
.site-sections,
.credit,
.discovery-post-header .title {
	background: none;
}

/* COMMON BACKGROUND */
[type=text], 
[type=password], 
[type=date], 
[type=datetime], 
[type=datetime-local], 
[type=month], 
[type=week], 
[type=email], 
[type=number], 
[type=search], 
[type=tel], 
[type=time], 
[type=url], 
[type=color], 
input[type=checkbox], 
textarea,
.note,
.reveal,
.site-sections,
.main-section .tabs,
.main-section .tabs-content {
	background-color: #2d2d2d;
}

/* ACCENT BACKGROUND */
.title,
[type=text]:focus, 
[type=password]:focus, 
[type=date]:focus, 
[type=datetime]:focus, 
[type=datetime-local]:focus, 
[type=month]:focus, 
[type=week]:focus, 
[type=email]:focus, 
[type=number]:focus, 
[type=search]:focus, 
[type=tel]:focus, 
[type=time]:focus, 
[type=url]:focus, 
[type=color]:focus, 
input[type=checkbox]:focus, 
textarea:focus,
.block .tag-item a,
#tag-alpha a,
#ac-ref,
.textarea-wrapper,
.post-actions,
.input--text,
#toc-html, .anchor-list, table,
table tbody, table tfoot, table thead,
.menuitem,
.tabs,
.tabs-content,
td.browser,
.br td,
.category-grid-img a,
.category-grid-level,
.adv_footer,
.pager li,
#search-button,
#book-navigation,
.book-toc,
kbd {
    background: none;
	background-color: #3d3d3d;
}

.play-advertise {
    background: #333842;
}

.category-grid-descr,
.about-author-social a, .social a {
	background-color: #333;
}

.page-frontpage .column:nth-child(1) .category-grid-img::before, .page-frontpage .column:nth-child(2) .category-grid-img::before, .page-frontpage .column:nth-child(3) .category-grid-img::before {
	background: #a62e1c;
    content: 'Новое';
}

.pager .pager-current,
.download-img {
	background-color: #4d4d4d;	
} 

/* PLAIN TEXT */ 
.view-content, 
p, 
iframe, 
.tab-conversation.active>a,
.discovery-col-header h2,
.meta li,
.user,
.webrefExample-tabs li,
table tfoot, table thead,
label,
[type=text], 
[type=password], 
[type=date], 
[type=datetime], 
[type=datetime-local], 
[type=month], 
[type=week], 
[type=email], 
[type=number], 
[type=search], 
[type=tel], 
[type=time], 
[type=url], 
[type=color], 
input[type=checkbox], 
textarea,
pre,
.webrefExample-tabs,
.credit {
	color: #bcbcbc !important;
}


/* HIGHLIGHTED TEXT */
h1, h2, h3, h4, h5, h6,
.discovery-col-header h2 strong,
#system-main h2.block_title,
.hint-ref,
[type=text]:focus, 
[type=password]:focus, 
[type=date]:focus, 
[type=datetime]:focus, 
[type=datetime-local]:focus, 
[type=month]:focus, 
[type=week]:focus, 
[type=email]:focus, 
[type=number]:focus, 
[type=search]:focus, 
[type=tel]:focus, 
[type=time]:focus, 
[type=url]:focus, 
[type=color]:focus, 
input[type=checkbox]:focus, 
textarea:focus,
#ac-ref,
.textarea-wrapper,
.input--text,
footer h4,
ul li i:before,
#search-button::after,
.recommend-title,
kbd,
#toc-html li::before, .anchor-list li::before, .book-toc .menu li::before {
	color: #cdcdcd !important;
}

.note {
    color: #ffae00 !important;
}

#system-main .recommend-text,
#result-fluidity div {
	color: #fff !important;
}

/* SLIDER */

.slick-dots li button:before {
	color: #9d9d9d;
}

.slick-dots li.slick-active button:before {
	color: #cdcdcd;
}

/* TITLE */ 

.title,
h2.block_title,
.attribute, 
pre .attribute, 
pre .variable {
	color: #ff4e33;
}

aside h2.block_title::after {
	background-color: #ff4e33;
}

/* LINK */
a {
	color: #e67e22;
}
footer a, 
header a,
aside .block a {
	color: #e67e22 !important;
}

a:hover, 
footer a:hover, 
header a:hover,
aside .block a:hover {
    color: #ef9a50 !important;
}

/* BREADCRUMB LINK */
.easy-breadcrumb_segment-title,
.css-col > li > a,
.css-col h2 {
	color: #b1cde4 !important;
}

.css-col > li > a:hover {
	color: #2e9fff !important;
}

/* BORDER */
[type=text], 
[type=password], 
[type=date], 
[type=datetime], 
[type=datetime-local], 
[type=month], 
[type=week], 
[type=email], 
[type=number], 
[type=search], 
[type=tel], 
[type=time], 
[type=url], 
[type=color], 
input[type=checkbox], 
textarea,
pre,
.webrefExample-tabs,
.pager li {
	border: 1px solid #3d3d3d;
}

ul.slick-dots {
	border-bottom: 1px solid #3d3d3d;
}

/* ACCENT BORDER */
#tag-filter,
#ac-ref,
aside .block,
.textarea-wrapper,
.input--text,
#toc-html, .anchor-list, table,
table tbody, table tfoot, table thead,
#search-button,
.about-author-social a, .social a,
kbd {
	border: 1px solid #4d4d4d;
}

.book-toc,
.about-author,
.book-navigation,
.disqus-footer__wrapper {
	border-color: #4d4d4d;
}

.section-icon {
	border-color: #7d7d7d;
}

.about-author-avatar {
	border-color: #adadad;
}

.block .tag-item a {
	border: 1px solid rgba(93, 93, 93, 0.4);
}

.input--text:focus {
	border: 1px solid #9d9d9d;
}

.post-actions {
    border-top: 2px solid #4d4d4d;
}

/* CLEAR BORDER */

.download-block,
.credit {
	border-color: transparent;
}


/* POSITIONING */
h1, h2, h3, h4, h5, h6 {
	margin-bottom: 0;
}

.breadcrumb {
    margin: 1rem 0 1rem;
}

#tag-filter {
    padding: 10px 18px 8px;
}

#tag-filter input {
    margin: 0 3px 3px 0;
    vertical-align: middle;
}

#tag-filter i::before, 
.tag-deprecated::after, 
.tag-nonstandart::after, 
li.tag-html5::after {
	margin-left: 3px;
}

#ac-ref {
    border-radius: 5px;
    box-shadow: none;
}

.cat_icon {
	display: none;
}

.recommend-text {
	padding: 5px 10px 2px;
}

.button {
    padding: .85em 1em 0.75em;
    margin: 0;
}

.slick-track {
	margin-bottom: 10px;
}

.view-display-id-recommend {
    margin-bottom: 4rem;
}

ul.slick-dots {
	padding-bottom: 10px;
}

.credit {
	opacity: 1;
}

/* TEXT */
.title h1 {
	text-transform: uppercase;
}

aside .block li {
	border-top: none;
    font-size: 16px;
}

/* TABLE */
table {
	border-collapse: collapse;
}

table tbody tr:nth-child(even) {
	background: #474747;
}

td, th {
	border: 1px solid #1d1d1d;
}

.tabs {
	border-bottom: 1px solid transparent;
}

.tabs-content {
	padding: 0;
}

td, th {
	border: 1px solid #1d1d1d;
}

.yes, .no, .warn {
	background-color: #4d4d4d;
}

.yes span::before {
	color: #b4c973;
}

.no span::before {
	color: #ff5a70;
}

.webrefExample-tabs .active,
.tabs-title>a[aria-selected=true] {
	background: #4d4d4d;
    color: #cdcdcd;
}

.tabs-title>a[aria-selected=true] {
	border-left: none;
    border-right: none;
}


/* SOURCE CODE */

pre .tag, 
pre .tag-name, 
.tag {
	color: #e8bf6a;
}

.attribute, 
pre .attribute, 
pre .variable,
pre .entity.class {
    color: #cc7833;
}

pre .operator {
    color: #e8bf6a;
}

pre .string, pre .support.value {
	color: #b4c973;
}

pre .hex-color {
	color: #6d9cbe;
}

.value, 
pre .css-value, 
pre .hex-color,
pre .source.css.embedded,
.ref dt, 
dl.param2 dt {
    color: #b4c973;
}

pre .keyword.unit {
	color: #fa6ea1;
}

pre .constant.numeric {
	color: #f173b9;
}

pre .css-property, 
pre .support.namespace, 
pre .vendor-prefix {
	color: #6c99bb;
}

.var {
	color: #c45837;
}

pre .entity.function, pre .entity.id {
    color: #c84646;
}

pre .entity.function, pre .entity.id {
    color: #c84646;
}
}
