/*LEFT MENU*/

.PTPAGELETHEADER {
    font-family: Lato, Arial, Helvetica;
    font-size: 14pt;
    color: #FFFFFF;
    background-color: #0074D9;
}
/*LEFT MENU LINKS*/

dl#pthdr2links dt a,
#ptnav2tree a {
    color: #0074D9;
    font-family: Lato;
    font-size: 14px;
}
/* TITLE BAR */

.PATRANSACTIONTITLE {
    font-family: "Didact Gothic", "Times New Roman", serif;
    font-size: 35px;
    font-weight: bold;
    color: #0074D9;
    letter-spacing: 2px;
    margin-bottom: 0px;
    padding-bottom: 0px;
    padding-right: 0px;
    margin-right: 0px;
    border-bottom-width: 5px;
    border-bottom-color: #0074D9;
}
/*ACADEMICS ETC.*/

.SSSGROUPBOXLEFTLABEL {
    font-family: Lato, Verdana, sans-serif;
    font-size: 17px;
    background-color: #0074D9;
    letter-spacing: .7px;
    border: none;
    width: 70%;
}
/*CONTACT INFO ETC.*/

.PSLEVEL2GRIDLABEL,
.PSLEVEL2GRIDLABEL:ACTIVE {
    font-family: Lato, Arial, sans-serif;
    font-size: 17px;
    color: #FFFFFF;
    background-color: #0074D9;
    letter-spacing: 1.5px;
}
/*LINKS UNDER ACADEMICS ETC.*/

.PSHYPERLINK,
.PSHYPERLINK:LINK,
.PSHYPERLINK:VISITED,
.PSSRCHRESULTSEVENROW:LINK,
.PSSRCHRESULTSEVENROW:VISITED,
.PSSRCHRESULTSODDROW:LINK,
.PSSRCHRESULTSODDROW:VISITED {
    font-family: Lato, Arial, sans-serif;
    font-size: 14px;
    color: #0074D9;
    text-decoration: none;
}
/*CONTACT SURROUNDING TABLE*/

.PSGROUPBOXWBO {
    border-style: none;
}
/* RIGHT SIDE LABELS*/

.SSSGROUPBOXRIGHTLABEL {
    font-family: Lato, Verdana, sans-serif;
    font-size: 15px;
    background-color: #0074D9;
    border: none;
}
/*FINANCES SUBHEADINGS*/

.PAGROUPBOXLABELLEVEL2 {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
}
/*RIGHT HAND SIDE TEXT IN BOX */

.PSEDITBOX_DISPONLY,
.PSLONGEDITBOXDISABLED {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
}
/*RIGHT SIDE TABLE*/

.SSSGROUPBOXRIGHTWBO,
.SSSGROUPBOXRIGHT {
    background-color: #F0F0F0;
    border: none
}
.PSLONGEDITBOX {
    font-family: Lato, sans-serif;
    font-size: 12px;
}
/*ADMISSION TABLE*/

.PSLEVEL1GRIDWBO {
    border: none;
}
/*APPLICATION TABLE COLUMNS*/

.PSLEVEL1GRIDCOLUMNHDR a:hover,
.PSLEVEL1GRIDCOLUMNHDR:LINK,
.PSLEVEL1GRIDCOLUMNHDR,
.PSLEVEL1GRIDCOLUMNHDR:HOVER {
    font-family: Lato, Arial, sans-serif;
    font-size: 12px;
    color: #FFFFFF;
    background-color: #0074D9;
}
/*FOOTER LINKS*/

.SSSFOOTERLINKBOLD {
    font-family: Lato, sans-serif;
    font-size: 12px;
    color: #0074D9;
}
.SSSFOOTERLINK {
    font-family: Lato, sans-serif;
    font-size: 12px;
    color: #0074D9;
    text-decoration: none;
}
/*SMALL PAGE TITLE*/

.PAPAGETITLE {
    font-family: "Didact Gothic", "Times New Roman", serif;
    font-size: 20px;
    color: #0074D9;
}
/*TAB TEXT*/

.SSSTABMAINTEXT,
.SSSTABMAINTEXT:HOVER {
    font-family: Lato, sans-serif;
    font-size: 15px;
    color: #0074D9;
    text-decoration: none;
    padding: 5px;
    text-align: center;
    display: inline;
}
.SSSTABACTIVE {
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    border: 3px solid #7FDBFF;
}
.SSSTABINACTIVE {
    background-color: #7FDBFF;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    border: 3px solid #7FDBFF;
}
/*NAME ABOVE TABS*/

.PALEVEL0PRIMARY {
    font-family: "Didact Gothic", Verdana, sans-serif;
    font-size: 20px;
    font-weight: bold;
    font-style: normal;
    color: #0074D9;
}
/*SELECT DISPLAY OPTION*/

.SSSTEXTTURQBOLD {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
    font-weight: bold;
    color: #0074D9;
    text-decoration: none;
}
/*RADIO BUTTON TEXT*/

.PSRADIOBUTTON {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
    color: #0074D9;
}
/*SCHEDULE TABLE TITLE*/

.HEADERLINK,
.PAGROUPDIVIDER {
    font-family: Lato, Verdana, Arial, sans-serif;
    font-size: 17px;
    font-weight: bold;
    font-style: normal;
    background: #0074D9;
    color: #FFFFFF;
    letter-spacing: 1px;
    text-indent: 1px;
    border: none;
}
.SSSPAGEKEYTEXT {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
}
.PSLEVEL3GRIDCOLUMNHDR,
.PSLEVEL3GRIDCOLUMNHDR:HOVER {
    font-family: Lato, Arial, sans-serif;
    font-size: 13px;
    color: #0074D9;
    background: #F0F0F0;
}
/*CALENDAR*/

.SSSTEXTWEEKLY {
    font-family: Lato, Arial, sans-serif;
    font-size: 13px;
    color: #FFFFFF;
    text-align: center;
    padding-top: 1px;
    padding-bottom: 1px;
    display: block;
    white-space: nowrap;
}
.SSSWEEKLYBACKGROUND {
    background: #0074D9;
    border-radius: 15px;
    border: 1px solid #FFFFFF;
}
/*CHANGE WEEK BUTONS*/

.SSSBUTTON_ACTIONLINK,
.SSSBUTTON_ACTIONLINK:HOVER {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
    color: #FFFFFF;
    background-color: #0074D9;
    border-radius: 15px;
    border: 1px solid #0074D9;
    padding-bottom: 3px;
}
.SSSGROUPBOXGREEN {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
    font-style: normal;
    color: #0074D9;
    background-color: rgb(255, 255, 255);
    border-top-color: #0074D9;
    border-bottom-color: #0074D9;
}
.PSEDITBOX {
    font-family: Lato, Arial, sans-serif;
    font-size: 12px;
}
/*REFRESH BUTTON*/

.SSSBUTTON_BLUELINK,
.SSSBUTTON_BLUELINK:HOVER {
    font-family: Lato, Arial, sans-serif;
    font-size: 15px;
    color: #FFFFFF;
    background-color: #0074D9;
    text-decoration: none;
    text-align: center;
    border-radius: 15px;
    border: 1px solid #0074D9;
    padding-bottom: 3px;
}
/*CALENDAR DAYS*/

.SSSWEEKLYA1BACKGROUND,
.SSSWEEKLYTIMEBACKGROUND,
.SSSWEEKLYDAYBACKGROUND,
.SSSTEXTWEEKLYTIME,
.SSSTEXTWEEKLYDAY {
    font-family: Lato, Arial, sans-serif;
    font-size: 13px;
    color: #0074D9;
    background-color: #F0F0F0;
}
/*Meeting info*/

.PSLEVEL1GRIDLABEL,
.PSGROUPBOXLABEL {
    font-family: Lato, Verdana, sans-serif;
    font-size: 17px;
    color: #FFFFFF;
    background-color: #0074D9;
    border: none;
}
.PSCHECKBOX {
    font-family: Lato, sans-serif;
    font-size: 14px;
    color: #0074D9;
}
.PSDROPDOWNLIST,
.PSTEXT {
    font-family: Lato, sans-serif;
    font-size: 14px;
}
h3 {
    font-family: Lato, sans-serif;
    font-size: 15px;
    font-weight: bold;
}
.PAPAGEINSTRUCTIONS {
    font-family: Lato, Arial, sans-serif;
    font-size: 14px;
}
/*ACCOUNT SUMMARY TABLE HEADER*/

.PAGRIDHEADER,
.PAGRIDHEADER:LINK {
    font-family: Lato, sans-serif;
    font-size: 13px;
    background-color: #0074D9;
    vertical-align: bottom;
    border-width: 1px;
    border: none;
}
/*ACCOUNT DETAIL*/

.PALEVEL0SECONDARY {
    font-family: Lato, Arial, sans-serif;
    font-size: 17px;
    color: #0074D9;
}
.STRONG {
    font-family: Lato, sans-serif;
}
/*CANCEL BUTTON*/

.SSSBUTTON_CANCELLINK,
.SSSBUTTON_CANCELLINK:HOVER {
    font-family: Lato, sans-serif;
    font-size: 14px;
    font-weight: normal;
    font-style: normal;
    font-variant: small-caps;
    color: #0074D9;
    background-color: #FFFFFF;
    border-radius: 15px;
    border: none;
}
/*NEXT BUTTON*/

.SSSBUTTON_CONFIRMLINK,
.SSSBUTTON_CONFIRMLINK:HOVER {
    font-family: Lato, sans-serif;
    font-size: 14px;
    font-weight: normal;
    font-style: normal;
    font-variant: small-caps;
    color: #FFFFFF;
    background-color: #0074D9;
    border-radius: 15px;
    border: none;
}
/*HIDE ROWS IN TABS TABLE*/

div#win0divDERIVED_SSTSNAV_SSTS_NAV_TABS div table tbody tr:nth-child(1),
div#win0divDERIVED_SSTSNAV_SSTS_NAV_TABS > div > table tbody > tr:nth-child(2) > td:nth-child(1):nth-child(3):nth-child(4):nth-child(6):nth-child(7):nth-child(9):nth-child(10):nth-child(12):nth-child(13),
