}
/*UW LOGO*/

#pthdr2logoswan {
    width: 1000px;
    height: 312px;
    background-size: 370px;
    background-image: url(https://ece.uwaterloo.ca/~achyu/pics/logos/UWlogo.jpg);
}
.PAADDITIONALINSTRUCTIONS,
