css:
@-moz-document url-prefix(https://calendar.google.com/) {


.chip dt {
    margin-top: 0;
}
/* Always hides the tasks sidebar */

#rhstogglecell {
    display: none;
}

#gadgetcell {
    display: none;
}

#maincell.has-sn-ex {
    margin-right: 0;
}

#mainbody {
    margin-left: 5px !important;
}

.st-c-pos {
    margin-top: 1px;
}

.wk-allday .st-bg-today {
    background: #fff;
}

.wk-dayname {
    border-width: 0;
    font-size: 18px;
}

.applogo {
    display: none;
}

.eui-s #gridcontainer {
    margin-left: 0;
}

.tc-icon-normal {
    margin-top: -1px;
}

#onegoogbar {
    display: none;
}

#mainnav {
    box-shadow: 0 5px 15px -7px #333;
    padding-bottom: 6px;
}

.eui-s #vr-nav {
    margin-top: 10px;
}

#vr-nav {
    margin-top: 10px;
    padding-bottom: 5px;
}

#nav {
    display: none;
}

#terms-and-privacy {
    display: none;
}

#topcontainerwk {
    padding-left: 57px;
}

.wk-tzlabel {
    display: none;
}

#weekViewAllDayBgwk td {
    border-width: 0;
}

.wk-daynames div {
    border-width: 0 !important;
}

#topLeftNavigation {
    left: 20px !important;
}

.date-nav-prev,
.date-nav-next {
    position: relative;
    top: -2px;
}

#mg-settings .goog-imageless-button-outer-box {
    height: 19px;
    top: 6px;
}

#gridcontainer {
    margin-right: 0 !important;
}

.st-c-pos span.evt-lk {
    font-size: 13px;
    margin-top: 2px;
    display: inline-block;
}

.mbox-cont {
    margin-top: 25px !important;
}

#calmaster {
    overflow: hidden !important;
}

.bubble.has-top-prong {
    margin-left: 0;
}

.tg-time-pri,
.tg-time-sec {
    font-size: 18px;
}

.date-top {
    color: #222;
    font-size: 18px;
    font-weight: normal;
}

.chip {
    margin-left: 6px;
    /*
    -webkit-box-shadow: 2px 2px 12px -1px rgba(51,51,51,0.41);
    -moz-box-shadow: 2px 2px 12px -1px rgba(51,51,51,0.41);
    box-shadow: 2px 2px 12px -1px rgba(51,51,51,0.41);*/
    
    -webkit-border-radius: 3px;
}

.mask-right {
    display: none;
}

.mask-left {
    display: none;
}

.chip dl {
    border-left: 0;
    border-right: 0;
    padding-top: 3px;
}

.cbrd {
    border: 0;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
}

.tg-dualmarker,
.tg-dualmarker60 {
    border-bottom: 0px dotted #ddd;
    border-top: 1px dotted #ddd;
}

.rb-n {
    border: 0 !important;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    padding-bottom: 2px !important;
    margin-left: 3px;
    margin-right: 2px;
    /*
    -webkit-box-shadow: 2px 2px 12px -1px rgba(51,51,51,0.41);
    -moz-box-shadow: 2px 2px 12px -1px rgba(51,51,51,0.41);
    box-shadow: 2px 2px 12px -1px rgba(51,51,51,0.41);*/
}

.rb-n,
.chip {
    -webkit-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.26);
    -moz-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.26);
    box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.26);
}
/*
#tgTable {
    position: relative;
    left: -1px;
}
*/

.tc-icon-darkcheck {
    position: relative;
    top: -1px;
}

.tg-col {
    border-left: 1px dotted #ddd;
}

#tgnowptr {
    display: none;
}

.cbrd dt,
.cbrd dd {
    position: relative;
    top: 1px;
    padding-top: 1px;
}

.cgd-col-last {
    display: none;
}

#tg-time-pri-last {
    display: none !important;
}

.crd-bottom {
    display: none;
}

.tg-mainwrapper {
    margin-top: 0 !important;
}

.cgd-gutter-bg-bottom {
    display: none;
}

.tg-spanningwrapper .tg-hourmarkers div:nth-child(18) {
    display: none;
}

.tg-time-pri,
.tg-time-sec {
    font-size: 18px;
    position: relative;
    top: 1.15px;
}

.crd-top {
    display: none;
}

.tg-time-pri {
    border: none;
}

td.tg-times-pri {
    border: none;
}

table#weekViewAllDayBgwk {
    border: none;
}

div#vr-nav {
    border-bottom: none;
}

div#topcontainerBorderwk {
    display: none;
}

.goog-imageless-button-collapse-right {
    border: none;
}

.goog-imageless-button,
.navbuttonouter {
    background: #fff;
    background-image: none;
    background-image: -moz-linear-gradient(top, #f5f5f5, #f1f1f1);
    background-image: -ms-linear-gradient(top, #f5f5f5, #f1f1f1);
    background-image: -o-linear-gradient(top, #f5f5f5, #f1f1f1);
    background-image: none;
    border: none;
    border-radius: 0;
}

.wk-dayname.wk-today {
    background: #fff;
}

.goog-imageless-button-checked {
    box-shadow: none;
    color: #222 !important;
}

.goog-imageless-button {
    text-transform: uppercase;
    color: #888;
    font-size: 12px;
}

.st-dtitle {
    line-height: 26px;
    font-size: 18px;
}

td.st-bg-td-first {
    background-color: #fff;
}

.crd-col.tg-times-pri {
    border-left: none;
    border-bottom: none;
}

#tgTable {
    border-bottom: none;
    border-right: none;
}

.goog-imageless-button-hover {
    box-shadow: none;
    color: #555 !important;
}

td.st-bg.st-bg-today {
    background-color: #f1f1f1;
    border: none;
}

td.st-dtitle.st-dtitle-today {
    border: none;
}

td.st-bg.st-bg-next {
    border-left: inherit;
}

td.st-dtitle.st-dtitle-next {
    border-left: inherit;
}

td.st-dtitle.st-dtitle-down {
    border-top: inherit;
}
    
}
