css:
@-moz-document domain("yuxiu.douyu.com") {
.show-layout-anchor {
    display: none;
}
.show-layout-stage {
    margin: 0 0 0 303px;
}
.layout-little .show-layout-stage {
    margin-left: 10px;
}
html {
    background: #ddd;
    background-size: cover;
}
.show-video-base {
    display: none;
}
.show-layout-gift-group {
    border-top: 1px solid #999;
    background: #fff;
}
.gift-group-list-item li {
    border: 1px solid #ccc;
    background-color: #fff;
}
.send-more-group {
    border-left: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
    background: #fff;
}
.send-more-arrow i {
    position: absolute;
    top: -6px;
    left: -10px;
    border-color: transparent transparent #999;
}
.gift-send-btn:hover {
    background: #666;
    color: #fff;
}
.gift-send-btn {
    background: #fff;
    color: #666;
    border: 1px solid #ccc;
}
.gift-group-list-box {
    position: relative;
    border: 1px solid #ccc;
}
.gift-type-nav .active,
.gift-type-nav a:hover {
    border-top: 3px solid #f00;
    color: #666;
}
.gift-type-nav a {
    color: #666;
}
.gift-actions-more .recharge {
    background-position: 5px -23px;
    color: #666 ! important;
}
.gift-actions-more .receive-btn {
    background-position: 5px 2px;
    color: #666 ! important;
}
.gift-description-cont {
    padding: 8px;
    border: 1px solid #999;
    border-radius: 2px;
    background-color: #fff;
}
.show-rank-box {
    background: #fe8b25 !important;
}
.rank-con-front ul li strong {
    padding: 0 10px;
    color: #333;
}
.show-chat-notice-box {
    background: #fe8b25 !important;
    color: #fff !important;
}
.show-chat-notice-box .show-chat-notice-box-left,
.show-chat-notice-box .show-chat-notice-box-inr,
.show-chat-notice-box .show-chat-notice-box-right {
    background: #fe8b25 !important;
}
.show-chat-group {
    background: #fff;
}
.show-chat-main {
    position: absolute;
    top: 155px;
    left: 0;
    right: 0;
    bottom: 0;
    border: 1px solid #999;
}
.rank-con-list {
    background-color: #fe8b25;
    color: #fff;
}
.show-chat-notice-box {
    background: #ccc;
    color: #fff !important;
}
.show-chat-notice-box strong {
    position: absolute;
    left: -31px;
    bottom: 14px;
    width: 18px;
    text-align: center;
    font-weight: 100;
    color: #fff;
}
.rank-con-front ul li em b,
.rank-con-list li b {
    color: #fff;
}
.layout-little .show-rank-nav ul li {
    width: 89px;
    background-position: -116px 0;
}
.show-rank-nav ul li {
    color: #333;
}
.c-list .jschartli {
    color: #000;
    font-size: 12px;
}
.c-list .name {
    font-size: 12px;
    color: #f00 !important;
}
.show-chat-bar {
    background: #fe8b25;
}
.show-chat-send-box {
    background: #fe8b25;
}
.show-chat-msg-box textarea {
    color: #fff !important;
}
.show-chat-announce-box p {
    background: #fff;
    color: #f00 !important;
    padding: 20px 20px 14px 22px;
    line-height: 22px;
    max-height: 107px;
    overflow: hidden;
}
.layout-little .show-rank-box {
    background: #fe8b25 !important;
}
.show-chat-main {
    position: absolute;
    top: 0;
}
#js-color-barrage .c-btn {
    color: #fff;
    line-height: 32px;
}
.show-chat-user-enter {
    overflow: hidden;
    white-space: nowrap;
    color: #333;
}
#js-chat-cont {
    height: 435px !important;
}
#douyu_show_room_flash_proxy {
    height: 82% !important;
}
}
