@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.nga.cn") {
  div,a,span,ul,p,body,button,#usernamebg{
    background: none ;
  }
  body{
    background-image: none;
    background-image: url(http://overwatch.nos.netease.com/1/images/bbs/background-overwatch-repeat.jpg) !important;
  }
  html body.haveCustomBackground div#mmc div#mc div#mainmenu.null div.stdbtn,
  html body.haveCustomBackground div#mmc div#mc div#mainmenu.null div.stdbtn div.innerbg,
  html body.haveCustomBackground div#mmc div#mc div#mainmenu.null div.stdbtn div.innerbg div div.td a,
  html body.haveCustomBackground div#mmc div#mc div#m_pbtntop.module_wrap div.w100 div.module_wrap_sub div.right_ table.stdbtn tbody tr td a.cell.rep.txtbtnx.nobr.silver,
  html body.haveCustomBackground div#mmc div#mc div#m_pbtntop.module_wrap div.w100 div.module_wrap_sub div#pagebtop.left table.stdbtn,
  #sub_forums div div,#sub_forums,
  html body.haveCustomBackground div#mmc div#mc div#m_cate5.module_wrap div.w100 div span div div.c.b2
  {
    background: none !important;
  }
  #sub_forums div{
    background-color: transparent !important;
  }
  a,h4,h3,h2,h1,b,
  span.urltip{
    color: 	rgb(0,180,255) !important;
  }
  .uitxt1:hover,
  .uitxt3:hover
  {
    color: 	rgb(255,199,14) !important;
  }
  html body.haveCustomBackground div.single_ttip2,
  html body.haveCustomBackground div#mmc div#mc span div div.w100 table.forumbox thead tr th
  {
    background: none !important;
    background-color: rgba(0,0,0,0.7) !important;
  }
  
  #topicrows tbody tr.row1.topicrow td,
  div#pagebbtm.left table.stdbtn tbody tr td a,
  html body.haveCustomBackground div#mmc div#mc div.module_wrap div.w100 div.module_wrap_sub div#pagebbtm.left table.stdbtn,
  html body.haveCustomBackground div#mmc div#mc div.module_wrap div.w100 div.module_wrap_sub div.right_ table.stdbtn tbody tr td a.cell.rep.txtbtnx.nobr.silver,
  html body.haveCustomBackground div#mmc div#mc div.module_wrap div.w100 div.module_wrap_sub div.right_ table.stdbtn,
  html body.haveCustomBackground div#mmc div#mc div#m_pbtntop.module_wrap div.w100 div.module_wrap_sub div#pagebtop.left table.stdbtn tbody tr td a,
  html body.haveCustomBackground div#mmc div#mc div#m_nav.module_wrap div.nav span.nav_spr,
  .stdbtn,
  .stdbtn tbody tr td a,
  .quote,
  .posterInfoLine,
  .block_txt.block_txt_c3,
  .block_txt.block_txt_c2,
  .catenew,
  .c.b4.invert.first,
  .c.b4,
  #loginform div,
  #loginform div a,
  #loginform div div input,
  #loginform div div select,
  div.div1 div.tip_title,
  div.single_ttip2 div.div1 div.div2,
  .colored_text_btn,
  div.div1 div.div2 div.div3 span input,
  html body.haveCustomBackground div#mmc div#mc span div div table.forumbox tbody tr.row1 td.c2,
  html body.haveCustomBackground div#mmc div#mc span div div table.forumbox tbody tr.row1 td.c2 input,
  html body.haveCustomBackground div#mmc div#mc span div div table.forumbox tbody tr.row1 td.c2 textarea
  {
    background: none !important;
    background-color: rgba(255,255,255,0.1) !important;
  }
  #topicrows tbody tr.row1.topicrow:hover,
  div#pagebbtm.left table.stdbtn tbody tr td a:hover,
  html body.haveCustomBackground div#mmc div#mc div.module_wrap div.w100 div.module_wrap_sub div.right_ table.stdbtn:hover,
  html body.haveCustomBackground div#mmc div#mc div#m_pbtntop.module_wrap div.w100 div.module_wrap_sub div#pagebtop.left table.stdbtn tbody tr td a:hover,
  html body.haveCustomBackground div#mmc div#mc div#m_nav.module_wrap div.nav span.nav_spr:hover,
  .stdbtn tbody tr td a:hover,
  .c.b4:hover,
  div.div1 div.div2 div.div3 span input:hover,
  .colored_text_btn:hover,
  html body.haveCustomBackground div#mmc div#mc span div div table.forumbox tbody tr.row1 td.c2 input:hover,
  html body.haveCustomBackground div#mmc div#mc span div div table.forumbox tbody tr.row1 td.c2 textarea:hover{
    background: none !important;
    background-color: rgba(255,255,255,0.2) !important;
  }
  
  #topicrows tbody tr.row2.topicrow td,
  .c.b3{
    background: none !important;
    background-color: rgba(255,255,255,0.2) !important;
  }
  #topicrows tbody tr.row2.topicrow:hover{
    background: none !important;
    background-color: rgba(255,255,255,0.2) !important;
  }
  html body.haveCustomBackground div#mmc div#mc div div.nav a.nav_link,
  html body.haveCustomBackground div#mmc div#mc div div.nav div.nav_root_c a.nav_root{
    background: none !important;
    background-color: rgba(255,255,255,0.2) !important;
  }
  html body.haveCustomBackground div#mmc div#mc div div.nav a.nav_link:hover,
  html body.haveCustomBackground div#mmc div#mc div div.nav div.nav_root_c a.nav_root:hover{
    background: none !important;
    background-color: rgba(0,0,0,0.7) !important;
  }
  html body.haveCustomBackground div#mmc div#mc div#m_posts.module_wrap div#m_posts_c.w100 table.forumbox.postbox tbody tr td{
    background: none !important;
  }
  html body.haveCustomBackground div#mmc div#mc div#m_posts.module_wrap div#m_posts_c.w100 table.forumbox.postbox tbody tr td span p,span,.block_txt.block_txt_c3,
  div.div1 div.div2 div.div3 span button
   {
    color: white;
  }
  
  
}



