css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
  #yt-masthead-container {
    background: rgba(19, 19, 19, 0.9);
    border-bottom-color: #131313;
  }
  
  #yt-masthead-content {
    max-width: 40%;
  }
  
  #yt-masthead .yt-masthead-logo-container {
    width: auto;
    margin-right: 20px;
  }
  
  #masthead-search #search-btn.search-btn-component {
    border-bottom-left-radius: 20px;
    border-top-left-radius: 20px;
    float: left;
  }
  #masthead-search-terms {
    border-bottom-right-radius: 20px;
    border-top-right-radius: 20px;
  }
  
  table.gssb_m {
    background: #1e1e1e;
    color: #fefefe;
  }
  
  td.gssb_a, td.gssb_a td {
    line-height: 40px;
  }
  
  tr.gssb_i, tr.gssb_i td {
    background: #3c3c3c;
  }
  
  tr.gssb_i b {
    font-weight: normal;
  }
  
  body  {
    background-color: #131313;
    color: #fefefe;
  }
  
  button, #watch7-sidebar .video-list-item a:hover .title, #watch7-sidebar .video-list-item a:hover .title .yt-deemphasized-text {
    color: #49a343;
  }
  
  #appbar-nav .appbar-nav-menu {
    font-variant-caps: all-petite-caps;
    letter-spacing: 0.3em;
    font-size: 20px;
    line-height: 30px;
    position: relative;
    /*top: -3px;
    border-bottom: 3px cyan solid;*/
  } 
  
  #masthead-appbar {
    background: rgba(19, 19, 19, 0.9);
    border-bottom: 0px rgba(19, 19, 19, 0.0) solid;
  }
  
  #appbar-nav .appbar-nav-menu span, 
  #appbar-nav .appbar-nav-menu h2 {
    font-size: 15px;
    text-indent: 0.3em;
  }
  
  .epic-nav-item-heading {
    color: #fff;
  }
  
  #appbar-content {
    position: relative;
    z-index: 1;
  }
  
  #appbar-nav>ul::after{
    display: block;
    position: relative;
    width: 100%;
    border-top: 2px #1e1e1e solid;
    content: "foo";
    left: 0;
    top: -2px;
    height: 0px;
    vertical-align: bottom;
    z-index: -1;
  }
  
  a.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
    border-color: #56c44e;
    border-width: 2px;
  }
  
  a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
    border-color: #56c44e;
    border-width: 2px;
  }
  
  #appbar-guide-menu, .guide-flyout {
    background: #1e1e1e;
    color: #8f8f8f;
  }
  
  .guide-item {
    color: #8f8f8f;
  }
  
  .guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background: #56c44e;
  }
  
  li.guide-section h3 a {
    color: #56c44e;
  }
  
  .guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: none;
  }
  
  .guide-section-separator {
    border-color: #131313;
  }
  
  .flex-width-enabled.flex-width-enabled-snap .content-alignment, .content-snap-width-1 .flex-width-enabled.flex-width-enabled-snap .content-alignment {
    width: 920px;
  }
  
  #browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
    border-color: #1e1e1e;
  }
  
  .yt-card {
    background: transparent;
  }
  
  .yt-shelf-grid-item {
    height: 240px;
    margin-right: 20px;
    width: 203px;
  }
  
  .shelf-title-cell {
    border-bottom: 2px #1e1e1e solid;
    padding-bottom: 2px;
  }
  
  .shelf-item .shelf-title-table {
    padding-top: 20px;
    padding-bottom: 20px;
  }
  
  .shelf-item .shelf-title-table .shelf-title-row .menu-container {
    top: 0;
  }
  
  .shelf-title-row {
    padding-bottom: 2px;
    margin-bottom: 26px;
  }
  
  .yt-lockup {
    background: #1e1e1e;
    height: 100%;
  }
  
  .yt-lockup .yt-lockup-content {
    height: 100%;
    padding: 7px;
  }
  
  .yt-lockup-grid .yt-lockup-thumbnail {
    margin-bottom: 1em;
    height: auto;
  }
  
  .yt-lockup-grid .yt-lockup-title {
    margin-bottom: 1em;
  }
  
  .yt-lockup-grid .yt-lockup-meta {
    margin-top: 2em;
  }
  
  .yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
    color: #fff;
  }
  
  .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
    text-decoration: underline #fff;
  }
  .yt-lockup:hover .yt-lockup-byline:not(:hover) a {
    text-decoration: none;
  }
  
  .branded-page-module-title-text {
    color: #fff;
    font-size: 20px;
  }
  
  
  .yt-ui-ellipsis {
    background-color: transparent;
  }
  
  
  
  .video-list-item {
    background: #1e1e1e;
  }
  
  .comment-simplebox-renderer-collapsed-content, #watch8-action-buttons,
  .yt-card .yt-uix-button-expander,
  #watch7-sidebar .watch-sidebar-separation-line,
  .comment-simplebox-renderer{
    border-color: #1e1e1e;
  }
  
  .like-button-renderer-like-button.yt-uix-button.yt-uix-button-toggled::before {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
  .yt-uix-button-has-icon:before {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
#yt-masthead-user .yt-uix-button-icon-bell {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
    .yt-sprite:not(.footer-logo-icon):not(.logo):not(.share-service-icon) {
        -webkit-filter: invert(100%);
  filter: invert(100%);
}
  
  .yt-card .yt-uix-tabs .yt-uix-button:hover, .yt-card .yt-uix-tabs .yt-uix-button:active, .yt-card .yt-uix-tabs .yt-uix-button.yt-uix-button-active, .yt-card .yt-uix-tabs .yt-uix-button.yt-uix-button-toggled {
    border-color: #56c44e;
  }
  
  .yt-card .yt-uix-tabs {
    border-color: #1e1e1e;
  }
  
  #watch7-sidebar .video-list-item:hover .title, #watch7-sidebar .video-list-item:hover .title .yt-deemphasized-text {
    color: #56c44e;
  }
  
  .yt-uix-button-text, .yt-uix-button-text[disabled] {
    color: #fefefe;
  }
  
  /* scrubber button */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover, .ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
   background: #56c44e !important;
}
/* progress bar */
.html5-play-progress, .ytp-play-progress {
   background: #56c44e !important; 
}
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
   background: #56c44e !important;
   z-index: -117;
}
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
   background-color: #56c44e !important;
}
.ytp-swatch-color {
   color: #56c44e !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
   background: #56c44e !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: #56c44e !important;
}
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg, .ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg {
   fill: #56c44e !important;
}
  
  
  .channel-header .branded-page-header-title .branded-page-header-title-link {
    color: #fefefe;
  }
  
  .yt-uix-expander-ellipsis {
    background-color: transparent;
    color: #fefefe;
  }
  
  
  
  /* Begin Player Screen */
  
  a, .video-player-view-component .video-detail a, #watch-header .yt-user-info a {
    color: #56c44e;
  }
  
  /* End Player Screen */
  
  /* Begin Channel Screen */
  
  
  
  .branded-page-v2-primary-col .branded-page-box.video-player-view-component:last-child, .branded-page-v2-primary-col .branded-page-box.c4-featured-content:last-child, .branded-page-v2-primary-col .branded-page-box.c4-featured-content-editor:last-child, .branded-page-v2-primary-col .branded-page-box.welcome:last-child,
  #c4-header-bg-container{
    border-color: #1e1e1e;
  }
  
  .yt-uix-button-subscribe-unbranded, .yt-uix-button-subscribe-unbranded[disabled], .yt-uix-button-subscribe-unbranded[disabled]:hover, .yt-uix-button-subscribe-unbranded[disabled]:active, .yt-uix-button-subscribe-unbranded[disabled]:focus {
    background-color: #131313;
    border-color: #131313;
    color: #555;
  }
  
  /* Page Progress bar */
  #progress {
    background: #56c44e;
  }
  
  /* Rounded badges */
  
  .yt-badge {
    border-radius: 20px;
    color: #fefefe;
    padding: 4px 24px;
  }
  
  .yt-uix-button-subscribe-unbranded, .yt-uix-button-subscribe-unbranded[disabled], .yt-uix-button-subscribe-unbranded[disabled]:hover, .yt-uix-button-subscribe-unbranded[disabled]:active, .yt-uix-button-subscribe-unbranded[disabled]:focus, .yt-uix-button.yt-uix-button-subscribe-unbranded, .yt-uix-button.yt-uix-button-subscribed-unbranded {
    background-color: transparent;
    border-radius: 20px;
    color: #fefefe;
    /*padding: 4px 24px;*/
    border-color: #ddd;
    text-transform: uppercase;
    /*padding: 4px 24px 4px 24px;*/
    padding: 0 0 0 0;
    height: 23px;
    font-weight: 400;
    line-height: 13px;
  }
  
  .yt-uix-button.yt-uix-button-subscribed-unbranded {
    color: #56c44e;
  }
  
  .subscribed-label {
    vertical-align: middle;
  }
  
  button.yt-uix-subscription-button .yt-uix-button-content>* {
    height: 13px;
    padding: 4px 24px 4px 24px;
  }
  
  button.yt-uix-subscription-button:hover .yt-uix-button-content .subscribe-label {
    color: #56c44e;
  }
  
  button.yt-uix-subscription-button {
    width: 129px;
    margin-left: 8px;
    margin-right: 8px;
  }
  
  button.yt-uix-subscription-button:hover {
    border-width: 2px;
  }
  
  .yt-lockup:hover .yt-uix-button-action-menu {
    border: 0px solid #858585;
  }
  
  #results .yt-lockup {
    margin-bottom: 20px;
  }
  #results .yt-lockup .yt-lockup-content {
    padding-top: 0;
    padding-bottom: 0;
  }
  #results .yt-lockup.yt-lockup-video h3.yt-lockup-title::before {
    display: inline-block;
    height: 1em;
    content: "VIDEO";
    font-weight: 400;
    font-size: 0.7em;
    vertical-align: text-top;
  }
  
  #results .yt-lockup.yt-lockup-channel h3.yt-lockup-title::before {
    display: inline-block;
    height: 1em;
    content: "CHANNEL";
    font-weight: 400;
    font-size: 0.7em;
    vertical-align: text-top;
  }
  
  #results .yt-lockup.yt-lockup-channel .yt-thumb {
    border-radius: 100%;
    background-color: transparent;
  }
  
  #results .yt-lockup.yt-lockup-channel .yt-lockup-thumbnail {
    width: unset;
  }
  
  .yt-thumb {
    background: transparent;
  }
  
  
}
