css:
@-moz-document domain("theloftlounge.ca") {
#sidebar-holder, #content-holder {
	position: static!important;
	height: auto!important;
}

#content {
	position: static!important;
	margin-left: 290px;
	width: auto;
}

#page-content {
	position: static!important;
}

#products-grid.border-true.large {
	height: auto!important;
	padding: 0!important;
}

#content-holder {
	width: 100%!important;
	float: right;
}

@media (max-width: 900px), (max-device-width: 1024px) {
	#content {
		margin-left: 0px;
	}
}
}
