@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.dragonnest.com") {
  
  /* This is totally done in about an hour of work and horribly organized but hey it works */
 
  #widget_2 .main-title::after {
    content: "\AVahr's Dark Theme v0.3.0.092520161604";
    white-space: pre;
    font-size: 10px;
    color: #707070;
    font-family: Arial;
  }
  
  /* Signature limits */
  .post-signature span[style] {
    font-size: 12px !important;
  }
  
  .post-signature img {
    max-height: 150px;
  }
  
  .post-signature {
    max-height: 225px;
    overflow-x: auto;
  }
  
  #vb-page-body {
    background-color: #101010;
    background-image: none;
  }
  
  #header {
    background-color: #EEE;
  }
  
  .view-mode .widget-tabs .widget-tabs-nav .module-title,
  .topic-list-container .topic-list-header, 
  .conversation-toolbar-wrapper .conversation-toolbar, 
  .view-mode .widget-tabs .widget-tabs-nav,
  .widget-no-border > div.widget-content,
  .private-message-search,
  .forum-list-container .forum-list-header {
    background-image: none;
    background-color: #202020 !important;
  }
  
  
  .widget-tabs .widget-tabs-nav li {
    background-color: #202020 !important;
  }
  
  .widget-tabs .widget-tabs-nav li a{
    color: #808080 !important;
  }
  
  .widget-tabs .widget-tabs-nav li.ui-tabs-active {
    background-color: #02151D !important;
  }
  
  .widget-tabs .widget-tabs-nav li.ui-tabs-active a {
    color: #EEE !important;
  }
  
  .category-header > td:nth-child(1) {
    background-color: #101420;
  }
  
  
  .forum-list-header,
  .folder-list .folder-item .folder-link span,
  #privateMessageContainer .main-pane .main-pane-container .main-pane-header,
  .forum-list-container .category-header .category {
    color: #ACE !important;
  }
  
  #channel-subtabbar .channel-subtabbar-list li a,
  #channel-subtabbar {
    background-color: #02151D;
    color: #EEE !important;
  }
  
  #breadcrumbs,
  .topic-list-container .topic-list,
  .list-footer,
  .b-toolbar,
  #topic-tab,
  #thread-view-tab,
  li.b-post > div > div > div,
  #profileTabs.profileTabs .post-content,
  li.b-post,
  .profile_sidebar_content,
  #profileFilterContainer,
  .conversation-empty,
  .message-empty,
  .b-button-group,
  .folder-list,
  .blockbody.widget-tabs-panel,
  .folder-item.special-folder.inbox a,
  .media-tab,
  #privateMessageContainer .main-pane .main-pane-container .main-pane-header,
  .count,
  .b-tabbed-pane__pane.b-tabbed-pane__pane--selected,
  .list-item.list-item-body.message-item,
  .list-item,.message-item.message-header,
  .b-comment-entry,
  #content {
    background-color: #202020 !important;
  }
  
  .topic-list-container .topic-list .topic-item.sticky {
    background-color: #202C40 !important;
  }
  
  .folder-link.selected {
    background-color: #303030 !important;
  }
  
  .folder-link .label.ellipsis:hovered {
    color: black;
  }
  
  li.b-post > div,
  .b-comment-entry__textbox,
  .bbcode_quote{
    background-color: #2C2C2C !important;
  }
  
  .module-title h1,
  .folder-list,
  ::-moz-placeholder,
  .page-title-widget .module-title, .page-title-widget .module-title h1,
  .profile-sidebar-widget .profile-menulist .profile-menulist-item > a > label {
    color: #ACE;
  }
  
  .topics-count,
  .posts-count,
  .lastpost-by,
  .widget-content,
  .usertitle,
  .b-userinfo__additional-info-block,
  .toolset-right,
  .topic-list-header,
  #profileTabs.profileTabs .post-content,
  #profileTabs.profileTabs .post-header,
  #breadcrumbs .crumb a,
  li.b-post-sm > .b-post-sm__content > .b-post-sm__post-content,
  .post-content,
  .forum-desc {
    color: #EEE !important;
  }
}
