css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
  body {
    background-image: url(http://overwatch.nos.netease.com/1/images/bbs/background-overwatch-repeat.jpg) !important;
  }
  
  /*é¡¶éƒ¨*/
  #main-header{
    background: none;
    background-color: rgba(255,255,255,0.2) !important;
    box-shadow: 0px 0px 3px #ffffff;
    color : rgb(255,199,14);
  }
  #main-header a{
    color: #00b4ff;
  }
  
  /*å·¦éƒ¨*/
  #left-sidebar nav,#left-sidebar footer{
   background: none;
    background-color: rgba(255,255,255,0.2) !important;
    box-shadow: 0px 0px 3px #ffffff;
  }
  #left-sidebar nav ul li a,
  #left-sidebar footer ul li a{
    color: #00b4ff;
    transition:color 0.5s;
  }
  #left-sidebar nav ul li a:hover,
  #left-sidebar footer ul li a:hover{
   color :white;
  }
  #search-terms,#search-submit{
    background: none;
    background-color: rgba(0,0,0,0.2) !important;
    color : rgb(255,199,14);
  }
  /*ä¸­éƒ¨å†…å®¹åŒº*/
  .style-brief,#discussions-area,#style-info,#view-code,
  html body div#content-wrapper article#main-article ul,
  .author-styles,.form-controls,
  #main-article p,
  html body div#content-wrapper article#main-article div#login form#password-login input{
    background-color: rgba(255,255,255,0.2);
    box-shadow: 0px 0px 3px #ffffff;
    transition:background-color 1s;
  }
  #long-description,#additional-info-text,h2{
    color : rgb(255,199,14);
  }
  .form-controls input,.form-controls textarea{
    background: none;
  }
  
  #style-info{
    margin-bottom: 20px;
  }
  .style-brief-text header a{
    color: #00b4ff;
  }
  .style-brief:hover{
    background-color: rgba(255,255,255,0.4);
  }
  #discussions li a,#no-discussions a,
  #style-author-info tbody tr td a,
  #show-button,
  #main-article ul li a,
  #main-article table.author-styles tbody tr td a,
  .form-controls span a,a,
  #main-article form div.form-controls div a,
  #main-article p a{
    color: #00b4ff;
    transition:color 0.5s;
  }
  #discussions li a:hover,
  #no-discussions a:hover,
  #show-button:hover,
  #main-article ul li a:hover,
  #main-article table.author-styles tbody tr td a:hover,
  .form-controls span a:hover,
  a:hover,
  #main-article form div.form-controls div a:hover,
  #main-article p a:hover,
  html body div#content-wrapper article#main-article div#login form#password-login label,
  html body div#content-wrapper article#main-article div#login form#password-login input,
  #view-code,#css{
    color:white;
  }
  html body div#content-wrapper article#main-article table.author-styles tbody tr:hover{
    background-color: rgba(255,255,255,0.1);
  }
  .style-warning{
    background: none;
    background-color: rgba(0,0,0,0.7);
    color :gray;
  }
  
  /*åº•éƒ¨é¡µç æ */
  .pagination{
    background-color: rgba(255,255,255,0.2) !important;
    box-shadow: 0px 0px 3px #ffffff;
    height: 40px;
  }
  .pagination span,.pagination em{
    height: 40px;
    font-size: 30px;
  }
  .pagination a{
    font-size: 30px;
    color : rgb(255,199,14);
    text-decoration:none;
    border-style: solid solid solid solid;
    border-width: 0px 0px 0px 0px;
    padding-left: 3px;
    padding-right: 3px;
    /*border-color:rgba(255,255,255,0.7);*/
    box-shadow: 0px 0px 3px #ffffff;
  }
  
  
}
