css:
@-moz-document domain("virustotal.com") {
body,
.footer,
.form-actions {
    background: #333030 !important;
}
.comment-text,
.well,
.dropdown-menu,
#antivirus-results > tbody > tr:nth-child(2n+1) > td,
#comment-signin {
    background: #424242 !important;
}
#dlg-signin,
.alert,
.modal-body,
.modal-header,
.modal-footer {
    background: #616161 !important;
}
#logo.center {
    padding-right: 16px !important;
}
img[alt="VirusTotal"] {
    display: none;
}
img#google-o-meter {
    display: none;
}
/* Texts */

a:hover,
#message {
    color: #FFF !important;
}
a,
strong,
h3,
h1,
.action,
.control-label,
.header {
    color: #DDD !important;
}
td,
dt,
address {
    color: #BBB !important;
}
.text-gray,
span,
.comment-text,
p,
dd,
li {
    color: #AAA !important;
}
.important {
    color: #333030 !important;
}
.enum {
    color: #AAA;
}
/* Tab lists */

.navbar-inner {
    background: #424242 !important;
}
li,
li > a {
    transition-duration: .25s !important;
}
li.active {
    background: #616161 !important;
}
li:hover {
    background: #0F75BC !important;
}
li > a {
    color: #DDD !important;
    text-shadow: none !important;
    background: none !important;
}
li > a:hover {
    color: #FFF !important;
}
.dropdown-menu > li:hover,
.dropdown-menu > li:hover {
    background: #424242 !important;
}
.text-red {
    color: #c52525 !important;
}
.action,
#btn-more-reputation,
#btn-sign-in,
.btn.cancel,
.btn {
    background: #0F75BC !important;
    border-radius: 0px !important;
}
.action:hover,
#btn-more-reputation:hover,
#btn-sign-in:hover,
.btn.cancel:hover,
.btn:hover {
    background: #094670 !important;
}
.action {
    padding-right: 1px !important;
}
div.file-chooser,
div.url-chooser {
    background: #FFF !important;
}
div.file-chooser input,
div.url-chooser input {
    border-radius: 0;
}
#file div.text-grey {
    margin-top: 10px;
}
#votes-resume > .popover-spot {
    width: 100% !important;
}
img#google-o-meter {
    width: 100%;
}
a[tab="comments"] .badge-info {
    background-color: #ffffff;
}
/* No boarders and hyperlinks underline */

* {
    border: none !important;
    text-decoration: none !important;
    text-shadow: none !important;
}
.popover,
.fade,
.bottom,
.in,
.popover-title {
    background: #333;
}
.popover.bottom .arrow:after {
    border-bottom-color: #444;
}
.defaultSkin td.mceToolbar {
    background: #555;
}
.extra-info h5 {
    color: #bbb;
    background: #333;
}
div.file-chooser, div.url-chooser {
    background: #333 !important;
}
.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
    background-color: #444;
}
}
