css:
@-moz-document domain("www.androidpolice.com") {
.band.content.main {
    background: #0d0d0d !important;
}

.band.sub-nav {
    background: #131313 !important;
    border-bottom: 1px solid #000000 !important;
}

.band.header {
    background: url(http://i.imgur.com/LkWZUcn.png) #000 !important;
}

.ap-nav-wrapper {
    background: #212121 !important;
}

.post {
    background: #131313;
    border: 1px solid #000000 !important;
    box-shadow: 0 1px 0 0 #000000 !important;
}

.primary .post-footer-inner {
    background: #131313 !important;
}

a {
    color: #7d7d7d !important;
}

body {
    color: #7d7d7d !important;
    background: #000000 !important;
}

.widget section {
    background: #131313 !important;
    border: 1px solid #000000 !important;
}

.note-note {
    border-color: #000000 !important;
    background-color: #0d0d0d !important;
}

.note-warning {
    border-color: #000000 !important;
    background-color: #0d0d0d !important;
}

.card-action {
    border-top: 1px solid #000 !important;
}

.cell-wrapper {
    border-bottom: 1px solid #000000 !important;
}

strong {
    color: #7d7d7d !important;
}

.ap-follow-btn .ap-sidebar-title {
    color: #7d7d7d !important;
}

.primary .post-footer {
    border: 1px solid #000000 !important;
    box-shadow: 0 1px 0 0 #000000 !important;
    background: #131313 !important;
}

.single-post-widget .post-list {
    background: #131313 !important;
}

.single-post-widget .post-list {
    border-right: 1px solid #000000 !important;
}

.single-post-widget .widget {
    border-bottom: 1px solid #000000 !important;
}

.pb-app-widget-discover .pb-app-widget-table {
    background: #0d0d0d !important;
}

.pb-app-widget-discover {
    border: 1px solid #000000 !important;
}

.categories {
    border-top: 1px solid #000 !important;
}

#sbar-content .widget section {
    box-shadow: 0 1px 0 0 #000000 !important;
}

.categories ul.post-categories {
    border-left: 1px solid #000 !important;
}

.categories li a {
    border: 1px solid #000 !important;
}

ul.share-btns li {
    border: 1px solid #000000 !important;
}

.share-box .shares span.count {
    border: 1px solid #9c9c9c !important;
}

.share-box .shares span.count:after {
    border-right: 4px solid #9c9c9c !important;
}

.wp-pagenavi {
    background: #131313 !important;
    border: 1px solid #000000 !important;
    box-shadow: 0 1px 0 0 #000000 !important;
}

#news-slider {
    background: #131313 !important;
}

.naag-slider .slides>li{
    border-right: 1px solid #000 !important;
}

.naag-slider .slides>li:nth-child(2n) {
    border-right: 1px solid #000 !important;
}

.fs-navigation {
    border-top: 1px solid #000 !important;
}

.fs-navigation {
    background: #0d0d0d !important;
}

.fs-control-prev {
    border-right: 1px solid #000 !important;
}

.fs-control-filters {
    border-right: 1px solid #000 !important;
}

blockquote.pr {
    background: #0d0d0d !important;
}

blockquote.pr section {
    border-right: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
}

blockquote.pr section p {
    color: #7d7d7d !important;
}

blockquote.pr footer {
    border-right: 1px solid #000 !important;
    border-bottom: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
}

blockquote.pr .fade-text {
    position: absolute;
    bottom: 60px;
    height: 100px;
    width: 94%;
    background: rgba(255,255,255,0);
    background: -webkit-gradient(linear,left bottom,left top,color-stop(0,#000000),color-stop(1,rgba(255,255,255,0))) !important;
    background: -ms-linear-gradient(bottom,#ffffff,rgba(255,255,255,0));
    background: -moz-linear-gradient(center bottom,#ffffff 0%,rgba(255,255,255,0) 100%);
    z-index: 1;
}
}
