css:
@-moz-document url-prefix("https://freefeed.net"), url-prefix("http://freefeed.net") {
@font-face {
    font-family: sahel;
    src: url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.woff);
    src: url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.woff?#iefix) format('embedded-opentype'), url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.woff) format('woff'), url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha9/dist/Sahel.woff) format('truetype');
    font-weight: normal;
}
.post, .timeline-post
{
    font-family: sahel !important;
    font-weight: 500;
    direction:rtl;
}
.post-userpic 
{ float: right !important;
    border: 1px #ccc solid;
       border-right-width: 1px !important;
     border-left-width: 1px !important;
    }
.post .post-body {
    margin-right: 63px;
}
.post-author {
    direction:rtl !important;
   

}
.post-text {
    border-right-width: 90px !important;
    font-size:14px;
    font-weight: 600;
   color: #333 !important;
}
.post-timestamp{
     direction:ltr !important;
}
.comment .comment-icon{
    float: right !important;
}

.likes .icon{
float: right;
color: #ff1414;
}

.post-textarea{
     font-family: sahel !important;
     direction:rtl;
    font-weight: 800;
}
.post-via-sources{
    direction:ltr !important;
    margin-right: 40px;
}
.col-md-3 *, .sidebar, box *, .search-input, .box-header-timeline{
    font-family: sahel !important;
    }
.comments
{
  font-size:12.5px; 
     font-weight: 600;
}

.post-footer, .likes{
   font-size:12px; 
     font-weight: 600; 
}
.box-body, body {
    background-color: #f8f8f8 !important;
    background: #f8f8f8 !important;
    
}
.profile .description
{
    font-family: sahel !important;  
}
}
