css:
@-moz-document url("http://dictionary.cambridge.org/"), url("https://dictionary.cambridge.org/") {
body > * {
    display: none;
}
.cdo-hdr__blocks {
    display: block;
    margin: 0;
    padding: 0;
}
.contain > * {
    display: none !important;
}
.contain > .cdo-search {
    display: block !important;
    width: 100% !important;
}
.contain > .cdo-search form > :not(.cdo-search__bar) {
    display: none !important;
}
}

@-moz-document url-prefix("http://dictionary.cambridge.org/dictionary/english/"), url-prefix("https://dictionary.cambridge.org/dictionary/english/") {
body {
    padding-top: 80px;
    padding-left: 0;
    padding-right: 0;
    padding-bottom: 0;
    margin: 0;
    margin-left: 10%;
    margin-right: 10%;
}
@media screen and (max-width: 800px) {
    body {
        margin-left: 3%;
        margin-right: 3%;
    }
}
@media print {
    body {
        padding: 0;
        margin: 0;
        width: 100%;
    }
}
body > * {
    display: none !important;
}
@media screen {
    header {
        display: block !important;
    }
}
header > * {
    display: none;
}
header > .cdo-search {
    display: block !important;
}
header .cdo-search > :not(form) {
    display: none;
}
header .cdo-search > form {
    display: block;
    position: relative;
    left: 20%;
    width: 60%;
}
@media screen and (max-width: 800px) {
    header .cdo-search > form {
        display: block;
        position: relative;
        left: 0;
        width: 100%;
    }
}
header .cdo-search form > :not(.cdo-search__bar) {
    display: none !important;
}
header .cdo-search form > .cdo-search__bar {
    width: 100%;
}
.contain {
    display: block !important;
    width: 100%;
    padding: 0;
    margin: 0;
}
.contain > :not(article) {
    display: none;
}
article > div {
    width: 100% !important;
    padding: 0 !important;
}
article > div > * {
    display: none;
}
#page-content {
    display: block;
    float: none;
    margin: 0;
    padding: 0;
    width: 100%;
}
#page-content > * {
    display: none;
}
#entryContent {
    display: block;
}
#entryContent > * {
    display: none;
}
#entryContent .cdo-dblclick-area {
    display: block;
}
#entryContent .cdo-dblclick-area > * {
    display: none;
}
#entryContent .cdo-dblclick-area > .page {
    display: block;
}
#entryContent .cdo-dblclick-area > .page > .page-header {
    display: none;
}
.tabs-entry > * {
    display: none;
}
.tabs-entry > .on {
    display: block;
}
.entry-body > div {
    padding: 1em !important;
    min-height: inherit;
}
.pos-header, .pos-body {
    padding: 0 !important;
}
.pos-body .cols {
    margin-top: 0 !important;
}
.di-head, .definition-src, .share {
    display: none;
}
@media print {
    .circle.sound {
        display: none;
    }
}
}

@-moz-document url-prefix("http://dictionary.cambridge.org/spellcheck/"), url-prefix("https://dictionary.cambridge.org/spellcheck/") {
body > * {
    display: none;
}
header {
    display: block;
}
header > * {
    display: none;
}
header > .cdo-search {
    display: block !important;
}
header > .cdo-search > * {
    display: none;
}
form#cdo-search-form {
    display: block !important;
}
form#cdo-search-form > * {
    display: none;
}
form#cdo-search-form > .cdo-search__bar {
    display: block;
    width: 80%;
    left: 10%;
}
.contain {
    display: block;
}
.contain > * {
    display: none;
}
.contain > article {
    display: block;
}
article > div {
    width: 80% !important;
    left: 10% !important;
    position: absolute !important;
    padding: 0 !important;
    margin: 0 !important;
}
article > div > div {
    width: 100% !important;
}
article .mod {
    display: none;
}
}
