css:
@-moz-document domain("logotournament.com") {
/*-----------------------------test css is above this line------------------*/

header nav.line-1:before {
	content: "V . 1 . 1";
	color: #c5c5c5;
	font-size: 9px;
}
@-moz-document url-prefix() {
	figure.designer.contest-list .signal-hint:hover .overlay-box, figure.signal-hint:hover .overlay-box {
		left: 0px !important;
	}
	header nav.line-1 figure.account-drop-down .drop-down {
		right: -14px !important;
	}
}
figure.tab.active {
	border-top: 2px solid #cecece;
	border-right: 2px solid #cecece;
	border-bottom: 0px solid #cecece;
	border-left: 2px solid #cecece;
}

figure.tab {
	margin-right: 5px;
}
form[name="logocourt"] figure.tab {
	top: 2px;
}
div.wrap-outer.gray-light {
	border-top: 2px solid #cecece;
}
div.wrap-outer.gray-light.scroll-to-me {
	border-top: 0px solid #cecece;
}
figure.scroll-tabs {
	top: 2px;
}
figure.modal section.background {
	background: #e2e2e2;
}
.button:hover {
	background: #f5be72;
	border-bottom: 3px solid #d6a766;
}
#tab_submit_entry {
	background-color: #b58181;
	color: #fefefe;
}
#tab_logocourt_list:before {
	content: "\f19c"" ";
	font-family: FontAwesome;
}
form[name='witdraw'] .button:before {
	content: "\f1ed"" ";
	font-family: FontAwesome;
	font-weight: normal;
}
a[href="logocourt/open"] [rel=open]:before {
	content: "\f19c"" ";
	font-family: FontAwesome;
}
[rel=closed]:before {
	content: "\f0e3"" ";
	font-family: FontAwesome;
}
[rel=judging]:before {
	content: "\f0b0"" ";
	font-family: FontAwesome;
}
[rel=top5]:before {
	content: "\f0ca"" ";
	font-family: FontAwesome;
}
[rel=extended]:before {
	content: "\f141"" ";
	font-family: FontAwesome;
}
[rel=complete]:before,
figure.payment-tabs a[href="account/payments?complete"] figure.tab:before {
	content: "\f00c"" ";
	font-family: FontAwesome;
}
[rel=blind]:before {
	content: "\f070"" ";
	font-family: FontAwesome;
}
[rel=staff]:before {
	content: "\f08a"" ";
	font-family: FontAwesome;
	color: #da9e4e;
}
[rel=live]:before {
	content: "\f192"" ";
	font-family: FontAwesome;
	color: red;
}
a[target=external]:after {
	content: " " "\f08e";
	font-family: FontAwesome;
}
form[name='logocourt'] div.wrap-inner h1:before {
	content: "\f19c"" ";
	font-family: FontAwesome;
}
figure.hold-payments figure.hold-details section.data:nth-child(1) div.clear:after {
	content: " ""(To all designers please raise a request to LT admin to 'ADD More Payment Options' like Payoneer and Skrill)";
	word-wrap: break-word;
	font-weight: normal;
	font-size: 14px;
	color: #fd8b8b;
}
a.menu-item[href*="logocourt"]:before {
	content: "\f19c"" ";
	font-family: FontAwesome;
}
a.menu-item[href*="logodesigners"]:before {
	content: "\f0c0"" ";
	font-family: FontAwesome;
}
a.activator[href*="logodesigners"]:before {
	content: none;
}
a.menu-item[href*="logodesigners/"]:before {
	content: none;
}
figure.contest-old-designer-test figure.entries-grid section.entries figure.entry .comments-indicator.new {
	animation: blink-animation .5s steps(5, start) infinite;
	-webkit-animation: blink-animation .5s steps(5, start) infinite;
}
@keyframes blink-animation {
	to {
		visibility: hidden;
	}
}
@-webkit-keyframes blink-animation {
	to {
		visibility: hidden;
	}
}
div.style2-description {
	filter: grayscale(100%);
	-webkit-filter: grayscale(1);
}
figure.side-panel section table tbody tr td section.click-catcher {
	width: 310px !important;
}
figure.table section.header {
	padding: 10px 0px 8px;
	border-bottom: 3px solid #d4d4d4;
	padding-left: 10px;
	background-color: #e8e8e8;
}
figure.entries-grid div.pad00 {
	clear: both;
display:none;
}
header .line-2-wrap {
	background: #383838;
}
figure.entries-grid section.entries {
	overflow: visible;
}
menu {
	margin: 47px 0 0 0;
}
div.mobile-menu-global-pages {
	border-right: 1px solid #5d5d5d;
}
menu a {
	color: #ffffff;
	margin: 0;
	font-size: 18px;
	padding: 0px 20px;
	border-left: 1px solid #5d5d5d;
}
menu .menu-item-drop-down .dropped .menu-item {
	font-size: 16px;
	display: block;
	white-space: nowrap;
	padding: 5px 5px 5px 10px;
	width: 90%;
	border-bottom: 1px solid #5f5f5f;
}
menu .menu-item-drop-down .dropped .menu-item:hover {
	background-color: #b18555
}
menu .menu-item-drop-down .dropped {
	position: absolute;
	left: 0px;
	top: 20px;
	border-radius: 0;
	padding: 5px 0px;
	-webkit-box-shadow: 10px 10px 0 0px rgba(0, 0, 0, .3);
	box-shadow: 10px 10px 0 0px rgba(0, 0, 0, .3);
	background: #383838;
}
header nav.line-1 figure.account-drop-down .drop-down {
	right: -10px;
	top: -4px;
	border: 0;
	border-radius: 0;
	-webkit-box-shadow: 10px 10px 0 0px rgba(0, 0, 0, .2);
	box-shadow: 10px 10px 0 0px rgba(0, 0, 0, .2);
}
figure.pager {
	-webkit-box-shadow: 3px 3px 0px 3px rgba(0, 0, 0, .3);
	box-shadow: -5px 5px 0px 0px rgba(0, 0, 0, .3);
	border-radius: 0px;
	border: 1px solid grey !important;
	z-index: 9999;
}
figure.entries-grid section.entries figure.entry .image-place:hover {
	top: -1px;
}
.revisions {
	position: fixed;
	top: 10px;
	left: 90px;
	background: #fff;
	padding: 2px 5px;
	border: 1px solid #acacac;
	z-index: 9999999;
	-webkit-box-shadow: 5px 5px 0px 0px rgba(0, 0, 0, .3);
	box-shadow: 5px 5px 0px 0px rgba(0, 0, 0, .3);
}
.container-pager,
.pager {
	position: fixed;
	top: 0;
	right: 0;
	width: 40px;
	border: 0px !important;
}
#tab_javascript {
	display: none;
}
div.toggling h2 {
	text-transform: uppercase;
	font-size: 16px !important;
	font-weight: bold;
	color: #c59450 !important;
}
.contest-progress div.title,
div.wrap-outer figure.contest-atts div,
div.wrap-outer div.wrap-inner span nobr,
div.wrap-inner nav,
figure.contest-status .details,
figure.contest-status .details span.semibold,
figure.entry-no-display p,
.disclaimer,
figure.sort-entries span:nth-child(1) {
	display: none !important;
}
figure.contest-brief .use {
	width: 50px;
}
section.click-catcher,
section.click-catcher figure.entry-comments {
	width: 850px !important;
}
section.click-catcher figure.entry-comments div.entry-block {
	height: 400px;
	width: 370px;
	float: left;
}
section.click-catcher figure.entry-comments .action {
	clear: both;
}
section.click-catcher figure.entry-comments .comments {
	height: 400px;
	width: 450px;
	overflow-y: auto;
	padding: 8px;
}
* {
	border-radius: 0 !important;
}
div.activity span.tablet {
	display: block !important;
	color: #95BC5C !important;
	font-weight: bold;
	position: absolute;
	margin: -60px 0 0 235px;
	text-align: right;
	font-size: smaller;
}
section.won-contest div.contest div.won i.fa-trophy {
	display: none;
}
figure.sort-entries span:nth-child(1) {
	display: none !important;
}
div.court-discussion figure.logocourt .logocourt-controls .logocourt-row-comments:before {
	display: none;
}
figure.how-court-works {
	display: none;
}
figure.contest-status-won section.won-text div.desktop {
	display: none;
}
div.use div {
	display: none;
}
figure.pms-list form section.header {
	display: none;
}
.DisplayNone {
	display: none;
}
.submit-entry div.block a,
.submit-entry div.pad20,
.submit-entry h2.pad20,
.submit-entry div.pad01 {
	display: none
}
.doing-ajax,
div.toggling h1 span,
.submit-entry h1 span,
.forum-informer,
figure.scroll-tabs a .disabled {
	display: none;
}
.scroll-to-me .wrap-inner .search-form {
	display: none;
}
div.activity .fa-question-circle:before,
.submitted h2 {
	display: none;
}
div.toggling h1 span,
div.wrap-outer .title .non-responsive {
	display: none;
}
figure.table.contest-list section.data .column-name .details i[data-title]:hover:after {
	display: none;
}
}
