css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.spacebattles.com") {
  
  #content .pageContent {
		background-color: #1E1E1E;
	}
	* {
		border-radius: 0 !important;
	}
	/*
	--------------------
	Forum Navigation 
	--------------------
	*/
	.navTabs {
		background-color: #1E1E1E;
		border-color: #3672b1;
	}
	.navTabs .navTab.selected .tabLinks, .navTabs .navTab.selected .navLink {
		background-color: #3672B1;
	}
	.breadcrumb {
		background-color: #2A2A2A;
		border: 1px solid black;
		font-size: 12px;
	}
	.breadcrumb .crust .arrow {
		display: none;
	}
	.breadcrumb .crust a.crumb {
		padding-left: 10px;
	}
	/*
	--------------------
	Links 
	--------------------
	*/
	a:link, a:visited {
		color: #207CC9;
	}
	.ugc a:hover, .ugc a:focus {
		color: white;
		background: none;
		text-decoration: underline;
		box-shadow: none;
	}
	/*
	--------------------
	Nodes/Subforum 
	--------------------
	*/
	.sectionMain {
		background-color: #2A2A2A;
		border-color: black;
		border-radius: 5px;
	}
	.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
		background-color: #2A2A2A;
		border-bottom: 1px solid black;
	}
	.node .nodeLastPost {
		background-color: #1E1F21;
		border: 1px solid #393939;
	}
	.nodeList .categoryStrip {
		background-color: #3672B1;
		border-color: #357ECD;
	}
	/*
	--------------------
	Miscellaneous
	--------------------
	*/
	.secondaryContent {
		background-color: #2A2A2A;
		border-color: black;
		border-radius: 5px;
	}
	.sidebar .section .secondaryContent h3 {
		border-bottom-color: black;
	}
	.messageList .newMessagesNotice {
		color: black;
	}
	.message .newIndicator {
		margin-right: -15px;
	}
	/*
	--------------------
	Posts 
	--------------------
	*/
	.messageUserBlock {
		background-color: #1E1E1E;
		border: 1px solid black;
	}
	.messageUserBlock .arrow {
		border-left-color: black;
		-moz-border-left-colors: black;
	}
	.avatar img, .avatar .img, .avatarCropper {
		border: 1px solid black;
		border-radius: 4px;
	}
	.message .messageInfo {
		background-color: #2A2A2A;
	}
	.likesSummary.secondaryContent {
		background-color: #1E1F21;
		border-color: #393939;
	}
	/*
	--------------------
	Threadmarks 
	--------------------
	*/
	.messageList .message.hasThreadmark {
		border-top: 1px solid black;
	}
	.threadmarker_nav_top .previous a, .threadmarker_nav_top .index a, .threadmarker_nav_top .next a, .threadmarker_nav_bottom .previous a, .threadmarker_nav_bottom .index a, .threadmarker_nav_bottom .next a {
		padding: 3px 6px;
		color: white;
		background-color: #3672B1;
		border: 1px solid black;
		border-radius: 4px;
	}
	.threadmarker_nav_top .previous a:hover, .threadmarker_nav_top .index a:hover, .threadmarker_nav_top .next a:hover, .threadmarker_nav_bottom .previous a:hover, .threadmarker_nav_bottom .index a:hover, .threadmarker_nav_bottom .next a:hover {
		color: black;
		background-color: #6cb2e4;
		border: 1px solid black;
		border-radius: 4px;
	}
	.threadmarker .label strong {
		color: #FF8C10;
	}
	.threadmarker .label {
		color: white;
		background-color: #3672B1;
		border: 1px solid black;
		border-radius: 4px;
	}
	.threadmarker {
		border: none;
	}
	.threadmark {
		background-color: #2A2A2A;
	}
	.memberListItem .extra .DateTime {
		padding-right: 10px;
	}
	/*
	--------------------
	Conversations 
	--------------------
	*/
	.conversation_view .messageList {
		border-top: 1px solid #415C87;
		border-right: none;
		padding-right: 0;
		border-radius: 10px;
	}
	/*
	--------------------
	Quotes/Spoilers 
	--------------------
	*/
	.bbCodeBlock .type {
		background-color: #393939;
	}
	.bbCodeBlock, .hasJs .bbCodeSpoilerText {
		background-color: #1E1E1E;
		border: 1px solid #393939;
	}
	.bbCodeBlock, .hasJs .bbCodeSpoilerText {
		margin-right: 0;
	}
	/*
	--------------------
	Buttons
	--------------------
	*/
	.button, .button.primary {
		color: white;
		background-color: #3672B1;
		border: 1px solid black;
		border-radius: 4px;
	}
	.button:hover, .button.primary:hover {
		color: black;
		background-color: #6cb2e4;
		border: 1px solid black;
		border-radius: 4px;
	}
	a.callToAction span {
		color: white;
		background-color: #3672B1;
	}
	/*
	--------------------
	Thread Lists
	--------------------
	*/
	.discussionListItem, .primaryContent {
		background-color: #2A2A2A;
		border-bottom: 1px solid black;
	}
	.discussionListItem .posterAvatar, .discussionListItem .stats {
		background-color: #2A2A2A;
	}
	.discussionList .sectionHeaders {
		background-color: #3672B1;
		border: 1px solid black;
	}
	.sectionFooter {
		background-color: #3672B1;
		border: 1px solid rgb(0, 0, 0);
		border-top: none;
	}
	.sectionFooter a {
		color: white;
	}
	/*
	--------------------
	Thread Banners 
	--------------------
	*/
	#uaThreadViewContainer, #uaThreadReadContainer {
		border: 1px solid black;
	}
	#uaThreadViewContainer h3, #uaThreadReadContainer h3 {
		border-bottom: 1px solid black;
	}
	/*
	--------------------
	Footer 
	--------------------
	*/
	.footer .pageContent {
		background-color: #1E1E1E;
		border: 0px solid black;
	}
	/*
	--------------------
	Text Editor 
	--------------------
	*/
	.messageList .message, .quickReply {
		padding-bottom: 10px;
	}
	.messageList .message, .quickReply {
		background-color: #2A2A2A;
		border: 1px solid black;
		border-radius: 5px;
	}
	#QuickReply .submitUnit {
		background-color: #2A2A2A;
		margin-top: 0px;
	}
	.bbCodeEditorContainer {
		background-color: #2a2a2a;
	}
	textarea.textCtrl.Elastic {
		background-color: #1e1e1e;
	}
	.textCtrl {
		border: 1px solid #393939;
	}
	.redactor_toolbar {
		background-color: #3672B1;
	}
	.redactor_box {
		background-color: #1E1E1E;
		border: 1px solid black;
	}
	div html {
		background-color: #1E1F21 !important;
	}
	/*
	--------------------
	User Polls 
	--------------------
	*/
	.pollBlock .pollOption label:hover {
		color: black;
		background-color: #6cb2e4;
	}
	/*
	--------------------
	Page Navigation 
	--------------------
	*/
	.PageNav a, .PageNav a {
		color: #6cb2e4;
		background-color: #2A2A2A;
		border-color: black;
	}
	.PageNav a:hover, .PageNav a:focus {
		color: #6cb2e4;
		background-color: #2A2A2A;
		border-color: #6cb2e4;
	}
	.PageNav a.currentPage {
		color: black;
		background-color: #3672B1;
		border-color: black;
	}
	/*
	--------------------
	Dropdown Menu
	--------------------
	*/
	.Menu .primaryContent, .Menu .secondaryContent {
		background-color: #2a2a2a;
	}
	.navPopup .PopupItemLinkActive:hover {
		background-color: #1e1e1e;
	}
	.Menu .sectionFooter {
		background-color: #3672b1;
	}

}
