css:
@-moz-document domain("speedtest.net") {
/*=========
Remove Ads
===========*/

#ad-top-wrap, .ad-vertical-left, .ad-vertical-right, .lbc-ad, #home-features.lbc, #dfp-eot-large, #dfp-eot-small, #dfp-eot-small.dfp-eot-small {
 display: none !important;   
}

/*============
Center Content
==============*/

#home-content {
 display: inline;   
}
#speedtest {
 margin-left: auto;
 margin-right: auto;
 display: block;
}
.body-ad-tower-stack #st-flash {
 margin: 0;
 float: none;
 display: block;
 width: inherit;
}

/*===================
Prettify Background
===================*/
.lowerboard #home-content-container {
 background: url('data:image/png;base64,') repeat-x;   
}
}
