  #form.fm span.bg.s_ipt_wr.quickdelete-wrap:hover{
    background-color :rgba(255,255,255,0.1) !important;
  }
  #su{
    background: none !important;
    border-bottom-width :0px !important;
    transition: background-color 1s !important;
    transition: box-shadow 0.25s !important;
    box-shadow:none !important;
    height: 34px !important;
  }
  #form.fm span.bg.s_btn_wr{
    height: 34px !important;
    animation: suMove 3s linear 0s infinite normal;
  }
  #su:hover{
    box-shadow : 0px 0px 2px rgba(255,255,255,0.7) !important;
    background-color: rgba(255,255,255,0.2) !important;
  }
  @-moz-keyframes suMove{
    0% {background-color: rgba(255,255,255,0) ;box-shadow : 0px 0px 2px rgba(255,255,255,0.5);}
    50% {background-color: rgba(255,255,255,0.3) ;box-shadow : 0px 0px 2px rgba(255,255,255,0);}
