css:
@-moz-document url-prefix("https://freefeed.net"), url-prefix("http://freefeed.net") {
@font-face {
    font-family: sahel;
    src: url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha6/dist/Sahel.woff);
    src: url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha6/dist/Sahel.woff?#iefix) format('embedded-opentype'), url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha6/dist/Sahel.woff) format('woff'), url(https://cdn.rawgit.com/rastikerdar/sahel-font/v1.0.0-alpha6/dist/Sahel.woff) format('truetype');
    font-weight: normal;
}
.post, .timeline-post
{
    font-family: sahel !important;
    font-weight: 500;
    direction:rtl;
     background-color: #141f38 !important;
    background: #232323 !important;
}
.profile-stats, .profile-stats-item{
    font-family: sahel !important;
     color: #bf4a74 !important;
}

.post-userpic 
{ float: right !important;
    border: 1px #ccc solid;
       border-right-width: 1px !important;
     border-left-width: 1px !important;
     margin-left: 20px;
    }
.post .post-body {
    margin-right: 63px;
  
     color: #f5f5f5 !important;
}
.post-author {
    direction:rtl !important;
    color: #d44659  !important;
 
   }
.post-textarea, .comment-textarea{
    direction:rtl !important;
    color: #d44659 !important;
	background-color: #232323 !important;
    background: #252525 !important;    
}
.post-timestamp{ color: #CCC !important;}


.post-text {
    border-right-width: 90px !important;
    font-size:14px;
    font-weight: 600;
   color: #f5f5f5 !important;
}
.post-timestamp{
     direction:ltr !important;
}
.comment .comment-icon{
    float: right !important;
    color: #bf4a74 !important;
     margin-left: 10px;
}
 .comment:before, .comment.highlighted{
 background-color: #232323 !important;
        border: 1px solid #ff1414 !important;
               color: #f5f5f5 !important;
              
}

.likes, .icon{
float: right !important;
color: #ff1414 !important;
     margin-left: 10px;
}
.post-lock-icon{
float: right !important;
color: #ccc !important;
     margin-left: 10px;
}

.post-textarea{
     font-family: sahel !important;
     direction:rtl;
    font-weight: 800;
}
.post-via-sources{
    direction:ltr !important;
    margin-right: 40px;
}
 .sidebar, .search-input, .box-header-timeline, .box-header-groups{
    font-family: sahel !important;
    color: #bf4a74 !important;
    }
.col-md-3 *, .post-edit-options, .post-edit-more-trigger{
    font-family: sahel !important;
    color: #CCC !important;
    }

.comments
{
  font-size:12.5px; 
     font-weight: 600;
}

.post-footer{
   font-size:12px; 
     font-weight: 600; 
    color: #bf4a74 !important;
}
.likes{
  
   font-size:12px; 
     font-weight: 600;
    color: #c5c5c5 !important;
    
    
}
.box-body, .box-footer {
    background-color: #232323 !important;
    background: #232323 !important;
    color: #d44659  !important; 
   }
body {
    background-color: #232323 !important;
    background: #232323 !important;
    
   }

.alert-info{ 
    background-color: #5a5a5a !important;
    background: #5a5a5a !important;
      color: #CCC !important;
    border-color:#a7a7a7 !important;
    font-family: sahel !important;
    
}

.post a {color: #b1a3a3!important ;}
.post a:focus{color: #bf4a74!important ;}
.post a:hover{color: #d44659 !important ;}

.profile-stats a {color: #b1a3a3!important ;}
.profile-stats a:focus{color: #bf4a74!important ;}
.profile-stats a:hover{color: #d44659 !important ;}
   
.send-to a {color: #b1a3a3!important ;}
.send-to a:focus{color: #bf4a74!important ;}
.send-to a:hover{color: #d44659 !important ;}

.alert-info a {color: #c3c3c3 !important;}
.alert-info a:focus{color: #bf4a74 !important;}
.alert-info a:hover{color: #d44659  !important;}

.profile .description
{
    font-family: sahel !important;  
    color: #b1a3a3!important ;
}
}
