css:
@-moz-document url-prefix("https://news.ycombinator.com/") {
body {
    color: #ccc;
    background-color: #333;
}
a:link {
    color: #c4c4ff;
}
a:visited {
    color: #b1b1ff;
}

.pagetop a:link, .pagetop a:visited {
    color: #000;
}

#hnmain:nth-child(n+1) {
    background-color: #484848;
}

.athing a:link {
    color: #c8c8c8;
}

.athing a:visited {
    color: #aaa;
}

.athing a:hover {
    color: #ddd;
}

.sitebit.comhead, .sitebit.comhead a:link {
    color: #aaa;
}

.sitebit.comhead a:visited {
	color: #aaa;
}

.sitebit.comhead a:hover {
    color: #b5b5b5;
}

.subtext, .subtext a:link {
    color: #a5a5a5;
}
.subtext a:visited {
    color: #aaa;
}

.itemlist tbody tr:nth-last-child(1) a:link,.itemlist tbody tr:nth-last-child(1) a:visited {
    color: #ccc;
}

.comment span {
    color:#b8b8b8;
}

.comhead, .comhead a:link, .comhead a:visited {
    color: #999;
}

.reply a:link, .reply a:visited {
    color: #aaa;
}

.yclinks a:link, .yclinks a:visited {
    color: #ccc;
}
}
