/* DARK THEME BEGIN */
@-moz-document domain('forums.furaffinity.net'){
* {
    font-family: Helvetica, Helvetica Neue, Arial;
}
html {
background: #20262e;
}
body {
    background: #20262e;
    color: #EBEBEB;
    /*font: 14px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;*/
    /*FA's Default setup*/
}
body .muted,
body a.muted,
body .muted a {
    color: #C7C7C7;
}
body .faint,
body a.faint,
body .faint a {
    color: #C7C7C7;
}
#header,
#header > div:first-child {
    margin-top: 2px;
}
#content .pageContent {
    background-color: #2A2A2A;
    padding: 15px;
    border: 1px solid #0F0F0F;
    border-radius: 6px;
    box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.1);
}
a:hover.externalLink {
    background: #3C3C3C;
    background-repeat: no-repeat;
}
a:link,
a:visited {
    color: #327BAE;
    text-decoration: none;
}
a.button {
    color: #327BAE;
}
a[href]:hover {
    color: #2F7B97;
    text-decoration: underline;
}
body #footer {
    background: #20262E;
    color: #3C5078;
}
.footer a:hover,
.footer a:active {
    color: #327BAE;
}
.PanelScroller .scrollContainer,
.PanelScrollerOff .panel {
    background-color: #2A2A2A;
    margin-bottom: 10px;
    border: 1px solid #393939;
    border-radius: 2px;
    font-size: 13pt;
}
.PageNav .pageNavHeader {
    padding: 0px 7px;
    background: none repeat scroll 0% 0% #222;
    border: 1px solid #393939;
    color: #C7C7C7;
    border-radius: 4px;
}
.PageNav a {
    border: 1px solid #393939;
    line-height: 26px;
    border-radius: 4px;
}
.PageNav a:hover {
    color: #FFF;
    text-decoration: none;
    background-color: #285E78;
    border-color: #393939;
}
.PageNav .scrollable {
    height: 28px;
    border-radius: 4px;
}
.PageNav a.currentPage {
    border-color: #393939;
}
a.PageNavPrev,
a.PageNavNext {
    color: #327BAE;
    padding: 0px;
    border: 1px solid #393939;
    border-radius: 4px;
    width: 30px !important;
}
.breadcrumb {
    border: 1px solid #393939;
    background-color: #333;
}
