/* PATCHES */ 
.button.primary {
    background-color: #525252;
}
.button {
    font-style: normal;
    font-size: 12px;
    font-family: Calibri, 'Trebuchet MS', Verdana, Geneva, Arial, Helvetica, sans-serif;
    color: rgb(0, 0, 0);
    background: #989898; /*url(styles/default/xenforo/gradients/form-button-white-25px.png) repeat-x top; */
    padding: 0px 6px;
    border: 1px solid rgb(221, 221, 235);
    border-top-color: rgb(255, 255, 255);
    border-bottom-color: rgb(179, 179, 189);
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    -khtml-border-radius: 7px;
    border-radius: 7px;
    text-align: center;
    -webkit-box-shadow: 0px 1px 4px 0px rgb(200,200,210);
    -moz-box-shadow: 0px 1px 4px 0px rgb(200,200,210);
    -khtml-box-shadow: 0px 1px 4px 0px rgb(200,200,210);
    box-shadow: 0px 1px 4px 0px rgb(200,200,210);
    outline: none;
    line-height: 21px;
    display: inline-block;
    cursor: pointer;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    box-sizing: border-box;
    height: 23px;
}


