css:
@-moz-document domain("zen.myatos.net") {
body {
  background-color: #222 !important;
  background-image: url(data:image/png;base64,) !important;
  background-clip: border-box !important;
  background-origin: padding-box !important;
  background-attachment: scroll !important;
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important;
  }
  
  h1, h2, h3, h4, h5, h6 {
    color: inherit;
  }
  
  .cke_editable, .cke_editable_themed, .cke_contents_ltr, .cke_show_borders {
    color: #fff !important;
  }  
  
  .navbar_new .navbar-inner {
    background: #000 !important;
  }
  
  .dropdown-menu-notif-content .bloc, .dropdown-menu-notif-content .bloc .wrap {
    color: #000 !important;
  }
 
  /**
  ** HOME
  **/
  
  .filter_title .ellipsis {
    color: #fff;
  }
  
  .feeds_more {
    background: #000 !important;
  }
  
  .feeds_more:hover {
    background: #035B99 !important;
  }

  .page_footer {
    color: #ccc;
  }
  
  .header_list .bold, span.browse_title, span.browse_title.dropdown-toggle {
    color: #ccc !important;
  }

  body .main .main_content {
    background: none;
  }

  .administrable, .dropdown-menu li.administrable a, .dropdown-menu li.administrable .txt, .mention, a, .count.administrable:hover, .administrable_no_hover, span.post_title {
    color: #ccc;
  }
  
  .administrable:hover, a.mention:hover, a:hover, .filters li .modal-toggle:hover, span.post_title:hover, span.post_title:hover .mention, div.post_title_mobile, .content .content_view_post h1 a.mention:hover, .label_space, ul.filters li:hover .administrable {
    color: #fff;
  }

  .nav-pills > li > a, .nav-pills > li > span.disabled {
    background: #fff;
    color: #000;
  }
  
  .administrableLight, .nav-pills-color a, .feeds li:hover, .items li.items_new, .dropdown-menu-notif li:hover, .dropdown-menu-notif li.item_unread, .tree_block .column_tree_block ul li:hover, .tree_block .column_tree_block ul li.current, .event_widget li.open ul.posts li:hover, .my_profile ul.info_bloc_list li:hover, .info_bloc #settings {
    background: #035B99;
    color: #ccc !important;
  }
  
  .item_content .message, span.message:hover {
    color: #ccc !important;
  }

  .quickpost {
    background: none;
    border: 2px solid #035B99;
  }
  
  .quickpost .false_input {
    background: none;
    color: #ccc;
  }
  
  .label_none, .label_user {
    color: #fff !important;
  }
  
  .greydark, .grey {
    color: #ccc !important;
  }
  
  .items > li, .new_style_widgets > li h3 {
    border-top: 2px solid #000;
  }

  .avatar {
    border: 1px solid #035B99;
  }
  
  ul.home_filters li.active:hover, ul.home_filters li.selected:hover, ul.home_filters > li.active, ul.home_filters > li.selected {
    background: #035B99;
  }
  
  ul.home_filters li.active:hover a, ul.home_filters li.selected:hover a, ul.home_filters > li.active a, ul.home_filters > li.selected a {
    color: #fff;
  }
  
  .widgets-new {
    background: none;
  }
  
  /**
  ** POST
  **/
  
  div.modale_new_preview .global_view .container_content {
   background-color: #222 !important;
   background-image: url(data:image/png;base64,) !important;
   background-clip: border-box !important;
   background-origin: padding-box !important;
   background-attachment: scroll !important;
   background-repeat: repeat !important;
   background-size: auto !important;
   background-position: left top !important;
  }
  
  .bkpublication, .content_container {
    color: #ccc !important;
  }
  
  .bkpublication hr {
   border-top: 2px solid #000 !important;  
  }
  
  .bkpublication .post_border {
    border-bottom: 2px solid #000;
  }
  
  .items.comment_preview > li {
    border: 2px solid #035B99;
  }
  
  .preview-content span {
    color: #ccc !important;
  }
  
  .container-right-col .label_space a {
    color: #035B99;
  }
  
  /**
  ** PROFILE
  **/
  
  ul.menuLeft.current {
   background: #035B99;
   border-top: 1px solid #042e4c;
   border-bottom: 2px solid #042e4c;
   border-left: 1px solid #042e4c;
  }
  
  ul.menuLeft.current > li.active > a, ul.menuLeft.current > li.active > a:hover, ul.menuLeft.current > li.active > span, ul.menuLeft.current > li.active > span:hover {
    background-color: #035B99;
  }
  
  ul.menuLeft li:hover:not(.active) {
    background: #035B99;
  }
  
  .black, .item_private .small_size a.black, .label.black {
    color: #ccc !important;
  }
  
  .user_profil_avatar .round_avatar_box {
    background: #000;
  }
  
  .tabs {
    background: #035B99;
  }
  
  /**
  ** SPACES
  **/
  
  .welcome_text {
    color: #ccc;
  }  
  
}
