css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Revision 4*/
/*Added .m-input field, .mdl-card__supporting-text*/
/*Small tweaks to alpha & text color settings*/
/*Getting frustrated at comment icons, dropdown shadow, email text field*/
/*Trimmed redundant code*/
/*Suggestions welcome.*/

@-moz-document domain("minds.com") {
  html{
    color: #cacaca;
  }
  .mdl-card{
    background: rgba(0,0,0,0.65) !important;
    color: #aaa !important;
  }
  .m-input, .ng-pristine, .ng-dirty, .ng-touched{
    background: rgba(27,27,27,0.95) !important;
    color: #aaa !important;
  }
  /*header*/
  .mdl-color--white{
    background: linear-gradient(#000, #333) !important;
  }
  .mdl-color--blue-grey-800 {
    background-color: #121212 !important;
  }
  /*text*/
  .mdl-color-text--blue-grey-500 {
    color: #cacaca !important;
  }
  .mdl-color-text--black {
	  color: #cacaca !important;
  }
  .mdl-card__supporting-text {
    color: #aaa !important;
  }
  .mdl-textfield__label {
    color: #aaa !important;
  }
  
  .minds-avatar {
    background: rgba(0,0,0,0.0) !important;
  }
  .mdl-card .m-owner-block {
    background: rgba(0,0,0,0.6) !important;
  }
  .m-owner-block a{
    color: #cacaca !important;
  }
  .minds-dropdown-menu{
    background: rgba(0,0,0,0.85) !important;
  }
  .mdl-menu__item{
    color: #fff !important;
        box-shadow: 0px 0px 9px !important;
  }
  .mdl-color--blue-grey-50 {
    background: rgba(0,0,0,0.6) !important;
  }
  .mdl-color-text--blue-grey-900 {
    color: #78ad56 !important;
  }
  /*BG*/

  .mdl-color--grey-100 {
    background: url("http://www.yfci.org/assets/ammap/gray-gradient.png") !important;
  }
  /*Blogs*/
  .m-title-block, .m-title-block-fixed {
    background: rgba(0,0,0,0.9) !important;
  }
  /*usercard*/
  .m-discovery-suggested .mdl-cell minds-card-user .m-usercard-bio {
    color: #aaa !important;
  }
  /*active tab*/
  .mdl-color--blue-grey-600 {
    background-color: #333 !important;
  }
  .mdl-card__title{
    color: #fff !important;
  }
  .mdl-button{
    color: #acacac !important;
  }
  minds-gatherings .minds-gatherings-search{
    background: rgba(0,0,0,0.6) !important;
    color: #aaa !important;
  }
  h1{
    color: #fff !important;
  }
  .mdl-tabs__tab{
    color: #fff !important;
    text-shadow: 0px 0px 9px #000 !important;
  }
  .mdl-menu__outline {
    background: rgba(0,0,0,0.7) !important;
  }
  .mdl-menu__item:hover{
    background: rgba(20,20,20,0.6) !important;
    color: #fff !important;
  }
  .mdl-card__supporting-text textarea{
    color: #aaa !important;
  }
  body a{
    color: #21CE54 !important;
  }
  minds-blog-view{
    background: url("http://www.yfci.org/assets/ammap/gray-gradient.png") !important;    
  }
  minds-comments form textarea{
    color: #aaa !important;
  }
  .mdl-grid {
    background: rgba(0,0,0,0.6);
  }
  .minds-blog-body p{
    color: #fff !important;
  }
  .m-h1-input {
    color: #cacaca !important;
  }
}
