css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filmtotaal.nl") {

* {
    font-size-adjust: .67 !important;
    box-shadow: none !important;
    border-radius: 0 !important;
}

}


@-moz-document domain("filmtotaal.nl") {

html, body {
/*     background: #414549 url("file:///C:/Users/swchuong/Pictures/Firefox/Wallpaper/NoName/Elisabeth%20Harnois/Elisabeth%20Harnois%20[000067].jpg") fixed no-repeat center / cover; */
    background: #414549 url("http://wfiles.brothersoft.com/e/elisabeth_harnois_47903-1600x1200.jpg") no-repeat fixed center top / 140% auto;
/*     height: 100%; */

}

/*Tansparent*/ .bg-color-white, .poster-grid-white-bg, .poster-grid .gradient-overlay {
    background: none;
}

/*No Border*/ .teasers ul > li {
    border: 0 !important;
}

/*Display none / Reclame*/ .ad-centering, .advert, .poster-grid .col-lg-5-banner {
    display: none;
}

/*Header*/
    .mod-header .wrap {
        background: rgba(0, 0, 0, .8);
        border-top: solid 3px rgba(170, 2, 3, .8);
        position: relative;
    }
    
    .sub-navbar {
        background-color: rgba(227, 224, 224, .6);
    }

    
    body {
            padding: 0;
    }
/*Header*/

.mod-footer {
    background: rgba(0, 0, 0, .8);
}

}
 

/*Frontpage*/@-moz-document url("https://www.filmtotaal.nl/"), url("https://www.filmtotaal.nl/films-op-tv") {
    
/*Lees kader*/
    .bg-color-dark, .poster-grid-dark-bg {
        background-color: rgba(48, 48, 48, .6);
    }

    .bg-color-grey, .poster-grid-gray-bg {
        background-color: rgba(227, 224, 224, .6);
    }
/*Lees kader*/

.block-last-news .teasers img, .block-tvmovies .eye-catcher:not(:hover), .block-tvmovies .teasers img, .poster-grid-white-bg  .item-ontv:not(:hover) {
    opacity: .6;
}

/*Hover*/
    .block-last-news .teasers li:hover {
        background: linear-gradient(transparent 10px, rgba(227, 224, 224, .6) 10px);
    }
    
    
    .block-tvmovies .teasers li:hover {
        background: linear-gradient(transparent 10px, rgba(227, 224, 224, .6) 10px, rgba(227, 224, 224, .6) 90px, transparent 90px);
    }
    
    .block-last-news .teasers li:hover *{
        opacity: 1;
        color: rgb(170, 0, 0) !important;
        text-shadow: none;
    }
    
    .block-tvmovies .teasers li:hover * {
        opacity: 1;
        color: rgb(0, 0, 0) !important;
        text-shadow: none;
    }
/*Hover*/

/*Lees tekst*/
.block-last-news .teasers ul li a *, .block-last-news .section-title > h1, .block-tvmovies .teasers ul li a *, .block-tvmovies .section-title > h1, .poster-grid-white-bg .heading-page-title h1, .poster-grid-white-bg .heading-page-title small, .poster-grid-white-bg .title span, .poster-grid-white-bg .title a.black {
    color: white;
    text-shadow: 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black;
}

/*text White*/ test {
    color: white;
}

/*No Shadow*/ .block-last-reviews .teasers li .stars *, .section-title > h1 small {
    text-shadow: none !important;
}

/*Text black*/ .sub-navbar ul li a {
    color: black;
}

}


/*Film info*/@-moz-document url-prefix("https://www.filmtotaal.nl/film/"), url-prefix("https://www.filmtotaal.nl/nieuws") {
  
.bgc-white, .bg-color-grey {
    background: none; 
}
    .news-headlines li {
        border: none;
    }
/*Lees kader*/
    .bg-color-dark {
        background-color: var(--background-test);
    }

    .bgc-grey, .mod-ratingbar, .mod-review_inline {
        background-color: rgba(227, 224, 224, .6);
    }
/*Lees kader*/
    
/*Hover*/
    .mod-aside .teasers li:hover, .block-last-news .teasers li:hover {
        background: linear-gradient(transparent 10px, rgba(227, 224, 224, .6) 10px);
    }
    
    
    .news-headlines li:hover {
        background: rgba(227, 224, 224, .6);
    }
    
    .mod-aside .teasers li:hover *, .block-last-news .teasers li:hover *{
        opacity: 1;
        color: rgb(170, 0, 0) !important;
        text-shadow: none;
    }
    
    .news-headlines li:hover * {
        opacity: 1;
        color: rgb(0, 0, 0) !important;
        text-shadow: none !important;
    }
/*Hover*/

.mod-hero:not(:hover), .mod-moviedescription .poster .wrap img:not(:hover), .news-headlines li > img:not(:hover), .mod-aside .teasers img:not(:hover), .block-last-news .teasers img:not(:hover), .news-headlines li > img:not(:hover) {
    opacity: .6 !important;
}

/*Lees tekst*/
.mod-moviedescription .main p, dl.table dt, dl.table dd, dl.table dd a, .mod-article .main .text, .mod-article .main .text a, .mod-comments > h1, .mod-comments .register, .mod-comments .register a, .mod-aside .teasers a h2, .mod-aside .teasers p, .mod-aside .teasers time, .block-last-news .teasers a *, .mod-aside .teasers .more, .comment-content-wrapper *, .mod-comments article time, .news-headlines li *, .news-headlines p .author, .news-headlines p time, .news-headlines .comments a {
    color: white;
    text-shadow: 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black;;
}

/*text White*/ test {
    color: white;
}

/*No Shadow*/ test {
    text-shadow: none !important;
}

/*Text black*/ test {
    color: black;
}  
}
