css:
@-moz-document domain("epitech.eu") {
body {
    color: #FFF;
    background: #333;
}
div#footer {
    background: #111;
}
#auth-login .message {
    color: #000;
}
#sidebar {
    background: #222;
}
#header #identification .infos:hover {
    background-color: #222;
    color: #fff;
}
#header #identification:not(.expanded) .infos:hover .nopicture {
    background-position: -284px -764px;
}
#wall #sidebar .title .picture.nopicture,
#homeboard section>h2>span.icon,
#module .tab > ul li.activite .item .info {
    background-image: url(https://puu.sh/rrQJt/b88d840507.png) !important;
}
#wall .icon.no-picture {
    background-image: url(https://puu.sh/rrQZO/0c83f054a8.png) !important;
}
#homeboard section {
    background: #222;
}
a {
    color: #2d75aa;
}
#breadcrumb ul li {
    background-color: #222 !important;
    color: #CCC;
}
#wall #content .bloc .item:hover {
    background: #222;
}
#wall #content .bloc .item:hover .data .content.html {
    background: none;
}
#header #identification.expanded .menu {
    background: #222;
    color: #fff;
    border: 1px solid #333;
    border-width: 0 1px 1px 1px;
}
#header #identification.expanded,
#header #identification.expanded a {
    color: #FFF;
}
#header #identification.expanded a.infos {
    background: #222;
}
#header #identification.expanded .picture {
    background-image: url(https://puu.sh/rrRvf/b68161495f.png);
}
#header #notification .dropdown:hover a,
#header #notification .dropdown.ui-expanded a {
    background: #222;
}
#header #notification .dropdown .layer {
    background: #222;
    color: #FFF;
    border-color: #333;
    box-shadow: -1px 2px 2px rgba(0, 0, 0, 0.6);
}
#header #notification .dropdown .layer .item:hover {
    background: #333;
}
#header #notification .dropdown .layer .item a {
    color: #2d75aa;
}
#header .intra {
    background-image: none;
}
#header .header-wrapper {
    background-color: #024f8b;
}
.datagrid table th > div {
    background: #222;
    color: #fff;
}
div.datagrid table thead th div div,
div.datagrid div:last-child table tfoot tr {
    background: none;
}
div.datagrid .overflow {
    background: #333;
}
div.datagrid table tfoot {
    background: #222;
}
#profil .nopicture {
    background-image: url(https://puu.sh/rrSwW/d44d2c1ed6.png);
}
h1 {
    color: #fff;
}
.datagrid tbody tr {
    color: #FFF;
}
div.datagrid tbody tr.item:hover {
    background: #222;
    color: #FFF;
}
#sidebar .filter h2 {
    background: #111;
    color: #FFF;
    border-color: #000;
}
#profil .bloc.main .data>.infos {
    background: #222;
    color: #FFF;
    border-color: #111;
}
#profil .bloc.main .data .infos>.item {
    border-color: #333;
}
#profil #flags .item {
    background-color: #222;
    box-shadow: 5px 5px 5px #111;
}
#profil #flags .item .success,
#profil #flags .item .header {
    border-color: #333;
}
#sidebar .rzone hr {
    border-color: #333;
}
#homeboard {
    border-color: #222;
}
#wall #content .item .first .data {
    border-color: #222;
}
#wall #content .item:hover .first .data {
    border-color: #333;
}
#wall #content.wall .bloc .item .identity .arrow.picture {
    border: solid transparent;
    height: 0;
    width: 0;
    border-color: rgba(34, 34, 34, 0);
    border-right-color: #222;
    border-width: 6px;
    background: none;
    margin-left: 68px;
    margin-top: 5px;
}
#wall #content.wall .bloc .item:hover .identity .arrow.picture {
    background: none;
    border-right-color: #333;
}
#sidebar form.filter.course .filters {
    border-color: #333;
}
input,
select,
textarea {
    background: #222;
    border: #111 1px solid;
    color: #FFF;
}
#sidebar input,
#sidebar select,
#sidebar textarea {
    background: #333;
    border: #111 1px solid;
    color: #FFF;
}
input[type="button"],
input[type="submit"],
button {
    cursor: pointer;
}
input:disabled {
    cursor: not-allowed;
}
#profil .bloc.tabulation .railroad a.all,
#profil .bloc.tabulation .railroad a:hover,
#profil .bloc.tabulation .railroad a.ui-active {
    color: #2d75aa;
}
#sidebar .planning.miniview {
    background: #151515;
    color: #fff;
}
#sidebar a {
    color: #2d75aa;
}
#sidebar .planning.miniview table td {
    color: white;
}
#sidebar .planning.miniview table td.weekend {
    background: #111;
}
#sidebar .planning.miniview table tr.ui-selecting td.today,
#sidebar .planning.miniview table td.today {
    background: #333;
    color: #FFF;
    border: 0;
}
#sidebar .planning.miniview table tr.selected td:hover,
#sidebar .planning.miniview table tr.selected td.weekend:hover,
#sidebar .planning.miniview table td.weekend:hover,
#sidebar .planning.miniview table td:hover,
#sidebar .planning.miniview table tr.selected td.ui-selecting,
#sidebar .planning.miniview table tr.selected td.weekend.ui-selecting,
#sidebar .planning.miniview table td.weekend.ui-selecting,
#sidebar .planning.miniview table td.ui-selecting {
    background: #FFF;
    color: #000;
}
#sidebar .planning.miniview table td.selected {
    background: #FFF;
    color: #000;
}
#sidebar>.wrapper>.bloc.filter {
    background: none;
}
#sidebar fieldset {
    border-color: #333;
    border-width: 1px;
}
.popup {
    background: #333 !important;
}
div#nmodalOverlay {
    background: #000;
    opacity: .8 !important;
}
.popup h1 {
    background: #222 !important;
}
.popup .item.buttons {
    background: #222 !important;
}
.planner .top {
    background: #222;
}
.planner .scroll {
    border-color: #222;
}
.planner .top .events tbody tr,
.planner .bottom .events tbody tr {
    border-color: #111;
    background-color: #222;
}
.planner .v-header {
    background: #333;
    color: #FFF;
    border-color: #111;
}
.planner.daysview > .days .scroll .events {
    border-color: #111;
}
.planner.daysview > .days .scroll .events .grid tr,
.planner.daysview > .days .scroll .events td,
.planner .v-header div {
    border-color: #222;
}
.waiter {
    background: #000;
    opacity: .5 !important;
}
.progress .message {
    background: #333;
    border-color: #222;
    color: #FFF;
}
#trombi a.item:hover {
    border-color: transparent;
    background: #222;
}
.ticket-view .button.hide > span,
.ticket-view .button.show > span,
.ticket .button.filter.hide > span,
.ticket .button.filter.show>span {
    color: black;
}
.message {
    color: #000;
}
#file .bloc.main ul li.ui-selected .wrapper,
#file .bloc.main ul li:hover .wrapper {
    background: #222;
}
#admin #main #content .bloc.main>.group {
    background: #222;
    color: #FFF;
}
h2 {
    color: #FFF;
}
#homeboard section article {
    background: #333;
}
#elearning #content .homeview ul li .color-wrapper,
#elearning #content .bloc .data .mainview ul li .wrapper.expand ul,
#elearning #content .homeview ul li .maxi-wrapper .wrapper.expand .separator,
#elearning #content .bloc .data .mainview ul li .title,
#elearning #content .homeview ul li .wrapper ul,
#elearning #content .homeview ul li .maxi-wrapper .separator {
    background: #222;
    color: #FFF;
}
#elearning #content .homeview ul li ul li a {
    color: #FFF;
}
#elearning #content .homeview ul li a {
    color: #2d75aa;
}
#elearning #content .homeview ul li .onglet .filter li {
    background: #222 !important;
    border-bottom: 1px #111 solid;
}
#elearning #content .homeview ul li .onglet .filter li.active {
    background: #111 !important;
}
#elearning #content .homeview ul li .onglet .filter li a {
    color: #fff !important;
}
#elearning.ui-fullscreen #content .bloc.main .rzone {
    background: #333;
}
#sidebar form.filter.course .datatree>dl>dd {
    background: #111;
}
#sidebar form.filter.course .datatree > dl dd {
    background: #111;
}
#module .tab > ul li.activite>.title {
    background: #333;
    border-color: #222;
}
#module .tab > ul li.activite>.title:hover {
    background: #222;
    border-color: #333;
}
#module .tab > ul li.activite .item.title .categ {
    color: #ccc;
}
#module .tab > ul li.activite > .title .dates .bargraph>.closed-on {
    background: #fFF;
}
#module .bloc.main .data .item.desc.credits label,
#module .bloc.main .data .item.desc.semestre label {
    color: #FFF
}
}
