css:
@-moz-document url-prefix("https://www.inacap.cl/tportalvp/intranet-alumno") {
#ina-overlay,
#ina-modal,
#contenedor > br,
#contenedor > br + #t1_select,
#contenedor > br + #t1_select + br,
[class^="bloque_banner"],
#t1_titulo,
#t4_titulo,
#t5_titulo,
#t1,
#fmtd,
.cursos-online,
.seguroAccidente,
.cursosExtranjero,
.losgenios,
.portalBlockBG .content-li:nth-child(3),
.portalBlockBG .content-li:nth-child(4),
.portalBlockBG .bgtop,
[class^="bloque_noticia"],
.tablaPrincipal footer,
.tablaPrincipal header,
.bloque_zlibre_5891 > p {
  display: none!important;
}

body {
  background: none!important;
}

#contenedor {
  margin-top: 26px!important;
}

#t5 {
  display: block;
}

a:hover,
a:focus,
[style*="#c00"],
[style*="#C00"],
[style*="#990000"] {
  color: #D81E5B!important;
}

.content-li ul li {
  background: none!important;
  padding: 0;
}

.content-li ul li a {
  padding: 1em 0 0 2em;
}

.content-li ul li.titulo-accesos {
  background: #D81E5B!important;
  color: #fff;
  height: 24px;
  padding-left: 0;
  margin-bottom: 0;
  text-align: center;
}

.content-li {
  border-radius: 3px;
  border: 0;
  overflow: hidden;
  background: #f9f9f9!important;
}

.bloque_zlibre_1067 {
  background: #f9f9f9!important;
}

.accessmail button,
li.ambiente-aprendizaje a {
  background: #D81E5B;
  border-radius: 3px;
  color: #fff!important;
  text-align: center;
  line-height: 58px;
  transition: all .2s ease;
  text-decoration: none!important;
  text-indent: 0;
}

.accessmail button:hover,
li.ambiente-aprendizaje a:hover {
  background: #EF1F64;
}

.accessmail button span {
  display: none;
}

.accessmail button:before {
  content: 'INACAP MAIL';
  display: inline-block;
  color: #fff;
}

li.ambiente-aprendizaje a {
  margin-top: 1em;
  width: 100%!important;
  height: 100%!important;
}

ul.accesos li.inacapMail,
ul.accesos li.ambiente-aprendizaje {
  background: none;
  border: none;
}

#contenedor > .box > .box-header {
  display: none;
}

#contenedor > .box {
  background: none!important;
  box-shadow: none!important;
  border: none;
}

#contenedor > .box > .box-content {
  border: none;
  padding: 0;
}

.box-content .box .box-content thead td {
  background: #f9f9f9!important;
}

.box-content .box .box-header {
  background: #f9f9f9;
}

.widget_intranet_alumno .box {
  box-shadow: none!important;
}

.widget_intranet_alumno .box-content {
  padding: 0!important;
}
}

@-moz-document url-prefix("https://siga3.inacap.cl/siga/portal_nl/alumnos/mis_notas") {
.panel-collapse {
	height: auto;
  display: block;
}

#carrerasSelectBoxIt,
#periodosSelectBoxIt,
#imprimir {
	background: none;
  border-radius: 3px;
}

[data-toggle] span {
	display: none!important;
}

.panel-heading {
	background: none!important;
  pointer-events: none!important;
}

.panel-heading .col-md-6 {
	width: auto!important;
  display: inline-block;
  display: none;
}

.panel-heading .col-md-6 p {
  display: inline-block;
}

.panel-heading {
  background: #D81E5B!important;
}

.panel-title {
  color: #fff!important;
  font-weight: 100!important;
}

tr th {
  background: none!important;
}

table,
table * {
	border: none!important;
}

#footer,
.navbar {
	display: none;
}

.container {
	width: 960px;
  background: #F9F9F9;
  border: none!important;
  box-shadow: none!important;
  padding: 0!important;
  margin: 0 auto!important;
}

body {
	background: #fff;
  font-size: 1.2rem;
  font-family: 'Helvetica Neue', Helvetica, sans-serif;
}
}

@-moz-document url-prefix("http://lms.inacap.cl/") {
#site-news-forum,
#newmessageoverlay,
#page-footer,
.navbar {
	display: none;
}

#page-header {
	height: 4rem;
  background: #D81E5B;
}

#page-content {
	padding-top: 1em;
}

img[src="http://lms.inacap.cl/pix/moodle_filter.png"] {
	display: none;
}

p img[src^="data"] {
	height: 4em;
}

#headerlogo {
	height: 100%;
  padding: 0;
}

#headerlogo img {
	height: 100%;
}

#region-pre {
	width: auto;
}

#frameleft,
#framerigth,
#frametopright,
#framebottom,
#frametop {
	background: none!important;
}

#frametopright,
#framebottomright {
	display: none;
}

.title {
	background: #D81E5B!important;
}
.title h2 {
	color: #fff!important;
}
.block {
	border: none;
}
}

@-moz-document url("http://www.inacap.cl/portal"), url("https://www.inacap.cl/tportalvp/alumnos") {
#slider,
.hcha {
	display: none;
}

.accesoInternos li a,
.menu-about {
	background-color: #D81E5B!important;
}

.contentCapitalHumano,
.secParallax {
	display: none;
}

header.p1 {
	background: #D81E5B!important;
  margin-bottom: 1em;
}

#navg a,
ul.nav a,
.ide-portal a:hover {
	color: #fff!important;
}

h1 {
	color: #D81E5B!important;
}

.secAccesos {
	background: #D81E5B!important;
}

h1.accesos {
	display: none;
}

.header {
	background: #D81E5B!important;
}
}
