css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wordpress.com") {
body.wp-admin * {
font-family:"Lucida Grande", "Lucida Sans Unicode", Verdana, "Bitstream Vera Sans", sans-serif !important;
}
.wrap h2, .quicktags, .search, #your-profile legend, .tablenav .displaying-num, #wphead h1 a, #wphead h1.long-title, #adminmenu a.menu-top,
#adminmenu .wp-submenu-head, .tool-box .title, .inline-edit-row fieldset span.title, .inline-edit-row fieldset span.checkbox-title, .wp-admin div#footer * {
font-family:Georgia, "Times New Roman", "Bitstream Charter", Times, serif !important;
}
* {
letter-spacing:normal !important;
}
img#wpstats {
display:none;
}
}
