css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://pack.guildwork.com(?!(/admin)).*") {
BODY {font-family: sans-serif; background-color: black;}
 body {
   background-image: url("http://files.north-edge.com/img/wolfBG.png");
    background-repeat: no-repeat;
    background-position: center;
    background-attachment: fixed;
    background-color: black;
}
a#brand>img {
  visibility:hidden
}

.content a,.panel a, a.hover {
  color:#9c8080 !important
}
a.actor,a.author {
color:#ffd747!important;
font-weight: 100
}
.container-header {
  width:1170px;
  margin:auto;
  padding-left:0;
	padding-right:0
}
body {
	background-color:#000000;
	background-repeat:no-repeat;
	background-size:cover;
	background-origin:content-box;
	background-attachment:fixed;
	font-family:"Century Gothic",CenturyGothic,AppleGothic,sans-serif
}
.navbar-brand {
	font-family:"Century Gothic",CenturyGothic,AppleGothic,sans-serif
}
.navbar {
	background-color:transparent;
	margin-bottom:0;
	border: none;
}
.navbar-default {
	border-color:transparent
}
.navbar-default .navbar-brand:focus,.navbar-default .navbar-brand:hover {
	color:black;
	background-color:transparent
}
.navbar-default .navbar-nav>li>a,.navbar-default .navbar-text {
	color:#fff
}
.navbar .navbar-brand {
	margin-left:0
}
ul {
	color:#fff
}
#forums td,#threads td {
	background:#000000
}
.fc-widget-content,.fc-widget-header {
	background:#4a5559
}
.fc-widget-header {
	background:black
}
.table,.table-bordered {
	background:#000000
}
#site-header {
	margin-top:20px
}
#site-body {
	background-color:transparent;
	margin-top:0
}
.navbar-fixed-top .container,.navbar-fixed-bottom .container {
	width:980px
}
.panel-default>.panel-heading,.panel-footer {
	background-color:black
}
.comment-container .bubble {
	padding:3px;
	-webkit-border-radius:3px;
	border-radius:3px
}
.comment-container .bubble .comment {
	border:1px solid #000;
	-webkit-border-radius:3px;
	border-radius:3px
}




.container-nav {
	width:1170px;
	height:50px;
	background-color:#000000;
	margin-left:auto;
	margin-right:auto;
	padding-left:0;
	padding-right:0;
}
ul.nav li:hover {
	background:black;
}
.navbar-default .navbar-nav>li>a:hover,.navbar-default .navbar-text {
	color:white
}
.page-header {
	margin:0 0 0 -30px
}

.panel,.panel-default,.panel-body {
	font-size:11px;
	margin:5px 7px;
	overflow:hidden;
	word-wrap:break-word;
	background:#000000;
	-moz-border-radius:3px;
	border-radius:3px
}
.panel-body {
	padding:5px
}
.panel-title {
	font-size:12px
}
.widget-body {
	background-color:#000000
}
#broadcasts,#feed {
	border-style:solid;
	border-width:5px;
	-moz-border-radius:0;
	border-radius:0;
	border-color:black;
	background-color:#000000
}
.broadcast {
	background-color:#000000;
	border-color:black;
	-moz-border-radius:0;
	border-radius:0;
	border-width:5px
}
#brand img {
	border-radius:0;
	margin-left:100px
}
img,img.avatar,.character {
	border-radius:5px;
	-moz-border-radius:5px
}
.thread,.clearfix {
	background:#000000
}
h3 {
	font-size:18px;
	line-height:27px;
	font-weight:30000;
	color:#fff;
	font-family:"Century Gothic",CenturyGothic,AppleGothic,sans-serif
}
.comment-container .bubble .comment .body {
	margin-top:-3px;
	background:#000000;
	color:#eee;
	-webkit-border-radius:3px;
	border-radius:3px
}
a,.comment-container .bubble .comment .meta p.author a.author,.convo-list-wrapper a.author,.convo-list-wrapper a.subject,#chat .conversation .conversation-frame .conversation-title a {
	color:#fff
}
#widgets .nav.nav-list.well {
	padding-top:7px;
	padding-bottom:7px;
	-webkit-border-radius:3px;
	border-radius:3px
}
#chat .conversation .close {
	font-size:16px
}
.close {
	opacity:.8
}
.control-group {
	border:1px black;
	background-color:rgba(0,0,0,0.6);
	border-radius:3px
}
.comment-container .bubble .comment {
    border: 0px solid #000;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}
.page-header {
	background:transparent;
	margin:0 0 0 0
}
.xivdb-tooltip-frame {
	box-shadow:none
}
.xivdb-tooltip-content-header-data-title {
	color:#fff !important
}
.well {
    background: #000;
    border: 1px solid #000;
    -webkit-box-shadow: none;
    box-shadow: none;
    font-size: 13px;
    text-align: center;
margin: 4px 4px;
overflow: hidden;
word-wrap: break-word;
background: #000;
-moz-border-radius: 3px;
border-radius: 3px;
}
.well {
    min-height: 10px;
    padding: 1px;
    margin-bottom: 20px;
    background-color: #000;
    border: 1px solid #404040;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
}
.nav {
    padding-left: 0;
    margin-bottom: 0;
    list-style: none;
}
}
