css:
@-moz-document domain("massivelyop.com") {
#header-wrap {
    background: #161616;
}

#wrapper {
    background:#101010;
}

body, input, textarea {
    font-family: "Bitter";
}
body {
    color: #dfdfdf;
    background-color: #101010;
}

div.fyre-comment-wrapper {
	background: #161616;
}

div.fyre-comment-divider {
	color: #202020!important;
}

.fyre-comment-divider {
	color: #202020;
}

.fyre, .fyre-comment, .fyre-comment p, .fyre-comment div {
    color: #b3b3b3!important;
}

.fyre a {
    color: #dd791f!important;
}

.fyre-editor .fyre-editor-container .fyre-editor-editable > p * {
    color: #cecece;
    font-family: "Helvetica Neue",Arial,Helvetica,Geneva,sans-serif;
    font-size: 12px;
}

.fyre-editor .fyre-editor-container .fyre-editor-editable {
    color: #a0a0a0;
	background-color: #333333;
    border: 1px solid #a1a1a1;
}

.socialspam {
    box-shadow: 0 0 4px #000000;
    border: 1px solid #303030;
    background: #202020;
}

.widget {
    box-shadow: 0 0 4px #000000;
    border: 1px solid #303030;
    background: #202020;
}

.search-form .search-field {
    background: #353535;
    border: 1px solid #404040;
	color: #d5d5d5;
}

.page-title, .post-title, .post-title a:link, .post-title a:visited, .archive-title span, .postmeta a:link, .postmeta a:visited, .post-pagination a:link, .post-pagination a:visited, #comments .comments-title, #respond #reply-title {
    color: #dd791f;
}

a, a:link, a:visited, .comment a:link, .comment a:visited, .archive-title span, .post-title a:hover, .post-title a:active, .post-pagination a:hover, .post-pagination a:active, .post-pagination .current {
    color: #dd791f;
}

a, a:link, a:visited, .comment a:link, .comment a:visited, .archive-title span, .post-title a:hover, .post-title a:active, .post-pagination a:hover, .post-pagination a:active, .post-pagination .current {
    color: #dd791f;

}

html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    color: #dadada;

}

#frontpage-slider {
    border: 1px solid #303030;
}

.frontpage-slider-controls .zeeflex-control-nav li {
    padding: 0 1px 1px 0;
    background: #dd791f;
    zoom: 1;
}

ul.related_post li a {
    color: #4b89d8;

}

.postmeta {
    color: #333;
	background: #35312c;
}

.prspeak {
    background: #252525;
    box-shadow: 0 0 4px #000000;
    border: 1px solid #404040;
}

.wp-polls {
    background: #151515;
    box-shadow: 0 0 4px #000000;
    border: 1px solid #353535;
}

.type-post, .type-page, .type-attachment {
    box-shadow: 0 0 4px #000000;
    border: 1px solid #303030;
    background: #181818;
}

blockquote {
    background: #35312c;
    border-left: 10px solid #201e1a;
}

.sourcesnip {
    color: #cbcbcb;
    box-shadow: 0 0 5px #101010;
    border: 1px solid #202020;
}

.bannerad3 {
    box-shadow: 0 0 4px #545454;
    border: 1px solid #000;
    background: #2a2723;
}

div#amzn-native-ad-0 div.amzn-native-products-list div.amzn-native-product {
    background-color: #252525;
    border: 1px solid #404040;
    box-shadow: 0 0 4px #000000;
}
.post-pagination {
    box-shadow: 0 0 4px #000;
    border: 1px solid #303030;
    background: #181818;
}
}
