css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ogame.gameforge.com") {
body {
    background: url("http://i.imgur.com/XAWA10x.png") !important;
}
#bar {
    background: #444444;
    border: solid 1px rgb(40, 40, 40);
    border-bottom: none;
    height: 18px;
}
#boxBG {
    background: none;
}
#info {
    background-image: none !important;
    background-color: rgba(0, 0, 0, 0.3) !important;
    border: 1px solid rgb(40, 40, 40);
    border-top: none;
}
#planet {
    padding: 0 6px 0 7px;
    top: 2px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(40, 40, 40);
}
#message-wrapper {
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(60, 60, 60);
}
#planetList {
    width: 151px;
    margin-top: -15px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(40, 40, 40);
}
#countColonies {
    top: -1px;
    height: 40px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(60, 60, 60);
}
#rechts {
    left: -7px;
}
#links {
    border: 1px solid rgb(40, 40, 40);
    margin-top: 2px;
    background: rgba(0, 0, 0, 0.3);
    margin-right: 2px;
}
#links #menuTable li a,
#ago_menubutton,
#oGameVersionCheckMenuButton,
#optionIFC .menubutton {
    background: none !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.5);
}
#links #menuTable li a:hover,
#ago_menubutton:hover,
#oGameVersionCheckMenuButton:hover,
#optionIFC .menubutton:hover {
    background: rgba(255, 255, 255, 0.2) !important;
}
#links .leftmenu li a.selected:link,
#links .leftmenu li a.selected:visited,
.no-touch #links .leftmenu li a.selected:hover,
#links .leftmenu li a.selected:active {
    background: rgba(255, 255, 255, 0.2) !important;
}
#eventboxContent {
    top: 2px;
    left: 2px;
}
#eventHeader {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(40, 40, 40);
}
#eventContent {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(40, 40, 40);
    border-bottom: none;
    border-top: none;
}
#eventFooter {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(40, 40, 40);
    border-top: none;
}
.eventFleet {
    background: rgba(222, 222, 222, 0.2) !important;
}
#eventContent .odd {
    background: rgba(222, 222, 222, 0.1) !important;
}
.content-box-s {
    background: rgba(0, 0, 0, 0.3);
}
.content-box-s .header {
    border: solid rgb(40, 40, 40);
    border-width: 0px 1px 1px 1px;
    background: rgba(0, 0, 0, 0.3);
}
.content-box-s .content {
    border: solid rgb(40, 40, 40);
    border-width: 0px 1px 0px 1px;
    background: none;
}
.content-box-s .footer {
    border: solid rgb(40, 40, 40);
    border-width: 0px 1px 1px 1px;
    background: none;
}
#star,
#star1,
#star2,
.c-left,
.c-right {
    display: none;
}
table.construction.active {
    background: none !important;
    border: none !important;
}
#buffBar {
    background: rgba(0, 0, 0, 0.3);
}
#planetdata .overlay {
    background: rgba(0, 0, 0, 0.5) !important;
}
#mmonetbar,
#siteFooter {
    background: rgba(0, 0, 0, 0.7);
}
#planet.shortHeader {
    padding-right: 0px;
    right: 2px;
    padding-left: 6px;
    background: rgba(0, 0, 0, 0.4);
}
#chatList {
    background: rgba(0, 0, 0, 0.3);
}
#chatList .header {
    background: none;
    border: 1px solid rgb(40, 40, 40);
}
#chatList .content {
    background: none;
    border: none;
}
.msg {
    background-image: none;
    background: #111a21;
}
#chatList {
    padding-bottom: 10px;
}
#chatList .header {
    background-color: rgba(0, 0, 0, 0.1);
    border-bottom: 1px solid rgb(30, 30, 30);
}
#chatList .footer {
    display: none;
}
#chatContent .header {
    background-image: none;
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(20, 20, 20);
}
#chatContent .content {
    background-image: none;
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(20, 20, 20);
    padding-bottom: 5px;
}
#chatContent .footer {
    display: none;
}
.js_playerlist {
    border: 1px solid rgb(20, 20, 20);
    background: rgba(0, 0, 0, 0.3);
}
.js_playerlist .header {
    background-image: none;
    background: rgba(0, 0, 0, 0.2);
    border-bottom: 1px solid rgb(20, 20, 20);
}
.js_playerlist .content {
    background-image: none;
    background: rgba(0, 0, 0, 0.1);
    border: none;
}
.js_playerlist .footer {
    background-image: none;
}
.js_playerlist #ui-id-1,
.js_playerlist #ui-id-3,
.js_playerlist #ui-id-5 {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(20, 20, 20);
}
.ui-accordion .ui-accordion-header::after {
    background-image: none;
}
li.playerlist_item {
    background: rgb(40, 40, 40);
    color: white;
}
li.playerlist_item.odd {
    background: rgb(50, 50, 50);
}
.menu_icon {
    margin-top: 2px;
}
#eventboxContent table#eventContent {
    color: white;
}
#eventHeader,
#buttonz .header,
#sendfleet .header,
.header .c-left,
.header .c-right {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgb(40, 40, 40);
    border-width: 1px 0px 1px 0px;
    top: 1px;
}
#buttonz .content,
.tabs_wrap .tab_ctn {
    background: rgba(0, 0, 0, 0.3);
}
#messages .tab_inner::before,
#buttonz .footer {
    background-image: none;
}
#messages #buttonz .content {
    padding: 10px 15px 15px;
}
.subtabs .ui-tabs-active.first .txt_link,
.subtabs .ui-tabs-active.last .txt_link,
.no-touch .subtabs .ui-state-hover,
.subtabs .ui-tabs-active,
.subtabs .last .txt_link,
.subtabs .first .txt_link,
.subtabs {
    background-image: none;
}
.ui-tabs-active,
.ui-state-active {
    background-image: none;
    border: 1px solid rgba(0, 0, 0, 0.7);
    border-radius: 0;
    background-color: rgba(255, 255, 255, 0.2);
}
.ui-state-hover {
    background-color: rgba(255, 255, 255, 0.3);
}
#ui-id-23 {
    background-image: none;
    border: 1px solid rgba(0, 0, 0, 0.7);
    border-radius: 0;
    background-color: rgba(210, 210, 255, 0.3);
}
#ui-id-23:hover {
    background-color: rgba(210, 210, 255, 0.4);
}
#slot01.slot {
    background-image: none;
    background-color: rgb(90, 90, 90);
    border: 1px solid rgb(50, 50, 50);
    top: 265px !important;
    right: 5px !important;
}
#slot01.slot:hover {
    background-color: rgb(110, 110, 110);
}
#detail {
    background-image: none;
    background-color: rgb(60, 60, 60);
    padding-left: 8px;
    padding-right: 6px;
    margin-left: -7px;
    border: 1px solid rgb(40, 40, 40);
}
#detail #description {
    background-color: rgb(40, 40, 40);
    border: 1px solid rgb(10, 10, 10);
}
ul#storage {
    background-image: none;
}
#resourceSettings #inhalt .headerRS,
#resourceSettings #inhalt .footerRS,
#resourceSettings .headerRS,
#resourceSettings .footerR {
    background-image: none;
}
#resourceSettings #inhalt .headerRS,
#resourceSettings #inhalt .footerRS {
    background-color: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(40, 40, 40);
}
#resourceSettings #inhalt .mainRS {
    background-image: none;
    background-color: rgba(0, 0, 0, 0.3);
    padding-top: 0;
}
#resourceSettings #inhalt .mainRS form table {
    background: none;
}
#resourceSettings #inhalt .mainRS form table tr #factor {
    background-color: rgba(0, 0, 0, 0.3);
}
#resourceSettings #inhalt .mainRS form table tr {
    background-color: rgba(0, 0, 0, 0.2);
}
#resourceSettings #inhalt .mainRS form table tr select {
    background-color: black;
}
#station .content {
    padding-bottom: 15px;
    border: 1px solid rgb(40, 40, 40);
}
#traderOverview #inhalt #planet {
    height: auto;
    padding: 0;
}
.h_battleships {
    background-image: none;
}
.factorbutton .btn_blue {
    background-image: none;
    background-color: rgb(90, 90, 90);
    border: 1px solid rgb(50, 50, 50);
    border-radius: 0;
}
.factorbutton .btn_blue:hover {
    background-image: none !important;
    background-color: rgb(110, 110, 110);
}
.fleetStatus {
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(50, 50, 50);
}
#fleet1 .allornonewrap {
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgb(50, 50, 50);
}
.ui-dialog .ui-dialog-titlebar {
    background-image: none;
    background: rgb(50,50,50);
}
.ui-dialog-content {
    background-image: none !important;
    background-color: rgb(65,65,65) !important;
}
#planetMaintenance .btn_blue, #abandonplanet #block {
    background-image: none;
    background-color: rgb(90, 90, 90);
    border: 1px solid rgb(50, 50, 50);
    border-radius: 0;
}
#planetMaintenance .btn_blue:hover, #abandonplanet #block:hover {
    background-image: none !important;
    background-color: rgb(110, 110, 110);
}
.build-faster {
    background-image: none;
    background-color: rgb(25,25,25);
    border: 0;
}
.build-faster:hover {
    background-image: none;
    background-color: rgb(50,50,70);
    border: 0;
}
}
