@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("board.ogame.org") {

body{background-color : #1D221E !important; color : #799776!important;}
table.tableoutborder{width : 94% !important;}
.tablea{background-image : url(http://img294.imageshack.us/img294/1537/tablebglr6.jpg) !important;background-repeat : repeat-x !important ; background-color : #292B28 !important ;}
.tableb{background-image : url(http://img294.imageshack.us/img294/1537/tablebglr6.jpg) !important;background-repeat : repeat-x !important ; background-color : #292B28 !important ;}
.tabletitle{background-image: url(http://img294.imageshack.us/img294/6015/catpictw6.png) !important; color : #BA6000 !important ;
background-color : #394539 !important}
.mainpage{background-color : #1D221E !important;border : 0px !important;}
.tablecat{background-image: url(http://img362.imageshack.us/img362/7950/aqwuw5.png) !important;color : #FFFFFF!important;background-color : #572502 !important; background-repeat : repeat-x !important ; }
.tablecat span.smallfont {color : #0B2334!important;  }
