css:
@-moz-document domain('2ch.hk') { 
  
  
  body {    
    padding: 0px;
    background: #edeef0 none repeat scroll 0 0;    
  }
  
  #adminbar {
    background: #466991 none repeat scroll 0 0;
    top: 0;
    width: 100%;
    z-index: 120;
    padding: 10px;
  }    
  
  #adminbar hr {
   display: none;
  }   
  
  #adminbar a {
    color: #fff;
    text-decoration: none;
  }
  
  article.postscontainer {
    border-radius: 2px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    background: #fff none repeat scroll 0 0;
  }
  
  .rmenu.desktop {
    padding-top: 13px !important;
  }
  
  .mainlogo, .welcomefriend, #SwitchStyles   { 
    display: none; 
  }   
  
  #adminbar .menu {
    width: 1000px;
    margin: 0 auto !important;;
  }
  
  .content a {
    color: #555 !important;;
  }
  
  .content a b {
    color: #9cb3cc !important;;
  }
   
  
  .content .boardsubj, .boardsubj span {
    color: #2a5885 !important;
  }
  

  .content .board-list-head .tr div  {
     background: #466991 none repeat scroll 0 0;
     color: white;
  }
}
