css:
@-moz-document domain("habrahabr.ru") {
body,
h1,
h2,
h3,
h4 {
    color: #A3BCD2;
}
body.nl .layout {
    background: #253648;
}
a {
    color: #44CCC0;
}
.icon-svg_geektimes,
.icon-svg_habrahabr {
    color: #A3BCD2;
}
#TMpanel .container {
    background: #314254;
}
#TMpanel .container .bmenu>a.current,
.dropdown-menu li a {
    color: #A3BCD2;
}
#TMpanel .container .bmenu>a {
    color: #44CCC0;
}
.post__title,
.post__title_link {
    color: #cae6ef;
}
.post__title_link:hover,
.post__title_link:visited {
    color: #cae6ef;
}
.sidebar-block {
    background: #314254;
    margin-bottom: 20px;
}
.html_format h1,
.html_format h2,
.html_format h3,
.html_format h4,
.html_format h5,
.html_format h6 {
    color: #fff;
}
.html_format code {
    color: #fff;
}
.post__flow,
.post__flow:hover,
.post__flow:visited {
    color: #44CCC0;
}
.top-flows__item-title,
.top-flows__item-title:hover,
.top-flows__item>a:visited {
    color: #44CCC0;
}
.top-flows__item-counter {
    color: #45fb6f;
}
.top-hubs__item-title,
.top-hubs__item-title:visited {
    color: #44CCC0;
}
body.nl .layout__navbar {
    border-bottom: 1px solid #314254;
}
.nav-links__item-link {
    color: #A3BCD2
}
.nav-links__item-link:hover {
    color: #cae6ef;
}
.author-info__name,
.post-author__link,
.post-author__name {
    color: #44CCC0;
}
.author-info__name:hover {
    color: #2f9a91!important;
}
.sidebar-block__title {
    border-bottom: 1px solid #253648;
}
.sidebar-block__footer {
    border-top: 1px solid #253648;
}
.sidebar-block_top-hubs {
    border-bottom: 3px solid #44CCC0;
    border-top: 5px solid #44CCC0;
}
.sidebar-block__title,
.similar-posts__title {
    color: #cae6ef;
}
.sidebar_right .block>.title {
    color: #cae6ef;
}
.sidebar_right .block .posts_list .post_item a.post_name {
    color: #A3BCD2;
}
.sidebar-block__footer a {
    color: #fff;
}
.btn_blue {
    background-color: #44CCC0;
}
.btn_blue:hover {
    background-color: #2f9a91!important
}
.buttons a.button,
.buttons button,
.buttons input[type=button],
.buttons input[type=submit] {
    color: #44CCC0;
    background-color: transparent;
    border: 1px solid #44CCC0;
    border-radius: 3px;
}
.buttons a.button:visited,
.buttons button:visited,
.buttons input[type=button]:visited,
.buttons input[type=submit]:visited,
a.habracut:visited {
    color: #fff;
}
.btn_flow-toggle {
    color: #cae6ef;
}
.tabs-menu_habrahabr {
    background-color: #314254;
}
.tab-item {
    color: #A3BCD2;
    border-right: 1px solid #253648;
}
a.tab-item_current,
a.tab-item:hover {
    background-color: #495f77;
    border-right: 1px solid #253648;
}
.menu_sub {
    background-color: #495f77;
}
.menu_sub .menu__item-link_current {
    color: #fff!important;
}
.page-header__title {
    color: #cae6ef;
}
.menu_sub .menu__item-link {
    color: #A3BCD2;
}
.tab-item_current .tab-item__value {
    color: #fff;
}
.counter_new {
    color: #45fb6f;
}
.tracker_page table.tracker_comments tr th {
    color: #cae6ef;
}
.postinfo-panel {
    background: #314254;
    border: 1px solid #253648;
}
.postinfo-panel__item_comments .post-comments {
    border-left: 1px solid #253648;
}
.voting-wjt.voting-wjt_infopanel {
    border-right: 1px solid #253648;
}
.views-count_post,
.favorite-wjt__counter {
    color: #cae6ef;
}
.profile-header .author-info__specialization {
    color: #A3BCD2;
}
#footer {
    border-top: 1px solid #314254;
    color: #cae6ef;
}
#footer a {
    color: #A3BCD2;
}
#footer dt a {
    color: #44CCC0;
}
.tracker_page table.tracker_comments tr td.comment_count a {
    color: #ff6f6f;
}
.buttons a.button.disabled,
.buttons a.button[disabled],
.buttons button.disabled,
.buttons button[disabled],
.buttons input[type=button].disabled,
.buttons input[type=button][disabled],
.buttons input[type=submit].disabled,
.buttons input[type=submit][disabled] {
    cursor: not-allowed;
    color: #4e879c;
    background-color: transparent;
    border-color: #65a3be;
}
.user_profile dt {
    color: #cae6ef;
}
.grey a,
a.grey {
    color: #fff;
}
.navigation-links__item-link {
    color: #44CCC0;
}
.hubs_list .hubs .hub .info .stat a {
    color: #A3BCD2;
}
.hubs_list .hubs .hub .common_tags {
    color: #A3BCD2;
}
.sidebar_right .block {
    background: #314254;
}
.companies .company .icon {
    background-color: #FFF;
}
.companies .company .description .name a {
    color: #cae6ef;
}
.companies .company .description {
    color: #A3BCD2;
}
.navigation-links__item-counter {
    color: #cae6ef;
}
.about_exchange .row .g-icon,
.for_authors_help .row .g-icon {
    color: #44ccc0;
}
form .item>label {
    color: #cae6ef;
}
.blue_buttons_panel {
    background: #314254;
}
.chosen-container-multi .chosen-choices .search-choice {
    background-color: #44CCC0;
}
.post-type-selector {
    background: transparent;
}
a.habracut {
    color: #4e879c;
    background-color: transparent;
    border-color: #65a3be;
}
.post__tags {
    color: #44CCC0
}
.similar-posts {
    background-color: #314254;
}
.post__time_published {
    color: #6e7b86
}
.post .hubs a {
    color: #6e7b86
}
.content-list_posts .content-list__item {
    border-top: 1px solid #253648;
}
.post-preview__rating {
    color: #45fb6f;
}
.tabs__menu .active {
    background: #495F77;
    color: #fff!important;
}
.menu__item-tab {
    color: #cae6ef!important;
}
.similar-posts__flow-link,
.similar-posts__flow-link:hover {
    color: #A3BCD2;
}
.column-wrapper_bordered {
    border-top: 1px solid #314254;
}
.html_format blockquote {
    border-left: 2px solid #44CCC0;
}
.post-type {
    background-color: #495F77;
    border: 0px;
}
.author-info__specialization {
    color: #cae6ef;
}
.post-type__value_author {
    color: #44CCC0;
}
.post-type__value_author:hover {
    color: #369a91;
}
.sidebar_right .block.mobile_applications .app_widget .app_title {
    color: #cae6ef;
}
.sidebar_right .block.mobile_applications .app_widget .description {
    color: #A3BCD2;
}
.sidebar_right .block .posts_list .post_item {
    color: #6e7b86;
}
.voting-wjt__counter_positive .voting-wjt__counter-score {
    color: #45fb6f;
}
.post-comments__link_new {
    color: #45fb6f!important;
}
.tabs__menu {
    background: #495F77;
}
.users .user .karma {
    color: #45fb6f;
}
.sidebar_right .block.people_location_cloud .tag-cloud a {
    color: #A3BCD2;
}
.sidebar_right .block.top_hub_invest table.users tr td.userinfo .username .bar {
    background: #495F77;
}
.sidebar_right .block.top_hub_invest table.users tr td.h_rating,
.top-users.top-user_rating tr td.h_rating {
    color: #A3BCD2;
}
.top-users.top-user_rating tr td.userinfo .username .bar {
    background: #495F77;
}
#people-tags li a {
    color: #A3BCD2;
}
.user_profile .note .add_note {
    color: #45fb6f;
}
.comment-item__username {
    color: #44CCC0;
}
.comments_list .comment_item .message.bad1 {
    background: none;
}
.comments_list .comment_item .message.bad2 {
    background: none;
}
.comments_list .comment_item .message.bad3 {
    background: none;
}
.comments_list .comment_item .message.bad4 {
    background: none;
}
.comments_list .comment_item .message.bad5 {
    background: none;
}
.promo-block {
    border: 1px solid #314254;
}
.content-list_promo .content-list__item {
    border-bottom: 1px solid #314254;
}
.promo-block__header {
    border-bottom: 1px solid #314254;
}
.promo-block__service {
    background: #495F77;
    border-left: 1px solid #495F77;
}
.icon_logo_freelansim:after,
.icon_logo_mk:after {
    color: #fff;
}
.promo-block__title-link,
.promo-block__title-link:hover {
    color: #cae6ef;
}
.content-list_promo .content-list__item-link {
    color: #44CCC0;
}
.vacancy__aside {
    color: #cae6ef;
}
.attrs__item {
    color: #A3BCD2;
}
.promo-block__footer {
    border-top: 0px;
    background: #495F77;
}
.promo-block__see-all {
    color: #cae6ef;
}
.promo-block__footer-link {
    color: #A3BCD2;
}
.live-broadcast {
    background: #314254;
}
.live-broadcast__title {
    color: #cae6ef;
}
.checkbox__label {
    color: #A3BCD2;
}
.content-list_feed .content-list__item-link {
    color: #44CCC0;
}
.live-broadcast__content {
    border-top: 1px solid #253648;
}
.sidebar_right .block.company_links .links .link a {
    color: #44CCC0;
}
.comment-item__status,
.comment-item__time_published {
    color: #6e7b86;
}
.comment-item.is_new {
    background: #314254;
}
.comment-item.is_topic_starter {
    background: #495F77;
}
.conversations .conversation {
    border-bottom: 1px solid #314254;
}

.conversations .conversation:hover{
    background:#314254;
}
.conversation_page .messages .message.new .info {
    background: #314254;
}
.conversation_page .messages .message .info .login a {
    color: #44CCC0;
}
.conversation_page .messages .message .info .time {
    color: #6e7b86;
}

.conversation_page .messages .message.my .info {
    background: #495F77;
}
}
