css:
@-moz-document domain("vk.com") {
.page_name {
	color: #FFFFFF;
	font-size: 19px;
	border: solid 4px #507299;
	border-radius: 10px;
	background-color: #305075;
	transition: 0.2s;
}
.page_name:hover {
	font-size: 23px;
	background-color: #223953;
	transition: 0.2s;
}
.people_cell_img:hover {
	border-radius: 5%;
	transition: 0.2;
}
.page_avatar_img {
	border-radius: 50px;
}
.post_header {
	background-color: #D9E0E7;
	border-top-left-radius: 10px;
	border-top-right-radius: 10px;
}
#side_bar ol {
	background: linear-gradient(to top,#C28FEF, #66CC33) rgba(0,0,0,0.95);
	border: solid 5px #507299;
	border-radius: 5px;
}
li .left_row:hover {
	opacity: 0.5;
}
.im-chat-input .im-chat-input--textarea>.flat_button{
	box-shadow: 0 0 10px rgba(94, 129, 168, 0.5);
	transition: 0.2s;
}
.im-chat-input .im-chat-input--textarea>.flat_button:hover{
	box-shadow: 0 0 20px rgba(94, 129, 168, 1);
	transition: 0.2s;
}
#side_bar #l_pr .left_icon {
	background: url(https://4.downloader.disk.yandex.ua/preview/62e150ccd67afbb7bda4f6e6e702d2cb68a40d94d91306bbd3f2547242eeb957/inf/MGc1lSUqG_obh9VVLOH8ZZuRKgtOEyFNc2dZaIlx51a3sox5r3LMYfRaDQvzq6DDXG0y9lHhzW-S2RKJAeU-zQ%3D%3D?uid=173730560&filename=menu_icon.png&disposition=inline&hash=&limit=0&content_type=image%2Fpng&tknv=v2&size=1280x846) no-repeat;
	background-position: 7px 6px;
}
body{
	background: #42648b;
	background-image:url(https://3.downloader.disk.yandex.ua/preview/fadc20dbe4714d96e0bbdb28e1315e8e410d990767b17385191be8a4d4a8f1f3/inf/MGc1lSUqG_obh9VVLOH8ZRxAryTZUt0F-LQ08FPc94I_Jjqyx_bHzb5gelvZ5LHzlVZSrsqRdwD4UXXt2-oxpw%3D%3D?uid=173730560&filename=%D0%91%D0%B5%D0%B7%20%D0%B8%D0%BC%D0%B5%D0%BD%D0%B8-3.png&disposition=inline&hash=&limit=0&content_type=image%2Fpng&tknv=v2&size=1280x846);
}
.left_menu_nav_wrap {
    line-height: 19px;
    padding: 14px 0 4px;
    font-size: 13px;
    text-align: left;
}
.left_menu_nav {
    padding-right: 10px;
    color: #fafbfc;
    white-space: nowrap;
}
#side_bar ol li .left_row:hover {
    background: rgb(148, 80, 224);
    text-decoration: none;
    border-radius: 2px;
	transition:0.3s;
}
#side_bar ol li .left_row {
    color: rgba(250, 251, 252, 0.82);
    border: 0;
    display: block;
    white-space: nowrap;
}
.wall_module .post_like_icon, .pv_like_icon, .mv_like_icon{
    background: url(https://2.downloader.disk.yandex.ua/preview/b979304178bb7edcd49fbba3bdf867e12e04e5aa3f3d416e48fd8394d8793360/inf/MGc1lSUqG_obh9VVLOH8ZbmFqrDoWq8GBgsZZTRw7qi2zmHxDWBksC_vTAUglv8vGQ7vESXBansHPY3OOqx2gg%3D%3D?uid=173730560&filename=%D0%91%D0%B5%D0%B7%20%D0%B8%D0%BC%D0%B5%D0%BD%D0%B8-4.png&disposition=inline&hash=&limit=0&content_type=image%2Fpng&tknv=v2&size=1280x846) no-repeat 0 0;
    display: inline-block;
    width: 17px;
    height: 16px;
    margin: 2px 0 1px;
    float: left;
    opacity: 0.4;
    filter: alpha(opacity=40);
}
#page_header_cont .back {
    position: absolute;
    width: 100%;
    background: linear-gradient(to top,#C28FEF, #66CC33) rgba(0,0,0,0.95);
    height: 42px;
    box-sizing: border-box;
}
.stl_active.over_fast #stl_bg {
    background-color: rgba(103, 203, 53, 0.46);
}
}
