css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stocktwits.com") {

body {
       background-color: Wheat !important;
	background-image: none !important;
	background-repeat: repeat-x;
}

a:hover   { color: Red !important ; }

.twitupdate
{
        background-color: Beige !important ;
	background-image: none !important ;
	background-repeat: no-repeat !important ;
        border:1px solid Tan !important ;
        margin-bottom:0px !important ; 
        margin-top: 5px !important ;
	height: auto !important;
	overflow: auto !important;
	width: auto !important;
}


.twitupdate li
{
	list-style: none !important;
        line-height: 35px !important ;
        display: block !important
}

/* POSTING BOX*/
div#twitpost 
{ 
  margin-left: 220px !important ; 
  margin-bottom: -5px !important;
  z-index:1000 !important;
	background-color: Tan !important;
	float: left;
        margin-top: 0px !important;
        padding: 10px !important;
	width: 620px !important;
}
div#length { display:none !important }
#postfield {margin: 0px !important ; padding: 0px !important }

/*REMOVE POST IT BUTTON */
input#twitter_update_submit 
{
   position: absolute !important ; 
   top: 80px !important ; 
   left: 220px !important 
}

span.topleft-stream {display:none !important}

p.feedthis {display:none !important}
div.rightbox {display:none !important ; }
div.leftbox h1 {display:none !important ; width: 0px !important}

div#footer {display:none !important}
div#cloud-inside {display:none !important}

#bottominner
{
	background-color: Wheat !important;
	float: left;
	margin: 0 auto;
	margin-top: 0px;
	min-height: 0px;
	padding-bottom: 0px;
	padding-top: 0px;
	width: 100% !important;
}

#wrapper
{
	background-color: Wheat !important;
	background-image: none !important;
	background-repeat: no-repeat;
	margin: 0 auto;
	width: auto !important;
}

#leftinner {background: Wheat !important ; width: 100% !important ; float: none !important }

.leftbox {
   background: none !important ; width: 100% !important ; 
}

li.profilepic {}
li.profilepic img {float:left !important ; width: 30px !important; height: 30px !important; border: 0px !important ; padding-right: 10px !important ; padding-top: 5px !important }

li.profilename {float:right !important}
li.profilename a {color: SaddleBrown !important }
li.entry {}
li.reply {display:none !important} 
span.bticker {display:none !important}

.ticker {color: IndianRed !important; font-size:30px !important;font-weight:bold !important ; text-transform: uppercase !important }

.financetalk {color:yellow !important ; display:none !important}


div#globalheader {position:absolute !important ; top: 5px !important ; left: 10px !important; }
div#globalheader img {margin-top: -20px !important ; width: 200px !important ; height: 95px !important; border: 0px !important}
div#globalcrop {display:none !important ; width: auto !important }

div#latestshadow {
   position:absolute  !important; 
   right: 10px !important; 
   top: 15px !important; 
   padding-right: 10px !important;  
   background-color: Beige !important; 
   z-index:1000  !important;
}
div#latestshadow li { display:inline  !important}
li.ltitle {color: Beige  !important}
.lsearch form { display:none  !important}
ul#latest a {
   border : 2px solid green  !important; 
   padding: 10px  !important; 
   color: blue !important ; 
   text-decoration: none !important; 
   background-color: Wheat !important
}

ul#latest a:hover {
   border : 3px solid green !important; 
   padding: 9px  !important; 
   color: blue !important ;  
   background-color: Beige  !important
}


span.topleft-stream {display:none !important}

.streamswrapper {width: 100% !important; background: none !important }
.streamscroll { background: none !important ; width: 100% !important }

#nav
{
	width: auto !important;
}
#cropnav
{
	width: auto !important;
}

#latestshadow
{
	background-image: none !important;
	background-repeat: no-repeat  !important;
	float: left;
}
#latest
{
	background-image: none !important;
	background-repeat: repeat-x  !important;
}

}
