css:
@-moz-document url("https://web.whatsapp.com/") {
body {
    background-image: url(https://i.imgur.com/bJj8eg2.jpg);
    background-size: cover;
    background-position: center;
}
.app-wrapper::after {
    display: none;
    z-index: -1;
}
.drawer-container-right,
.pane-chat,
.pane-intro {
    background-color: rgba(0, 0, 0, 0.0);
}
.app {
    background-color: rgba(0, 0, 0, 0.4);
    width: 100%;
    height: 100%;
    top: 0;
    border-radius: 0px;
}
html[dir] ._3dqpi {
    background-image: none;
    background-color: rgba(0, 0, 0, 0.13);
    background-position: top left;
    background-repeat: repeat-x;
    border-radius: 0;
    -webkit-transform-origin: center;
    transform-origin: center
}
.pane-chat,
.pane-intro {
    /*width: 80%;*/
}
.drawer-header {
    background-color: #4E718C;
}
.pane-intro:after {
    border-top: 6px solid #4E718C;
}
.app,
.app.three {
    width: 100%;
}
.drawer-container-panel,
.pane-info {
    width: 385px;
    left: 80%;
}
.three .pane-chat {
    width: 59.8%;
}
.intro-title {
    color: #fff;
    font-size: 36px;
    margin-top: 38px;
    content: "";
}
.intro-text {
    font-size: 14px;
    color: #fff;
}
.intro-image {
    width: 356px;
    height: 355px;
    background-image: url("http://i.imgur.com/h96Go9K.png");
}
#wrapper:before {
    display: none;
}
html[dir] ._3qlW9 {
    background-color: rgba(255, 255, 255, 1);
}
html[dir] ._1GX8_ {
    background-color: rgba(0, 0, 0, 0);
}
html[dir] ._1FKgS::after {
  background-color: rgba(0, 0, 0, 0);
  }

.app-wrapper::after {
    position: fixed;
    z-index: -1;
    background-color: #ffffff;
    width: 0%;
    height: 127px;
    content: '';
    top: 0;
    left: 0;
}
.pane-intro:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    border-top: 6px solid #58e870;
    width: 0%;
    height: 0;
}
*::-webkit-scrollbar-thumb {
    background-color: #b3b3b3
}
}
