css:
@-moz-document domain("www.twitch.tv") {
.prime-offers,
.video-chat__header,
.stream-chat-header,
.channel-leaderboard,
.video-chat__message-menu,
.video-chat__input,
.rooms-header,
/*.user-notice-line:not(.user-notice-line--highlighted),*/
.vod-message__header,
.tw-button-icon[aria-label="Bits"],
.hype-train-progress-bar-info-view__left,
.hype-train-progress-bar-info-view__progress-value,
.jDaxrs,
.chat-badge,
.ffz-badge,
.chat-input__badge-carousel,
.bnsqjT, /* points float animation */
.mod-icon[data-test-selector="chat-ban-button"],
.ffz-mod-icon[data-action="ban"],
.ffz-mod-icon[data-action="msg_delete"],
.ScCoreButton-sc-1qn4ixc-0[href^="/moderator/"],
.ffz--points-line,
.qORKA,
.GTGMR,
.krJuwP,
.dNXiq > .gzJsTf {
    display: none !important;
}
.chat-line__message span,
.vod-message a,
.vod-message span,
.tw-textarea,
.chat-line__message--link,
.tw-inline-flex {
    font-size: 17px !important;
}
.chat-line__message--link,
.mentioning {
    word-break: break-all !important;
}
span.chat-author__display-name {
    font-weight: 100 !important;
    word-break: keep-all !important;
    display: inline-flex !important;
    max-width: 13rem !important;
    overflow: hidden !important;
    overflow-wrap: normal !important;
}
.chat-line__message,
div.vod-message {
    padding: 0rem 0.3rem 0.6rem 0.7rem !important;
}
.simplebar-scrollbar {
    width: 2px !important;
}
.chat-badge[alt^="Подписчик"],
.chat-badge[alt~="Subscriber"],
.chat-badge[alt$="подписки"],
.chat-badge[alt^="Основатель"],
.chat-badge[alt="Модератор"],
.chat-badge[alt="Moderator"],
.chat-badge[alt="Владелец канала"],
.chat-badge[alt="Broadcaster"],
.chat-badge[alt^="Predicted"],
.chat-badge[alt="Verified"],
.ffz-badge[data-badge="moderator"],
.ffz-badge[data-badge="subscriber"],
.ffz-badge[data-badge="founder"],
.ffz-badge[data-badge="broadcaster"],
.ffz-badge[data-version="pink-2"],
.ffz-badge[data-version="blue-1"],
.ffz-badge[data-badge="partner"]
{
    display: inline-block !important;
    padding-left: 3px !important;
    height: 16px !important;
    width: 3px !important;
}
img.chat-badge[alt^="Подписчик"],
img.chat-badge[alt$="подписки"],
img.chat-badge[alt~="Subscriber"],
img.chat-badge[alt^="Основатель"],
.ffz-badge[data-badge="subscriber"],
.ffz-badge[data-badge="founder"] {
    background-color: #ffb100 !important;
}
img.chat-badge[alt="Модератор"],
img.chat-badge[alt="Moderator"],
.ffz-badge[data-badge="moderator"] {
    background-color: #14b866 !important;
}
.ffz-badge {
    background-image: revert !important;
    background-size: 0 !important;
    background-repeat: no-repeat;
    min-width: revert !important;
}
img.chat-badge[alt="Владелец канала"],
img.chat-badge[alt="Broadcaster"],
.ffz-badge[data-badge="broadcaster"] {
    background-color: #fc3636 !important;
}
img.chat-badge[src^="https://static-cdn.jtvnw.net/badges/v1/4b76d5f2-91cc-4400-adf2-908a1e6cfd1e"],
.ffz-badge[data-version="pink-2"] {
    background-color: #ff6cc9 !important;
}
img.chat-badge[src^="https://static-cdn.jtvnw.net/badges/v1/e33d8b46"],
.ffz-badge[data-version="blue-1"] {
    background-color: #387aff !important;
}
img.chat-badge[alt="Verified"],
.ffz-badge[data-badge="partner"] {
    background-color: #9C1DC7 !important;
    /* #910cbe #9146ff*/ 
}
.simplebar-content {
    padding-bottom: 10px !important;
}
.channel-page__right-column,
.video-watch-page__right-column,
.right-column--theatre,
.right-column--theatre .channel-root__right-column {
    width: 270px !important;
}
div.channel-page__video-player--theatre-mode,
.persistent-player--theatre {
    width: calc(100% - 270px) !important;
}
div.chat-line__message.bttv-highlighted {
    background-color: #0d0857d1 !important
}
.pd-b-2.pd-x-2 {
    padding: 0 1em 1em !important;
}
.viewer-card-layer {
    width: 270px !important;
}
.viewer-card-layer.relative {
    position: static !important;
}

.video-chat__sync-button {
    width: 230px !important;
    font-size: 11px !important;
}
div.whispers.whispers--theatre-mode {
    right: 27rem !important;
}
.tw-balloon--md,
.reward-center__content {
    width: 24rem !important;
}
div.emote-picker__content-block {
    padding: 0.5rem !important;
}
div.chat-input {
    padding: 0 0.7rem !important;
}
.video-chat {
    flex-basis: 270px !important;
}
.right-column--theatre .chat-shell__expanded {
    min-width: 270px !important;
}
.tw-button-icon--secondary {
    width: 2rem !important;
}
textarea.tw-textarea {
    padding: 0.1rem 0.5rem 0 0.5rem !important;
    line-height: 1.5 !important;
}

.tw-textarea::-webkit-scrollbar {
    width: 10px;
}
.chat-input__buttons-container {
    margin-top: 0.2em !important;
    margin-bottom: 0.2em !important;
}
.chat-input__buttons-container .ScCoreButton-sc-1qn4ixc-0,
.chat-input__input-icons .ScCoreButton-sc-1qn4ixc-0,
#emote-menu-button,
.sc-fznWqX  {
    height: 25px;
}
.chat-input__input-icons .ScCoreButton-sc-1qn4ixc-0 {
    width: 20px;
}
.chat-input__input-icons {
    padding-right: 0.2em;
}
.tw-tooltip-wrapper.inline {
    display: inline !important;
}
.video-chat__message .tw-pd-x-05 {
    padding-left: 0.1rem !important;
}
#bttv-highlighted {
    background-color: #680d0d !important;
}
.tw-c-text-base {
    color: #e0e0e0!important;
}
.user-notice-line {
    padding: 0 !important;
    border-left: 0 !important;
    padding-left: 0.7rem !important;
    padding-right: 0.3rem !important;
}
.chat-line__message-body--highlighted {
    border-width: 0 0 2px 0 !important;
    background: none !important;
}
.user-notice-line .chat-line__message {
    padding: 0 !important;
}
.mod-icon[data-test-selector="chat-delete-button"] {
    display: none !important;
}

.tw-core-button-label {
    padding: 2px !important;
}
.community-highlight,
.hype-train-progress-bar__container {
    min-height: 0 !important;
}
.video-player__container--theatre-whispers {
    bottom: 0 !important;
}
.chat-line__status,
.chat-line__message .chat-line__message--deleted span:nth-child(2)  {
    font-size: 10px !important;
    line-height: 10px !important;
    padding: 0.1rem 0.7rem !important;
}
.viewer-card-layer__draggable {
    left: -100px;
}
.tw-mg-05 {
    margin: 0 !important;
}
.tCXbA,
.cafLeC  {
    margin: 0 !important;
}
.jPJPAu,
.iipfuT {
    padding: 0 !important;
}
.chat-line__username-container>span {
    display: inline-block !important;
}
.hype-train-progress-bar-info-view__timer {
    margin: 0 !important;
}
/*.bttv-tooltip-wrapper > .chat-badge {
    margin-right: -2px !important;
}*/
.chat-line__message--emote {
    max-width: 56px !important;
    /* max-height: 28px !important; */
}
.hIoXds {
    margin: 0 !important;
}
}
