css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("sufficientvelocity.com") {
  .adv_tabs_wrapper {
    font-size: 12pt;
  }

  .adv_accordion {
    border-top: 1px solid #2096e1;
    border-bottom: 1px solid #2096e1;
    margin-left: 0 !important;
  }
  .adv_accordion {
    font-size: 12pt;
    border: 1px solid rgb(7,59,110);
    margin-left: 0 !important;
  }
  .adv_accordion dt:hover, .adv_accordion dt.AdvSlideActive { /* Hover over "box" */
    background: rgb(226,228,229) /*url('styles/flexile-dark/xenforo/gradients/category-23px-light.png') repeat-x top*/;
    color: black; /* Font now black on white/gray instead of white. */
  }
  .adv_accordion dt {
    font-variant: small-caps;
    font-weight: bold;
    font-size: 13px;
    color: rgb(0,0,0);
    background: rgb(78,78,78) /*url('styles/flexile-dark/xenforo/gradients/category-23px-light.png') repeat-x top*/;
    padding: 6px;
    /*
    border-right: 1px solid #2096e1;
    border-bottom: 1px double rgb(30,31,32);
    border-left: 1px solid #2096e1;
    */
    border: 1px solid rgb(7,59,110);
    cursor: pointer;
    position: relative;
    *zoom: 1;
  }
  .adv_accordion dd {
    background-color: rgb(42,42,42);
    padding: 6px;
    /*
    border-right: 1px solid #2096e1;
    border-left: 1px solid #2096e1;
    */
    border: 1px solid rgb(7,59,110);
    margin-left: 0 !important;
    position: relative;
  }
  .adv_accordion dt:hover, .adv_accordion dt.AdvSlideActive { /* Expanded box "idle"/not hovered. */
    background: rgb(78,78,78) /*url('styles/flexile-dark/xenforo/gradients/category-23px-light.png') repeat-x top*/;
  }
}
