css:
@-moz-document url-prefix("https://share.dmhy.org/") {
/*顽固广告*/
.main > div:first-child, .main > div:last-child {
    display: none !important;
}

.headerleft, .headerright, .inner, .footer, .kiwi-ad-wrapper-950x80, #zds_ad, #shareman_ad, #id01_24413, #id01_50333, #id01_370164, #msdl_ad, #qyy_ad, #sbj_ad, #m_ad, #before-comment {
    display: none !important;
}

.container {
    background: #EEE;
}

ins, iframe {
    display: none !important;
}

/*头部主体*/
.bg {
    width: 1300px;
    margin: 0 auto;
    background: rgba(0, 0, 0, 0);
}

/*导航条主体部分*/
.container>.bg>.header {
    height: 220px;
    margin: 0 0 20px 0;
    border-bottom-left-radius: 15px;
    border-bottom-right-radius: 15px;    /*background: #FFF;*/
    background: url(http://www.nuthx.com/dmhy.jpg);
    background-size: cover;
    box-shadow: 0 0 5px rgba(53, 53, 53, 0.3);
}

.top_sort {
    position: absolute;
    height: 35px;
    width: 1300px;
    margin: 185px 0 0 0;
    background: rgba(44, 62, 80, 0.8);
    border-bottom-left-radius: 15px;
    border-bottom-right-radius: 15px;
}

.top_sort .menu {
    width: 1160px;
    height: 35px;
    background: rgba(0, 0, 0, 0);
}

.top_sort .menu ul {
    line-height: 35px;
}

.top_sort .menu ul li {
    width: 145px;
    height: 35px;
}

.top_sort .menu ul li a {
    font-size: 14px;
    color: #FFF !important;
}

.top_sort .menu ul li a font {
    color: #FFF !important;
}

.top_sort .menu ul li a:hover {
    background: rgba(0, 0, 0, 0.3);
}

.top_sort>.menu>ul>li:first-child>a:hover {
    border-bottom-left-radius: 15px;
}

.top_sort .menu ul li ul {
    border-radius: 15px;
}

.top_sort .menu ul li ul li {
    width: 145px;
    height: 35px;
    background: rgba(44, 62, 80, 0.8) !important;
}

.top_sort .menu ul li ul li a {
    background: rgba(0, 0, 0, 0) !important;
}

.top_sort .menu ul li ul li a:hover {
    background: rgba(0, 0, 0, 0.35) !important;
}

/*导航条右侧可展开部分*/
#expand-button {
    width: 140px;
    height: 35px;
    background: rgba(0, 0, 0, 0);
}

#expand-button:after {
    content: "字幕组";
    position: absolute;
    width: 140px;
    height: 35px;
    top: 0;
    right: 0;
    font-size: 14px;
    line-height: 36px;
    text-align: center;
    color: #FFF !important;
}

#expand-button:hover {
    border-bottom-right-radius: 15px;
    background: rgba(0, 0, 0, 0.3);
}

#team-expand, #team-collapse {
    background: rgba(44, 62, 80, 0);
    width: 140px;
    height: 35px;
    margin: 0;
    border-bottom-right-radius: 15px;
}

#team-dialog {
    margin: 55px 0 0 0px;
    width: 1258px;
    padding: 17px 20px 17px 20px;
    border-radius: 12px;
    background: #FFF;
    border: 1px solid #2C3E50;
    box-shadow: 0 0 22px rgba(53, 53, 53, 0.35);
}

#team-dialog div:first-child {
    display: none;
}

#team-inner {
    width: 1300px !important;
}

#team-dialog li {
    width: 120px;
    height: 28px;
    border: 0;
    margin: 3px;
    padding: 0px;
    font-size: 10px;
    background: #3E5368;
}

#team-dialog li a {
    color: #FFF;
    line-height: 28px;
}

/*搜索框*/
.quick_search {
    position: absolute;
    width: 1300px;
    height: 35px;
    top: -1px;
    margin: 1px 0 0 0;
    padding: 0;
    border: 0;
    background: rgba(44, 62, 80, 0.77);
}

.quick_search input {
    float: left;
    outline: none;
}

.quick_search .quick_input {
    width: 380px;
    height: 35px;
    margin: 0;
    border: 0;
    padding: 0 12px 0 12px;
    font-size: 14px;
    color: #FFF;
    background: rgba(0, 0, 0, 0);
}

.formButton {
    width: 90px;
    height: 35px;
    margin: 0;
    border: 0;
    background: rgba(240, 57, 76, 0.7);
    cursor: pointer;
    font-size: 14px;
    color: #FFF;
}

.formButton:hover {
    background: rgba(240, 57, 76, 0.88);
}

.quick_search>form>a {
    float: left;
    width: 90px;
    height: 35px;
    margin: 0 -2px 0 0;
    border: 0;
    background: rgba(0, 0, 0, 0);
    cursor: pointer;
    font-size: 14px;
    line-height: 37px;
    color: #FFF;
}

.quick_search>form>a:hover {
    text-decoration: none;
    background: rgba(0, 0, 0, 0.3);
}

.quick_search>form>a>span {
    display: none;
}

#AdvSearch {
    margin: -14px 0 0 584px;
    font-size: 14px;
    background: rgba(0, 0, 0, 0.3);
}

#AdvSearch p {
    line-height: 35px;
    color: #FFF;
}

#AdvSearch p:nth-child(4) {
    display: none;
}

.main {
    padding: 0 0 50px 0;
    background: rgba(0, 0, 0, 0);
}

/*下拉框*/
#AdvSearch select {
    margin-top: -3px;
    height: 35px;
    border: 0;
    color: #F0394C;
    font-size: 14px;
    background: rgba(0, 0, 0, 0);
    -webkit-appearance: none;
}

#AdvSearch p:nth-child(1) select, #AdvSearch p:nth-child(2) select {
    width: 80px;
}

#AdvSearch p:nth-child(3) select {
    width: 120px;
}

/*每日新番框*/
#mini_jmd {
    width: 1298px !important;
    margin: 0 0 20px 0;
    padding: 0;
    border-radius: 14px;
    background: #FFF;
    border: 1px solid #2C3E50;    /*border:1px solid #C6C6C6;*/
    box-shadow: 0 0 4px rgba(53, 53, 53, 0.1);
}

#mini_jmd>.nav_title {
    height: 20px;
    background: #2C3E50;
    color: #FFF;
    font-size: 14px;
    line-height: 16px;
    padding: 10px 0 4px 13px;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
}

#mini_jmd>.nav_title>a {
    color: #FFF;
}

#mini_jmd>.nav_title>span {
    display: none;
}

.jmd_base {
    margin: 10px 0 10px 0;
    border-spacing: 3px 0;
}

.jmd_base>tbody>tr {
    background: #FFF;
}

.jmd_base>tbody>tr>th {
    width: 100px;
}

.jmd_base>tbody>tr>td>a {
    margin: 3px;
    padding: 8px;
    font-size: 12px;
    color: #FFF;
    display: inline-block !important;
    background: #3E5368;
}

.jmd_base>tbody>tr>td>a:hover {
    text-decoration: none;
    background: #2C3E50;
}

/*开启全部日期*/
.jmd {
    display: none !important;
}

.jmd_base {
    display: block !important;
}

/*番剧列表*/
.table {
    width: 1298px !important;
    margin: 0 0 -25px 0;
    padding: 0;
    border-radius: 14px;
    background: #FFF;
    border: 1px solid #2C3E50;    /*border:1px solid #C6C6C6;*/
    box-shadow: 0 0 4px rgba(53, 53, 53, 0.1);
}

.clear>.nav_title {
    height: 20px;
    background: #2C3E50;
    color: #FFF !important;
    font-size: 14px;
    line-height: 16px;
    padding: 10px 13px 4px 13px;
}

.table>.clear>.nav_title {
    border-bottom-left-radius: 12px;
    border-bottom-right-radius: 12px;
}

.clear>.nav_title>a {
    color: #FFF;
}

.clear>table {
    border-spacing: 0;
}

.table>.nav_title {
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
}

.table>.nav_title>.fl>a, .table>.nav_title>.fr>a {
    color: #FFF;
}

.table>.nav_title>.fr>a:nth-child(3) {
    display: none !important;
}

table.tablesorter thead tr .headerSortDown, table.tablesorter thead tr .headerSortUp {
    background: #2C3E50;
}

.tablesorter>thead>tr>th {
    height: 20px;
    font-size: 14px;
    background: #34495E;
}

.tablesorter>thead>tr>th:hover {
    background: #2C3E50;
}

.even>td {
    background: #EEE !important;
}

table.tablesorter tbody td {
    padding: 10px 8px;
}

/*"粘贴分享"框隐藏*/
body>div>div>div.main>div:nth-child(11)>div:nth-child(1)>a>img {
    display: none !important;
}

/*主页和详情页底部多余信息隐藏*/
.bg>div:nth-child(4), .bg>div:nth-child(5), .bg>div:nth-child(6) {
    display: none !important;
}

/*主页底部多余信息隐藏*/
.main>.main>div:nth-child(11), .main>div:nth-child(14), .main>div:nth-child(15) {
    display: none;
}
}

@-moz-document url-prefix("https://share.dmhy.org/topics/view/") {
#mini_jmd, .user-sidebar, #recent-commnet {
    display: none;
}

.topics_bk {
    padding: 0;
    border: 0;
    margin: 0;
    background: rgba(0, 0, 0, 0);
}

/*发布信息*/
.topic-main>.box:nth-child(1) {
    position: absolute;
    width: 1298px;
    height: 357px;
    margin: 0;
    padding: 0px;
    border-radius: 14px;
    background: #FFF;
    border: 1px solid #2C3E50;
    box-shadow: 0 0 4px rgba(53, 53, 53, 0.1);
}

.topic-title h3 {
    height: 20px;
    background: #2C3E50;
    color: #FFF;
    font-size: 14px;
    line-height: 16px;
    padding: 10px 0 4px 13px;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
}

.topic-main>.box:nth-child(1)>div {
    margin: -10px 0 0 15px;
    float: left;
}

.topic-main>.box:nth-child(1)>div>ul {
    margin: 0;
}

.topic-main>.box:nth-child(1)>div>ul>li {
    margin: 10px;
    font-size: 13px;
}

.topic-main .share {
    display: none;
}

.topic-main>.box:nth-child(1)>div>ul>li:nth-child(6) {
    display: none;
}

#JN_ad {
    display: none;
}

/*下载列表*/
.topic-main .resource-detail {
    position: absolute;
    width: 1055px;
    top: -1px;
    margin: 268px 0 0 220px;
    border: 0;
    background: rgba(0, 0, 0, 0);
}

.ui-tabs .ui-tabs-nav {
    display: none;
}

.ui-tabs .ui-tabs-panel {
    padding: 0;
    border: 0;
}

.ui-tabs .ui-tabs-panel p {
    overflow: hidden;
    white-space: nowrap;
    margin: 10px;
}

.ui-tabs .ui-tabs-panel hr {
    display: none;
}

.file_list {
    position: absolute;
    top: -1px;
    width: 1254px;
    height: 144px;
    margin: 156px 0 0 -207px;
    padding: 0;
    background: rgba(0, 0, 0, 0);
    border: 2px solid #C6C6C6;
    border-radius: 8px;
}

.file_list ul li {
    padding: 8px;
}

.even {
    background: #EEE !important;
}

.warn {
    display: none !important;
}

/*文件信息*/
.topic-main>.box:nth-child(2) {
    width: 1298px;
    margin: 377px 0 20px 0;
    padding: 0px;
    border-radius: 14px;
    border: 1px solid #2C3E50;
    background: #FFFFFF;
    box-shadow: 0 0 4px rgba(53, 53, 53, 0.1);
}

.topic-main>.box:nth-child(2) strong {
    height: 20px;
    color: #222222 !important;
    font-size: 15px;
    line-height: 55px;
    padding: 30px 15px 9px 17px;
}

.topic-main>.box:nth-child(2) p {
    margin: 4px 0 0 0 !important;
    padding: 15px !important;
}

.main {
    padding: 0 0 7px 0;
}
}
