css:
@-moz-document url-prefix("https://www.shogi.or.jp/") {
/**************/
/* styles.css */
/**************/
/* main */

html {
    font-size: 12px !important;
}
/* link style */

a {
    color: #333333;
}
/* base style */

body {
    min-width: 860px;
}
#wrapper {
    background: none !important;
}
#container {
    padding-bottom: 10px;
}
/* gheader set*/

#gheader {
    background: none !important;
    margin-bottom: 30px;
}
#gheader:after {
    background: none !important;
    height: 30px;
    bottom: -30px;
}
#gheader .headerUtilityNav li {
    font-size: 1.000rem;
}
#gnav > ul > li {
    height: 36px;
    /* added */
}
#gnav > ul > li > span {
    height: 36px;
}
#gnav > ul > li .innerNav {
    height: 36px;
}
#gnav > ul > li .innerNav ul li {
    height: 36px;
}
#gnav > ul > li .innerNav ul {
    height: 36px;
    /* added */
}
#gnav > ul > li .innerNav ul li {
    height: 36px;
    /* added */
}
#gnav > ul > li .innerNav ul li span {
    height: 36px;
    /* added */
}
/* topicpath set */

.topicpath {
    margin: 0px;
    background: none !important;
}
.topicpath ul li {
    font-size: 1.000rem;
}
/* aside set */

#aside .asideParts01 dl {
    background: none !important;
}
#aside .asideParts02 dl dt {
    padding: 10px 10px 0px;
}
/* footer set */

.footerMenuAreaWrap {
    background: none !important;
}
.footerMenuAreaWrap #footerMenuArea {
    padding: 10px 0 10px;
}
.footerMenuAreaWrap #footerMenuArea .footerMenuAreaInner01 dl {
    margin-bottom: 10px;
}
.footerMenuAreaWrap #footerMenuArea .footerMenuAreaInner02 .snsArea {
    padding: 10px 7px;
    margin-bottom: 8px;
}
.footerMenuAreaWrap #footerMenuArea .footerMenuAreaInner02 .footerMenuList01 li {
    font-size: 0.875rem;
}
#gfooter {
    padding: 15px 0 20px;
}
#gfooter .pageTop {
    bottom: 10px;
}
#gfooter p {
    font-size: 0.875rem;
}
#gfooter .copy {
    font-size: 0.875rem;
}
/* section style */

.section01 {
    margin-bottom: 5px;
}
.section02 {
    margin-bottom: 10px;
}
.section03 {
    margin-bottom: 15px;
}
.section04 {
    margin-bottom: 20px;
}
.section05 {
    margin-bottom: 25px;
}
.section06 {
    margin-bottom: 30px;
}
.section07 {
    margin-bottom: 35px;
}
.section08 {
    margin-bottom: 40px;
}
.section09 {
    margin-bottom: 45px;
}
.section100 {
    margin-bottom: 50px;
}
.boxElementsA01 {
    padding: 5px 5px 5px;
}
.boxElementsB01 {
    padding: 5px 5px 5px;
}
.imgLayoutElementsA01 .image {
    padding: 0 5px 5px 0;
}
.imgLayoutElementsA02 .image {
    padding: 0 0 5px 5px;
}
.imgLayoutElementsB01 .image {
    padding-right: 10px;
}
.imgLayoutElementsB02 .image {
    padding-left: 10px;
}
/* heading style */

.pageHeadingElementsA01 {
    font-size: 1.250rem;
    padding: 5px 25px;
    margin-bottom: 5px;
}
.headingElementsA01 {
    font-size: 1.000rem;
    padding: 5px 10px;
    margin-bottom: 0px;
}
.headingElementsA01.ico01 {
    padding-left: 10px !important;
}
.headingElementsA01.ico01:after {
    background: none !important;
    left: 5px;
}
.headingElementsA01.ico03:after {
    top: 5px;
}
.headingElementsA01.ico05 {
    padding-left: 35px !important;
}
.headingElementsA01Area .headingA01Parts01 {
    margin-bottom: 5px;
}
.headingElementsA01LayoutArea .headingElementsA01LayoutInner01 {
    padding-right: 5px;
}
.headingElementsB01 {
    padding: 10px 10px;
}
.headingElementsC01 {
    padding: 4px 10px 0;
}
/* column style */
/* table elements */

.tableElements01 thead th {
    border-left: 1px solid #c9c9c9;
    padding: 2px 7px;
}
.tableElements01 tbody th {
    padding: 2px 7px;
}
.tableElements01 tbody td {
    padding: 2px 7px;
    border-left: 1px solid #c9c9c9;
    /* added*/
}
.tabElements01 .tabNav01 + .btn a {
    height: 50px;
    padding: 5px;
    font-size:1.500em;
}
.tableElements02 tbody th {
    padding: 2px 7px;
}
.tableElements02 tbody td {
    padding: 2px 7px;
}
.tableElements03 thead tr th {
    text-align: center;
    /* added */
    
    border-left: 1px solid #c9c9c9;
    /* added */
    
    padding: 2px 7px;
}
.tableElements03 tbody td {
    padding: 2px 7px;
}
.tableElements03 tbody td[colspan="5"] {
    background: #e7e5e5;
}
.tableElements04 thead tr th {
    padding: 5px 5px;
}
.tableElements04 tbody td {
    padding: 5px;
}
.tableElements05 tbody th {
    padding: 5px 6px;
}
.tableElements05 tbody td {
    padding: 5px 6px;
}
.tableElements07 thead th {
    padding: 6.5px 1.5px;
}
.tableElements07 tbody td {
    padding: 6.5px 1.5px;
}
.tableElements08 thead th {
    padding: 6.5px 1.5px;
}
.tableElements08 tbody td {
    padding: 6.5px 1.5px;
}
/* list style */

.floatListA01Col2-20 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col2-40 li {
    padding: 0 0 15px 15px;
}
.floatListA01Col3-20 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col3-30 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col3-40 li {
    padding: 0 0 15px 15px;
}
.floatListA01Col4-30 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col4-40 li {
    padding: 0 0 15px 15px;
}
.floatListA01Col5-20 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col5-30 li {
    padding: 0 0 15px 15px;
}
.floatListA01Col5-40 li {
    padding: 0 0 20px 20px;
}
.floatListA01Col6-10 li {
    padding: 0 0 5px 5px;
}
.floatListA01Col6-20 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col6-30 li {
    padding: 0 0 15px 15px;
}
.floatListA01Col6-40 li {
    padding: 0 0 20px 20px;
}
.floatListA01Col7-10 li {
    padding: 0 0 5px 5px;
}
.floatListA01Col7-20 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col7-30 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col7-40 li {
    padding: 0 0 15px 15px;
}
.floatListA01Col8-20 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col8-30 li {
    padding: 0 0 10px 10px;
}
.floatListA01Col8-40 li {
    padding: 0 0 15px 15px;
}
/* pagingElements */

.pagingElements01 ul li a {
    width: 34px;
    height: 28px;
    padding: 4px 1px 4px;
    font-size: 1.000rem;
}
.pagingElements01 ul li span {
    padding-top: 14px;
}
/* tab Elements */

.tabElements01 .tabContentsA01 {
    padding: 10px 5px;
}
.tabElements01 .tabContentsA02 {
    padding: 0px 0;
}
/* news */

.articleArea .writerArea {
    padding: 15px;
}
.schoolDetail dl dd {
    padding: 0 0px 0px;
}
/********************/
/* lowerSetting.css */
/********************/

.listMenu .inner .text {
    padding: 0 0px;
}
.boxElementsB01.boxL {
    padding: 0px 0px;
}
.boxElementsB01.boxS {
    padding: 0px 0px;
}
/************/
/* home.css */
/************/

.home#wrapper {
    background: none !important;
}
.home#wrapper .homeBgBody01 {
    background: none !important;
}
.home .homeContents03 {
    padding: 4px;
}
.home .homeContents03 input.gsc-search-button,
.home .homeContents03 input.gsc-search-button:hover,
.home .homeContents03 input.gsc-search-button:focus {
    padding: 5px 31px;
}
.home .homeContents03 .tagWord {
    padding-top: 5px;
}
.home .homeContents03 .tagWord dd + dd {
    margin-left: 5px;
}
.home .homeContents06 .gameBnrArea {
    padding: 5px 5px;
    margin-bottom: 5px;
}
.home .homeContents06 .gameBnrArea .ttl {
    text-align: center;
    margin-bottom: 5px;
}
.home .homeContents06 .mediaInfo {
    margin: 5px 0;
}
}
