css:
@-moz-document url-prefix("http://www.animefreak.tv/") {
body, #page > div.wrapper {
    background: black !important;
}

#utilities, #header, #Reklam, #apDiv1, #footer, .rtecenter, .defaultblock .blockcontent iframe, .defaultblock h2, #a-filter42974, #primary .messages, #primary .help .help, li.menu-14602, #sitemission, #primary > div > div:nth-child(3), #page .wrapper .rsidebar .loginblock, #primary > div > div.defaultblock > div, #page > div.wrapper > div.lsidebar > div:nth-child(1) > div, #page > div.wrapper > div.lsidebar > div:nth-child(3) > div, #page > div.wrapper > div.rsidebar > div:nth-child(5) > div {
    display: none !important;
}

#primary > div > div > div.content > div {
    background-image: none !important;
}

.loginblock h2, tr.even
  {background: #161616 !important;}

tr.odd:hover, tr.even:hover {
  background: black !important;
  border: 1px white solid !important;}

.loginblock, .rsidebar .item-list ul li a:link, #primary .singlepage h1, #primary .singlepage .view.view-glossary.view-id-glossary.view-display-id-page.view-dom-id-2 .attachment.attachment-before .view.view-glossary.view-id-glossary.view-display-id-attachment.view-dom-id-1 .view-content .views-summary.views-summary-unformatted
  {color: gray !important;}

#primary .singlepage .node .content hr {background: gray !important;}

li a.active, #primary .singlepage thead th a
  {color: white !important;}

.loginblock h2, #primary .node .content .book-navigation a, .item-list ul li a, #primary .singlepage form legend a, #primary .singlepage h2.nodeTitle a, #primary .node .content a:link, #primary .singlepage p span span span, #primary .singlepage h2 span strong, #primary .singlepage p a span, #primary .singlepage .path p a:link, #primary .singlepage .view.view-glossary.view-id-glossary.view-display-id-page.view-dom-id-2 .attachment.attachment-before .view.view-glossary.view-id-glossary.view-display-id-attachment.view-dom-id-1 .view-content .views-summary.views-summary-unformatted a:link, #primary .singlepage div div table tbody tr td a
  {color: #d2d2d2 !important;}

#primary .content blockquote {border: 1px gray solid !important;}

th {border-bottom: none !important;}

tbody {border-top: none !important;}

#primary .singlepage h2.nodeTitle a,  #primary .singlepage .path p a:link:hover, tr.even, tr.odd
  {border: none !important;}

li a.active:hover, .rsidebar .item-list ul li a:link:hover, .item-list ul li a:hover,  #primary .singlepage .path p a:link:hover, #primary .singlepage .view.view-glossary.view-id-glossary.view-display-id-page.view-dom-id-2 .attachment.attachment-before .view.view-glossary.view-id-glossary.view-display-id-attachment.view-dom-id-1 .view-content .views-summary.views-summary-unformatted a:link:hover, #primary .singlepage div div table tbody tr td a:hover
  {text-decoration: underline !important;}

.path p span
  {background: #161616 !important;
  color: #d2d2d2 !important;}

#submenu ul li a, .item-list .pager li.pager-current, .form-submit, #primary .node .content .multi {
  -moz-appearance: none !important;
  background: linear-gradient(#333, #000) !important;
  border: 1px #444 solid !important;
  color: #999 !important;
  box-shadow: unset !important;}

#submenu ul li a:hover, .item-list .pager li.pager-current:hover, .form-submit:hover, #primary .node .content .multi:hover
  {background: #222 !important;}

#searchbox {
    padding-top: 0px !important;
}

#header {
    height: 0px !important;
}

.book-navigation, .forum-topic-navigation, #primary > div > div.node > h2 > a {
    background: black !important;
}

#page > div.wrapper > div.rsidebar > div:nth-child(4) > div, #page > div.wrapper > div.lsidebar > div:nth-child(2) > div {
    background: none !important;
    background-color: black !important;
}
}
