/* freefeed.net ferfer style
V4.00 96/01/23
By : Mohammad Mirzaie
*/
@-moz-document domain("freefeed.net") {
    @font-face {
        font-family: Tahoma;
        font-weight: normal;
}
body{
    font-family: Tahoma;
    background: #EAEAEA;
    background-size: auto;
    background-attachment: fixed;
    float: none;
    direction: inherit;
}
    .sidebar .logged-in .avatar {
    float: left;
    margin-left: 14px;
}    
    .comments {
    direction:rtl !important;    
    font-size: 12.5px;
    line-height: 23px !important;
        
}    
.post .post-body {
    margin-right: 63px;
}
.post .post-body .post-text {
    direction: rtl !important;
    font-size: 13.5px;
    line-height: 23px !important;
}
.post .post-body .post-header {
    direction: rtl !important;
    font-size: 12.5px;
    line-height: 23px !important;
}
    .post-textarea, .comment-textarea{
    direction:rtl !important;   
}
    .post-author {
    direction:rtl !important;
}
    .pull-right {
    float: right !important;
}
    .post .post-userpic {
    float: right !important;
}
    
.posts {
    direction: rtl !important;
    font-size: 12.5px;
    line-height: 23px !important;
}
    
.content .box-body{
    background-color: #fff!important;
        background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}

.timeline-post-container {
    padding: 14px;
}
.post-body .likes .icon{
    float:right !important;
    font-size: 14px; 
    margin-left: 5px;
}
.post-lock-icon{
    float: right !important;
    margin-left: 5px;
}
.post-footer {
    direction: ltr;
    text-align: right;
}
.comment .comment-body {
    word-wrap: break-word;
    margin-left: auto;
    margin-right: 19px;
}
.comment .comment-icon {
    float: right !important;
}    
.comments .comment a.date {
    float: right !important;
}
.comment .comment-time {
    float: right !important;
}

.timeline-post-container {
    padding: 14px;
    border: 1px solid gray;
    border-radius: 10px;
    margin: 10px;}
.sidebar .box-body{
    padding: 10px 10px 3px 0;
}

.box-header, .box-header-empty, .box-header-timeline, .box-header-friends, .box-header-groups {
    border: none;
}

.sidebar .box-body {
    background-color: #FFFFFF;

    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
 
div.box-header-timeline {
    font-size: 16px;
}

li.previous a, li.next a {
    background-color: #d1d1d1;
    color: #000000;
}
.box-header-friends {
    font-weight: bold;
    font-size: 15px;
    background: #289628;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 5px;
}
.box-header-search {
    font-weight: bold;
    font-size: 15px;
    border: none;
    background: #289628;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 5px;
}
.box-header-groups {
    border: none;
    background: #FF8300;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 5px;
}
div.box-header-timeline {
    font-size: 16px;
    background: #5393D8;
    color: white;
    padding: 5px;
    direction: ltr !important;
    border-radius: 10px 10px 0 0;
    margin-left: 0px !important;
}

.col-md-3 *, .sidebar, box *, .search-input, .box-header-timeline{
    font-family: Tahoma;
}
}
