css:
@-moz-document domain("cokeandpopcorn.ch"), domain("cokeandpopcorn.eu"), domain("cokeandpopcorn.me"), domain("cokeandpopcorn.tk") {
body {
    background: url(http://i.imgur.com/nRjF8DN.jpg) #000 scroll left top !important;
}
#btmfree .rounded2 {
    visibility: collapse;
}
#btmfree .rounded2 div,
#btmfree .rounded2 p + a,
#btmfree .rounded2 p + a + a {
    visibility: visible;
    color: white;
}
#bodyBottom,
#footer,
#btmfree .rounded2 > p,
#btmfree .rounded2 > b,
#btmfree .rounded2 > span,
#btmfree .rounded2 > br,
#right > p:last-child,
#askForHelp,
#rightcol,
#left,
#header + div[align="center"],
#main h3,
#main #info,
.reportContainer,
div#body > div:last-child,
#shareEpisode,
#shareEpisode + br,
#shareEpisode + br + br,
#shareEpisode + br + br + div,
#offlineBox,
#titl,
#blank,
.light-cable,
.light-cable + a.light-switch,
#topfree,
#topfree2 {
    display: none !important;
}
div#right {
    margin: auto;
    float: none;
}
#right p.rightTop,
#right,
#right p.rightBottom {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
}
.announcement {
    background-color: rgba(0, 0, 0, 0.5);
    color: #83837d;
}
#right img.cover {
    border-top-color: rgba(255, 255, 255, 0.1);
    border-right-color: rgba(255, 255, 255, 0.1);
    border-bottom-color: rgba(255, 255, 255, 0.1);
    border-left-color: rgba(255, 255, 255, 0.1);
}
#right div.details div.title,
#right div.details,
#right div.banner728 div,
.midPage,
.midPage2,
.left,
.left2,
.left3,
.left4,
.left5,
.left6,
.left7,
.left8,
#rightcol1,
#rightcol2,
#rightcol3 {
    background-color: transparent !important;
    border-top-color: rgba(255, 255, 255, 0.1);
    border-right-color: rgba(255, 255, 255, 0.1);
    border-bottom-color: rgba(255, 255, 255, 0.1);
    border-left-color: rgba(255, 255, 255, 0.1);
    color: #83837d;
}
span.boxTitle,
h3 {
    border-bottom-color: #62625c;
}
#right div.rightText a {
    color: #eee;
}
#right div.rightText a:visited {
    color: #999;
}
#right div.rightText ul.tabs a {
    color: black;
}
}
