css:
@-moz-document domain("aquitaine.fr") {
.portal #portalPageBody {
    background-color: #000;
    min-height: 300px;
}
.portal.up.launcher .app-launcher-portlet .app-launcher-item .app-icon-wrapper .app-icon {
    border-radius: 5px;
    box-shadow: 0 0 0px 0px ;
}
.portal .portal-nav .menu li a:hover, .portal .portal-nav .menu li a:focus {
    background: #fff;
    color: #ff1769;
}
#region-header-right {
    padding-right: 32px;
    background-color: #11183c;
}
.portal.up.accueil #portalPageBody {
    background-color: #21183c;
    min-height: 300px;
    padding: 0 15px;
}
/*/*/
.portal .up-portlet-titlebar>h2 {
    color: #21183c;
    font-size: 1.4em;
    padding: 7px 7px;
    margin: 0;
    list-style: none;
    line-height: 20px;
    text-transform: uppercase;
    background : #d5d3dd
}
.portal .portal-nav {
    background: #6e1074;
    border-top: 0 solid transparent;
    border-bottom: 0 solid transparent;
    font-weight: bold;
}
.portal .up-gallery .gallery-inner {
    display: none;
    overflow: hidden;
    height: 200px;
    position: relative;
    background : #21183c
}
.portal #region-customize #customizeOptionsWrapper {
    margin-top: 1px;
    z-index: 800;
    background : #21183c
}
.portal .up-portlet-content-wrapper {
    background-color: #fff;
    border: 1px solid #302357;
    border: none;
    -webkit-border-bottom-right-radius: 0;
    -webkit-border-bottom-left-radius: 0;
    -moz-border-radius-bottomright: 0;
    -moz-border-radius-bottomleft: 0;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    overflow: hidden;
    padding: 10px;
}
.portal .portal-nav .menu .active a {
    background: #fff;
    color: #21183c;
    text-transform: uppercase;
}
html {
    margin: 0;
    padding: 0;
    background : #21183c
}
body, body#micro, #top-wrapper {
    background: #21183c;
}
a, a:visited, a:link, a:active {
    color: #f8f;
    text-decoration: none;
}
.mask {
    background-color: rgba(33,24,60,0.9);
}
.blockinstance-header .title {
    border-bottom: 3px solid #302357;
    color : #6e1074
}
h1, h2, h3, h4, h5, h6 {
    color: #ff1769;
}
.sideblock {
    border-bottom: 1px solid #302357;
    background : #fbf8fd
}
.main-nav li a, .main-nav li a:link, .main-nav li a:visited, .main-nav li a:active {
    background: #6e1074;
    color: #FFFFFF;
    font-weight: normal;
    padding: 10px 18px;
}
.main-nav li.selected a, .main-nav li.selected a:link, .main-nav li.selected a:visited, .main-nav li.selected a:active {
    color: #21183c;
    background: #FFFFFF;
    font-weight: bold;
}
#allonline a {
    color: #302357;
}
.home-info-box {
    background-color: #aca7bb;
    border: 1px solid #827b9a;
    border-radius: 8px;
    box-shadow: none;
}
.home-info-box h2.title {
    color: #21183c;
}
.home-info-box p {
    margin: 0;
    color: #f8fbfd;
}
.r0, .r0 td, .d0 {
    background-color: #f8fbfd;
}
.subpage {
    border-color: #21183c;
}
ul.in-page-tabs li a, ul.in-page-tabs li a:link, ul.in-page-tabs li a:visited, ul.in-page-tabs li a:active, ul.in-page-tabs li input.linkbtn, ul.in-page-tabs li button.linkbtn {
    color: #FFFFFF;
    background: #21183c;
    border: 1px solid #21183c;
}
ul.in-page-tabs li a.current-tab, ul.in-page-tabs li a.current-tab:link, ul.in-page-tabs li a.current-tab:visited, ul.in-page-tabs li a.current-tab:active, ul.in-page-tabs li input.current-tab, ul.in-page-tabs li button.current-tab {
    color: #21183c;
    background-color: #FFFFFF;
    border-bottom: 1px solid #FFFFFF;
}
input.text, input.password, input[type="text"], input[type="password"], input.text:focus, input.password:focus, input[type="text"]:focus, input[type="password"]:focus, #container .mce-container.mce-tinymce, .resizable-textarea {
    border-color: #21183c;
}
#header h1#app-name {
    clear: both;
    color: #FFFFFF;
    font: normal 400 2.8em/2.5em Verdana,"Times New Roman", serif;
    padding: 0 0 0 0px;
    text-align: center;
    text-transform: uppercase;
    background: #6e1074;
}
.warn {
    color: #fff;
    font-size: 1.3em;
    border: 2px solid #635d76;
    height: 30px;
    width: 568px;
    background: url(https://lea.aquitaine.fr/images/attention.png) no-repeat #635d76;
    background-position: 15px 1px;
    padding: 5px 10px 5px 60px;
    position: absolute;
    top: 372px;
    margin: 0 auto;
}
#winstructions {
    color: #21183c;
    font-size: 1.7em;
    font-weight: bold;
    margin: 0 0 0.5em;
    border-top: none;
    cursor: auto;
}
#content .idpsList {
    border: 2px dotted #a19dac;
}
#cas #wayf {
    background: url(http://img4.hostingpics.net/pics/613024cnxorange.jpg) no-repeat transparent;
    font-family: 'Roboto', sans-serif;
    float: none;
    min-height: 15em;
    position: relative;
    margin: 0 auto;
    height: 373px;
    width: 638px;
    top: calc((100% - 8em - 373px) / 2);
    padding-left: 14px;
    padding-top: 43px;
    z-index: 2;
}
.container h3, .container h4, .container h5, .container h6, .container .h4, .container .h5, .container .h6 {
    color: #a19dac;
}
.container h1 {
    color: #fbf8fd;
    text-transform: uppercase;
    margin-top: -4px;
}
#span_contacts {
    text-align: center;
    padding-top: 2em;
    position: absolute;
    left: 50%;
    margin-left: -115px;
    /* width: 230px; */
    margin-top: 10px;
    margin-bottom: 10px;
    font-size: 12px;
    color : #fbf8fd
}
#div_footer {
    border-top: 1px solid #fbf8fd;
    padding-top: 1em;
    font-size: 12px;
}
#span_contacts a {
    color: #fbf8fd;
}
#titre_version {
    font-family: "Oswald", "RobotoCondensedRegular", Arial, sans-serif;
    padding-top: 2em;
    font-size: 14px;
    float: right;
    color: #fbf8fd;
}
div.login {
    font-size: 1.5em;
    padding: 0;
    padding-left: 25px;
    margin: 0 0 10px 20px;
    color: #21183c;
    font-weight: bold;
}
.bouton {
    color: #fff;
    font-weight: bold;
    background-color: #21183c;
    border: none;
}
div.loginbox {
    background-color: #fbf8fd;
    position: relative;
    width: 90%;
    height: 90%;
    margin: 10 5% 10 5%;
}
#header {
    background-color: #21183c;
    min-height: 70px;
    margin: 0px;
    width: 100%;
    height: 25%;
    padding: 0px;
    background-image: url(https://idp.aquitaine.fr/idp/images/background-all-actus.jpg);
    background-repeat: no-repeat;
    background-position: 50% 50%;
}
div.loginbox div.rightpane {
    overflow: hidden;
    float: left;
    border: 2px #fbf8fd solid;
    position: relative;
    margin: 0 1.5% 0 1.5%;
    width: 50%;
    height: 90%;
}
div.loginbox div.leftpane {
    border: 2px #fbf8fd solid;
    position: relative;
    margin: 0 1.5% 0 1.5%;
    width: 10%;
    height: 90%;
    float: left;
}
#cas #sidebar {
    background: url(https://lea.aquitaine.fr/cas/images/attention.png) no-repeat #635d76;
    background-position: 10px 4px;
    color: #fff;
    font-size: 1.3em;
    border: 2px solid #635d76;
    height: 60px;
    width: 450px;
    padding: 5px 10px 5px 60px;
    position: absolute;
    top: 300px;
    left: 13px;
    margin: 0 auto;
}
#content h4 {
    color: #21183c;
    font-size: 1.3em;
    font-weight: 400;
    padding: 1px 1px;
    text-align: center;
}
#cas .box {
    position: relative;
    height: 250px;
    border: 2px dotted #a19dac;
    top: 48px;
    width: 520px;
}
#cas form {
    font-family: 'Roboto', sans-serif;
    background: url(http://img4.hostingpics.net/pics/613024cnxorange.jpg) no-repeat transparent;
    background-position: 0px 8px;
    position: relative;
    top: calc((100% - 8em - 373px) / 2);
    width: 500px;
    margin: 0 auto;
    padding-left: 13px;
}
.portal #region-customize #customizeOptions .up-gallery {
    background: #fbf8fd none repeat scroll 0 0;
    border: 1px solid #fbf8fd;
}
#content {
    background-repeat: no-repeat;
    position: absolute;
    height: calc(100% - 8em);
    top: 8em;
    left: 0;
    bottom: 0;
    right: 0;
    color: #333;
}
.container, .container-fluid {
    padding-right: 15px;
    padding-left: 15px;
    width: 100%;
}
body {
    background-size: cover;
    color: #ff1769;
    background-color: #000;
}
.portal.up.accueil.logged-in .nouveautes .up-portlet-content-wrapper-inner {
    max-height: none;
    margin-bottom: -121px;
    margin-left: -27px;
    margin-right: -27px;
}
.l-cat-1 .nav-tree .nav-depth-3>a.active {
    background-color: #f8fbfd;
}
.l-cat-1 .nav-tree .nav-depth-2>a.active {
    background-color: #fbf8fd;
}
element.style {
    border-bottom-color: #fbf8fd;
}
.page {
    color: #868686;
}
}
