/* Knytt Levels Dark 1.5 by compact
 * Do whatever you want with this thing - this was a low-effort color swap that anyone could have done in thirty minutes.
 * If you redistribute / edit / whatever this style, you don't have to credit me - just link back to the style.
 * To the game that ate up two years of my awkward teenaged life.
 */

@-moz-document domain("knyttlevels.com") {

  html, body {
    background: #000;
    color: #CCC;
    margin: 0;
    padding: 0;
  }
  
  h2 {
    text-transform: capitalize;
    font-style: normal;
  }
  
  a {
    color: #3DBBEE;
    text-decoration: none;
  }
  
  a:hover {
    color: #00EEFF;
    text-decoration: underline;
  }
  
  div {
    border-color: #AAA;
    background: #222;
  }
  
  div.content {
    width: 75%; /* at higher resolutions, the table starts getting weird -- i'll work out a fix later */
    margin-top: 50px;
    text-align: center;
    border-radius: 4px;
  }
  
  div.menu {
    margin-left: 10px;
    text-transform: capitalize;
    border-radius: 0px 4px 4px 4px;
  }
  
  div.menutitle {
    margin-left: 10px;
    margin-bottom: 7px;
    margin-top: 12px;
    text-align: center;
    text-transform: capitalize;
    border-radius: 4px 4px 0px 0px;
  }
  
  div.logo {
   display:none; /* i wish that i could keep this in, but the logo is alpha'd really poorly - you can comment this section out if this bothers you*/
  }
  
  /* background of form upload */
  div.content div {
   border-radius: 4px;
   background: #222;
  }
  
  /* form upload prompt itself */
  form {
    color: #CCC;
  }

  td {
   width: 875px;
   padding: 10px;
   border-left: hidden;
   border-right: hidden;
   border-color: #CCC;
  }
  
  td.num {
   width: 25px;
   padding: none;
   text-align: center;
  }
  
  tr:hover {
   background: #181818;
  }
  
  td.icon {
   width: 32px;
  }
  
  td.right {
    width: 100px;
    text-align: center;
  }
  
  th {
    display:none; /* hidden because i just don't like th elements in general, feel free to comment it out as you see fit */
  }
  
}
